css:
@-moz-document domain("komica.org"), domain("gzone-anime.info"), domain("2cat.cf"), domain("2cat.tk"), domain("komica2.net"), domain("komica.yucie.net") {
/* Content */
html, body, .Form_bg, .ListRow1_bg, .ListRow2_bg, .popup_area div , #quickreply-form {
    background-color: #111;
	color: #a5a5a5;
}
.post:target, .reply.reply_hl, .threadpost.reply_hl, div.blueTable {
	background-color: #262626;
}
a, .qlink, .text-button {
    color: #3581ac !important;
}
.-del-button a, .title {
	color: #bb2121 !important;
}
.reply {
    background-color: #111;
}
.id3 {
	color: #2222d0;
}
.id8 {
	color: #b66d24;
}
.id9 {
	color: #b82d2d;
}

input, textarea, select {
    background-color: 
#202020;
color:
    #d7d7d7;
}
}

@-moz-document url("https://www.komica.org/bbsmenu.html") {
/* Menu */
h3, .category {
	color: #bb2121;
}
a {
	text-decoration: none;
	font-size: 14px;
}
a:hover {
	color: #638ba2 !important;
	text-decoration: underline;
}
small {
	color: #B65C92;
}
.bookmark-excluded a {
	text-decoration: line-through;
}
}
