css:
@-moz-document domain(evrl.to) {
.footer_container {
    display: none !important
}
.container>.navbar-header, .container-fluid>.navbar-header, .container>.navbar-collapse, .container-fluid>.navbar-collapse {
    left: 5px !important;
    position: fixed;
}
.navbar .nav.navbar-nav {
    position: fixed;
    top: 0px;
    left: 129px;
}
#leftnav {
    position: fixed !important;
    top: -57px !important;
    bottom: 500px !important;
    left: 1154px !important;
    background: #ffffff00 !important;
    z-index: 1034 !important;
    width: 299px !important;
}
#leftnav div.dropdown {
    top: 0px !important;
    right: 0px !important;
    position: fixed;
}
#leftnav .sidebtn {
    display: inline-grid;
    /* outline: none !important; */
    /* position: fixed; */
    height: 0px !important;
    top: 8px !important;
    /* margin-top: 9px !important; */
    width: 42px !important;
}
#leftnav .leftopen .sidebtn {
    width: 42px !important;
}
.right {
    float: unset !important;
}
#leftnav .leftopen.leftbuttons {
    width: 371px !important;
}
#leftnav .sidebtn .sidetitle {
    color: #252525 !important;
}
#leftnav .leftopen .sidetitle {
    width: 0px !important;
}
#leftnav .leftbuttons {
    background: #25252500;
    position: fixed;
    width: 371px;
    overflow: hidden;
    top: -15px;
    right: 55px;
    height: 9%;
}
#leftnav-move {
    display: none
}
#leftnav-top .glyphicon {
    color: #03a9f4;
    font-size: 16px;
}
html body div#main_container div#leftnav div div.leftbuttons div#leftnav-top{
         display: none !important;
}
.container {
    position: relative;
}
#leftnav .dropdown-menu {
    margin-top: 10px;
    margin-left: -115px;
    border-radius: 0 0 4px 0;
    padding-top: 0;
    border-top: 0;
    background-color: #ffffff;
}
li.dropdown.nav-search-dropdown {
    display: none;
}
.navbar-inverse {
    background: #333;
    /* margin: 0; */
}
.navbar .nav.navbar-nav>li>a {
    float: left;
    font-family: "Roboto",Tahoma;
    color: #ffffff;
}
.navbar-search .form-control {
    color: #fff;
    background-color: #fff;
    border: 1px solid #fff;
}
#leftnav .sidebtn .glyphicon {
    color: white;
}
.leftbuttons .sidebtn .side_notify {
    position: absolute;
    right: -7px;
    top: -10px;
    border-radius: 20px;
    background: #ffffff;
    color: #333;
    font-size: 1px;
    padding: 3px 3px;
}
.avatar_small {
    border: 2px solid #ffffff;
    margin-right: 4px;
    float: left;
    width: 33px;
    height: 33px;
    padding: 0px;
    /* border-radius: 2px; */
    -moz-border-radius: 2px;
    -webkit-border-radius: 20px;
    color: #fff;
}
#leftnav .dropdown-toggle .avatar_small {
    width: 33px;
    height: 33px;
    margin-top: -1px;
}
.nm .tab-block .tabs {
    background: #333 !important;
    left: 0px !important;
    position: fixed !important;
    top: 50px !important;
}
html body div#main_container div#content_container div.nm div.tab-block.articles div div.tabs div {
    position: fixed;
    overflow: visible;
    box-sizing: border-box;
    min-height: 0px;
    left: 0px;
    margin-top: 0px;
}
a.tab.tab_gear {
    border-bottom-right-radius: 30px;
}
.topinfo>div {
display: none;
}
.topbutton {
    margin: -27px 0 0 0 !important;
}
html body div#main_container div#content_container div.nm div.tab-block.articles div div.tabs div {
    width: 116px;
}
.nm .tab-block .tabs .tab {
    text-align: center;
    padding: 24px 0px;
    font-weight: 400;
    color: #fff;
    text-transform: uppercase;
    transition: all 150ms linear;
    display: block;
    position: relative;
    background-color: #333;
    /* font-size: 9px; */
}
.nm .tab-block .tabs .tab:hover, .nm .tab-block .tabs .tab.selected {
    color: #fff;
    background: #777;
    cursor: pointer;
    text-decoration: none;
}
.nm .simple-block>div>.content.top-news>a:hover {
    background: #797979;
}
.nm .simple-block>div>.content.top-files>a:hover {
    background: #797979;
}
.nm .simple-block>div>.content.last-news>div a.item {
    transition: all 600ms ease-in-out;
    border-right: 2px solid #eee;
    width: 325px;
}
html body div#main_container div#content_container div.nm div.tab-block.feed div div.tabs div {
    width: 116px;
}
a.tab.tab_gear {
    border-bottom-right-radius: 30px;
}
html body div#main_container div#content_container div.nm div.tab-block.feed div div.tabs div {
    background-color: white;
}
html body div#main_container div#content_container div.nm div.tab-block.articles div div.tabs {
    background-color: white !important;
}
}
