css:
/* 
     Glasser Fix - Firefox 3
     version 1.0.2
*/

/******** urlbar curve & visual effects ********/

#main-window #urlbar, .autocomplete-textbox-container, #identity-box{
	-moz-border-radius-topleft: 11px !important;
	-moz-border-radius-bottomleft: 11px !important;}
#main-window #urlbar{
	margin-left: 3px !important;}
#identity-box {	
	margin-left: -1px !important;
	border-right: 1px solid !important;
	border-right-color: rgba(0,0,0,.2)!important;
	background: -moz-dialog url(chrome://browser/skin/navbar-textbox-buttons.png) repeat-x !important;}
#identity-box:hover{
	background-color: rgba(72, 215, 251, 0.4) !important;}
#identity-box.verifiedDomain,#identity-box.verifiedIdentity{
        margin-left: -1px !important;
        padding-left: 1px !important;}
#identity-box.verifiedDomain{ 
	background-position: 0 -121px !important;}
#identity-box.verifiedDomain:hover{ 
	background-position: 0 -181px !important;}
#identity-box.verifiedIdentity{ 
	background-position: 0 -241px !important;}
#identity-box.verifiedIdentity:hover{ 
	background-position: 0 -301px !important;}
#identity-box > hbox{
	margin-left:10px !important;
	margin-right:-4px !important;
	border: none !important;
	background-color: transparent !important;}
#page-proxy-stack{
   	margin-left:-7px !important;
   	margin-right:7px !important;}
#identity-icon-label{ 
   	margin-left:-4px !important;
   	margin-right:8px !important;}
#identity-box, .autocomplete-history-dropmarker, .textbox-input-box, .search-go-container{
   margin-top: -1px!important;
   margin-bottom: -1px!important;}

/******** urlbar icons ********/

#feed-button:not(:hover), #safebrowsing-urlbar-icon:not(:hover),#star-button:not(:hover), #go-button:not(:hover) {
	opacity: 0.5 !important;}
#urlbar > .autocomplete-history-dropmarker > .dropmarker-icon{
	margin-left: 3px !important;
        margin-right: 3px !important;}
#urlbar > .autocomplete-history-dropmarker:hover{
	background-color: rgba(72, 215, 251, 0.4) !important;}

/******** searchbox visual effects ********/
.searchbar-engine-button{
	background: -moz-dialog url(chrome://browser/skin/navbar-textbox-buttons.png) repeat-x !important;}
.searchbar-textbox:not(:hover):not([focused="true"])>.searchbar-engine-button{
       opacity:.8!important;}
.searchbar-engine-button:hover{
	background-color: rgba(72, 215, 251, 0.4) !important;}
.searchbar-textbox>.autocomplete-textbox-container>.textbox-input-box>.autocomplete-textbox.textbox-input{
	padding-left: 1px !important;
	padding-right: 2px !important;}

/******** navigation toolbar padding & menubar remove background ********/

#main-window #navigator-toolbox #nav-bar{
	padding:3px 0 4px 0 !important;}
#main-window #navigator-toolbox #toolbar-menubar{
	background:none !important;}
