css:
@-moz-document domain("okoun.cz") {

/* DELETE THIS LINE
@import url('https://fonts.googleapis.com/css?family=Roboto+Condensed');
   DELETE THIS LINE ALSO, LEAVE THE ONE IN MIDDLE, so custom fonts can load properly */
body, html {
    padding: 10px 8%;
    font-family: IBMPlexSans, Helvetica Neue, Segoe UI, Helvetica, Verdana, Arial, sans-serif!important;
    background-color:#242425!important;
    line-height:1.4!important;
}
#body {
    background-color:var(--color2)!important;
    margin:0!important;
    padding: 10px 20px;
    border: 1px solid #343536;
    border-radius: 4px;
}

/* text color */
body, .item.hover .actions span.prev {
    color:rgb(215, 218, 220)!important;
}
body, .content * {
    font-family: IBMPlexSans, Helvetica Neue, Segoe UI, Helvetica, Verdana, Arial, sans-serif!important;
}

/* post img zoom effect */
.item .content img {
    width: 30%;
}
.item .content img:hover {
    width: auto;
    z-index:1000;
}

/* reklamy */
#adwrapper-lb, #adwrapper-bs, .share {
    display:none!important;
}

.main .main {
    width:100%!important;
}

/* side panel breadcrumbs */
.main .ctx {
    display:none;
}

/* header */
.head {
    font-family: 'Roboto Condensed', sans-serif;
    letter-spacing: 1.5px;
    border-top: 1px solid #343536!important;
    border-left: 1px solid #343536!important;
    border-right: 1px solid #343536!important;
    border-bottom: none;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    background-image: linear-gradient(to right, #8aa, palevioletred);
    width: inherit;
    padding: 5px 10px;
    margin:-11px -21px 8px!important;
}
.menu > .search > .text.prompt, .menu > .search > .text, .head .user .login input.text {
    background: transparent;
    border-color: #fff;
    color: #fff!important;
}
.menu > .search > .text:hover, .head .user .login input.text:hover {
    background-color: transparent!important;
    border-color: #000;
    color: #000!important;
}
.head .menu {
    margin-left: 5em;
    margin-top: 14px;
    margin-bottom: -14px;
}
a[href="/msgbox.jsp"], a[href="/favourites.jsp"], a[href="/favourites.jsp?new=1"], a[href="/myBoards.jsp"], a[href="/activeUsers.jsp"] {
    position:absolute;
    margin-top: -32px;
    letter-spacing: 1px;
}
a[href="/msgbox.jsp"] {margin-left: 31.5em;}
a[href="/favourites.jsp"] {margin-left: 35.8em;}
a[href="/favourites.jsp?new=1"] {margin-left: 42.5em;}
a[href="/myBoards.jsp"] {margin-left: 47.5em;}
a[href="/activeUsers.jsp"] {margin-left: 52.6em;}
.head .user {
    padding-top: 17px;
}
.user img {
    border-radius: 16px;
    margin-top:-2px;
    border: 1px solid #ffffff52;
}
.board .main .search input.text.authors {
    width: 17.8em;
}

.messages, #errorBox.messages, #sysmsgBox.messages {
    background-color: rgba(0, 0, 0, .81);
    color: #F2992E;
    border: 1px solid #F2992E;
    border-radius: 3px;
    font-weight: 600;
    padding: 0 .5em;
    position:absolute!important;
    z-index: 1001;
    left: 48vw;
}
.messages:hover {
    opacity: 0;
    -webkit-transition: all .5s linear;
    -o-transition: all .5s linear;
    transition: all .5s linear;
}
.legend {
    color: #000;
    padding-left: 1em;
}

/* new post area */
fieldset {
    background: #008080!important;
    padding: 10px 50px 20px;
    margin-bottom: 0!important;
    border-right: 1px solid #343536;
    border-left: 1px solid #343536;
    border-top: 1px solid #343536;
    border-top-right-radius: 4px;
    border-top-left-radius: 4px;
}
.post.content form, .post.content .form {
    padding: 0 10%;
}

/* reply form */
.actions.replyForm > .post.content > .form {
    padding: 0!important;
}
.item .actions.replyForm a.reply {
    margin-left: 5px;
    padding-right: 25px;
    border-bottom: 1px solid #008080;
    border-top: 1px solid #343536;
    border-left: 1px solid #343536;
    border-right: 1px solid #343536;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
}
.actions.replyForm > .post.content, .actions.replyForm > .post.content > .form {
    padding: 0;
    margin-left: 0;
}
.post.content .tools {
    background: #008080;
    padding: 0 40px!important;
    margin: 0!important;
    border-right: 1px solid #343536;
    border-left: 1px solid #343536;
    border-bottom: 1px solid #343536;
    border-bottom-right-radius: 4px;
    border-bottom-left-radius: 4px;
}
.actions.replyForm > .post.content > .form > fieldset {
    border-top-left-radius: 0;
}
.actions.replyForm > .reply {
    background-color: #008080!important;
}
.actions.replyForm > .post.content > .form > .tools {
        padding: 5px 10px!important;
}
.yui-skin-sam .yui-button {
    border: none!important;
    background:none!important;
    margin-left:0!important;
}
.yui-skin-sam .yui-button .first-child {
    border: none;
}
.yui-button button {
    border: none;
    border-radius: 4px;
    box-sizing: border-box;
    text-align: center;
    text-decoration: none;
    font-size: 12px!important;
    font-weight: 700;
    letter-spacing: .5px!important;
    text-transform: uppercase;
    background-color: #d7dadc;
    min-width: 96px!important;
    min-height: 27px!important;
    padding: 5px 10px!important;
    margin-bottom: -4px;
}
.yui-button button:hover {
    background-color: #fff!important;
}
.tools > .yui-button button {
    margin-bottom: 0;
}

.post.content .tools select {
    background: transparent;
    color: #343536;
    border: 1px solid #343536;
    border-radius: 4px;
}

/* post preview */
.preview .label {
    color: #fff;
    border:none;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    text-align: center;
    text-decoration: none;
    font-size: 14px;
    font-weight: 500;
    letter-spacing: 1.4px;
    line-height: 2em;
    font-style: italic;
    background-color: #c6c6c630;
    padding: 4px 15px;
    margin-left:0;
}
.preview .item, .preview .item:hover{
    background-color: #c6c6c630;
}

h2, h3, h4, body.form input.text.name {
    font-weight:400!important;
}
h2, .ctx h3, .index h3, .expbutton .hd, .ad .label {
    font-family: IBMPlexSans, Helvetica Neue, Segoe UI, Helvetica, Verdana, Arial, sans-serif!important;
}

/* welcome info panel */
.welcome, .item .content .confirm.reply {
    background-color:#000!important;
    border:1px solid #343536!important;
}
.welcome {
    padding:10px!important;
    margin-bottom: -.5em!important;
}
.room .item.new {
    background-color: var(--color2);
    background-image: linear-gradient(to right,#123b17, #0000, #0000)!important;
    border-left:none!important;
    border-right:none!important;
    border-top:none!important;
}
.room .item.new:hover {
    background-image: linear-gradient(to right, #1d5523, #0000, #0000)!important;
}

/* new post button */
.expbutton .hd {
    font-family: 'Roboto Condensed', sans-serif;
    border: none;
    border-radius: 4px;
    box-sizing: border-box;
    text-align: center;
    text-decoration: none;
    font-size: 12px;
    letter-spacing: .5px;
    text-transform: uppercase;
    background-color: #d7dadc;
    min-width: 96px;
}
.expbutton .hd a {
    color: var(--color2)!important;
    font-weight: 700!important;
    padding: 5px 0;
}
.expbutton .hd:hover {
    background-color: white;
}
.expbutton {
    margin: 1em 0 0;
}

.expbutton .post.content, .actions.replyForm .post.content {
    background-color: var(--color2)!important;
    border:none!important;
    width:100%!important;
    margin:0!important;
}

.actions.replyForm > .post.content > .form > fieldset {
    padding:10px 20px;
}
.replyForm > 
.post.content form, .post.content .form {
    padding: 12px 120px;    
}
textarea {
    height:9em!important;
    background-color: #ffffff5e;
    border: 1px solid #343536;
    border-radius: 4px;
    color: #000;
    margin-left: -10px!important;
    padding: 5px 10px!important;
}
input.text.title.long.prompt, input.text.title.long {
    background-color: #ffffff5e;
    margin-left: -10px;
    color: #000!important;
}

input {
    background-color: var(--color2);
    color: #818384!important;
    font-style: normal!important;
    border: 1px solid #343536;
    border-radius: 4px;
    padding: 5px 10px!important;
}
.post.content .tools {
    padding-bottom:5px!important;
    margin:-9px 0 0 0!important;
}

/* pagination */
.pager ul {
    font-size: inherit;
    font-family: 'Roboto Condensed', sans-serif;
}
.pager ul li.newest, .pager ul li.newer, .pager ul li.older, .pager ul li.oldest {
    background: none;
    padding: 0;
}
.pager ul li.newest a, .pager ul li.newer a, .pager ul li.older a, .pager ul li.oldest a {
    color: var(--color2)!important;
    background-color: var(--color1);
    padding: 2px 8px;
    border: 1px solid var(--color1);
}
.pager ul li.newest a:hover, .pager ul li.newer a:hover, .pager ul li.older a:hover, .pager ul li.oldest a:hover {
    color: var(--color1)!important;
    background-color: #000;
}
li.newest, li.newer, li.older, li.oldest {
    color: #717171;
    text-transform: uppercase;
}

.pager ul li a {
    background-color: var(--color1);
    border: 1px solid var(--color1);
    color: #000!important;
}
.pager ul li a:hover {
    background-color: #000; 
    color: var(--color1)!important;
}
li.curr {
    background-color: #000!important;
    color: var(--color1);
    border: 1px solid var(--color1)!important;
}

/* post */
.item {
    margin-bottom: 8px!important;
}
.item:hover {
    background-color: var(--color2);
    background-image: linear-gradient(to bottom, #0000, #222);
    color:#DDD!important;
}
.articles, .footer {
    border:none!important;
}
.user.ico, .user .ico {
    display:none!important;
}
.user {
    color: var(--color1)!important;
}
.item .meta .user {
    font-weight:400!important;
    font-size:12px;
}
.descr, .hpdescr {
    display:none!important;
}
.item .content {
    margin-bottom: 1em!important;
}
.content, .item .meta, body.userlist .main .main, body.boardlist .main .main {
    font-size:13px!important;
}
/* link colors */
 a {
    color:var(--color1)!important;
    text-decoration:none!important;
    font-weight:500!important;
}
h2 a {
    color: var(--color1)!important;
}
a.reply:hover {
    text-decoration: underline!important;
}
a:hover {
    color: white!important;
}
.head h1 a:hover, .head .menu a:hover, .head .user a:hover, .footer a:hover {
    text-decoration: underline!important;
}
.head .menu a, .head .user a, .footer a, .item.hover .actions a, .item.hover .actions span.prev a.prev {
    color: white!important;
}
.head .menu a:visited, .footer a:visited, .head .user a:visited {
    color: white!important;
}
.item .permalink a:hover {
    text-decoration:none!important;
    color:white!important;
}

/* post date */
 .item .date, .item .permalink a, .item .meta a {
    color:#888!important;
    font-style:italic;
}
a.reply, a.prev, a.thread {
    color:#888!important;
    text-decoration:none!important;
}
.item.hover .actions a:hover, .item.hover .actions span.prev a.prev:hover {
    text-decoration: underline!important;
}
.reply {
    background:none!important;
    font-weight:500!important;
}
.prev, .thread {
    background:none!important;
    margin-left:-15px!important;
}
.listing .item {
    padding:0em!important;
}
.item .meta {
    padding:0 0 0 5px!important;
    margin:0 0 -.1em 0!important;
    margin-left:0px!important;
}
.room .item {
    border-bottom:1px dotted gray!important;
}
.item .actions {
    border:none!important;
    margin:-22px 0 0 0!important;
    float:right;
    z-index:1001;
    font-style:italic;
}
.item .meta .delete, .item .actions {
    font-size:.8em!important;
}
.main .actions {
    padding-top:.4em!important;
    padding-bottom:0!important;
}
.item a.link {
    background-image:none!important;
}
.item .meta .permalink {
    right: 0em!important;
}
.item .content, .item .title {
    padding-left:5px!important;
}
.head h1 a {
    background-image: var(--undyne) !important;
    background-position: 0 -48px!important;
    padding:15px 0!important;
    color:transparent!important;
}
.head h1 a:hover {
    background-position: 0 0!important;
    color:transparent!important;
}

/* small icons */
.ico {
    transition: .5s ease;
}
.expcol .ico {
    background-image: var(--icons) !important;
}
.col .ico {
    background-position: 0 -40px!important;
}
.col .ico:hover {
    background-position: 0 -40px!important;
    transform: rotate(90deg);
}
.exp .ico {
    background-position: 0 -40px!important;
    transform: rotate(90deg);
}
.exp .ico:hover {
    background-position: 0 -40px!important;
    transform: rotate(0deg);
}

.board .bmk a {
    background-image: var(--icons) !important;
}
.board .bmk a.on {
    background-position: 0 -16px!important;

}
.board .bmk a.on:hover {
    background-position: 0 0!important;
}
.board .bmk, .board .bmk a, .board .bmk a input {
    width: 16px!important;
    height: 16px!important;
}
.board .bmk a.off:hover {
    background-position: 0 -16px!important;
}
.board .bmk a.off {
    background-position: 0 0!important;
}

.footer {
    background-color: #000;
    background-image: linear-gradient(to right, #88aaaa40, #db709340);
    font-family: 'Roboto Condensed', sans-serif;
    letter-spacing: 1.5px;
    border-bottom: 1px solid #343536!important;
    border-left: 1px solid #343536!important;
    border-right: 1px solid #343536!important;
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
    width: inherit;
    padding: 15px 10px;
    margin: 0 -21px -11px!important;
}

/* Root */
 :root {
--color1: #4fbcff;
--color2: #1b1b1b;
     --undyne:url('data:image/png;base64,');
    --icons: url('data:image/png;base64,');
}
}

@-moz-document url-prefix("https://www.okoun.cz/boards") {
/* side panel breadcrumbs */
.main .ctx {
    display: block!important;
    background-color: var(--color2)!important;
    width: 12%!important;
    position: absolute;
    right: -200px;
    padding: 8px!important;
    font-size: 70%!important;
}
.ctx div.ico-wrapper, .info {
    display:none!important;
}

}

@-moz-document url("https://www.okoun.cz/") {
.yui-gb.item:hover, .yui-u, .item:hover {
    background-color:#1b1b1b!important;
}
}
