css:
@-moz-document domain("feheroes.gamepedia.com") {
:root {
    --main-bg: #262626;
    --main-bg-dark: #1b1b1c;    
    --main-bg-lighter: #373737;
	--main-bg-light: #cccccc;    
    
    --main-title: #dedede;
    --main-sub: #828282;
    --main-link: #6a98af;
}

body {
    background: var(--main-bg-lighter) !important; 
}

/**********************************
             NAVIGATION
***********************************/

#mw-head a {
    color: var(--main-link) !important;
}

#mw-head a:hover {
    text-decoration: none !important;
}

#mw-head {
    background-color: var(--main-bg-lighter);
}

#mw-head #right-navigation ul,
#mw-head #left-navigation ul {
    background: var(--main-bg-lighter) !important; 
}

#mw-head #right-navigation ul li,
#mw-head #left-navigation ul li {
    background: var(--main-bg-lighter) !important;
}

#mw-head #right-navigation ul li.selected,
#mw-head #left-navigation ul li.selected {
    font-weight: bold;
}

#mw-head #left-navigation #p-sharing span {
    color: var(--main-link) !important;
}

#mw-head #right-navigation #p-search input[type=search] {
    border: 1px solid !important;
    border-color: var(--main-title) !important;
    border-radius: 0 !important;
    background-color: var(--main-bg) !important;
    padding: 3px 5px;
    color: var(--main-title);
}

#mw-head #right-navigation #p-search input[type=submit] {
    background-color: var(--main-bg) !important;
    border: 1px solid !important;
    border-color: var(--main-title) !important;
    border-radius: 0 !important;
}

#mw-head #right-navigation .vectorTabs,
#mw-head #left-navigation .vectorTabs {
    background-image: none !important;
    border-left: 1px solid !important;    
    border-color: var(--main-title) !important;    
}

#mw-head #right-navigation .vectorTabs ul li,
#mw-head #left-navigation .vectorTabs ul li {
    background-image: none !important;
    border-right: 1px solid !important;
    border-color: var(--main-title) !important;  
}

#mw-head #right-navigation .vectorTabs li.selected,
#mw-head #left-navigation .vectorTabs li.selected {
    background-image: none !important;
}

#mw-head #right-navigation .vectorTabs span,
#mw-head #left-navigation .vectorTabs span {
    background-image: none !important;    
}

#mw-panel {
     background-color: var(--main-bg-lighter) !important;
}

#mw-panel a {
    color: var(--main-link) !important;
}

#footer li {
    color: var(--main-sub) !important;
}

#footer a {
    color: var(--main-link) !important;
}

/**********************************
              CONTENT
***********************************/

.mw-body {
    background-color: var(--main-bg);
    border-color: var(--main-title) !important;
}

.mw-body a {
    color: var(--main-link) !important;
}

/* WIKITABLE */

.mw-body .mw-body-content .wikitable:not(.tierlist) tr {
    border: none !important;
}

.mw-body .mw-body-content .wikitable:not(.tierlist) th {
    color: var(--main-title) !important;
    background-color: var(--main-bg-lighter) !important;
    border: 1px solid !important;
    border-color: #AAA !important;
}

.mw-body .mw-body-content .wikitable:not(.tierlist) td {
    color: var(--main-sub) !important;
    background-color: var(--main-bg) !important;
}

/* AMTABLE */

.mw-body .mw-body-content table.ambox {
    background-color: var(--main-table) !important;
    border: 1px solid !important;
    border-color: var(--main-title) !important;    
}

/* SKILLTABLE */

.mw-body .mw-body-content .wikitable.skillbuild tbody > tr:first-child > td {
    color: var(--main-title) !important;
    background-color: var(--main-bg-lighter) !important;
}

.mw-body .mw-body-content .wikitable.skillbuild tbody > tr:first-child > td span {
    color: var(--main-title) !important;
}

/* TIERTABLE */

.mw-body .mw-body-content .wikitable.tierlist tbody > tr:first-child > th,
.mw-body .mw-body-content .wikitable.tierlist.default tbody > tr:nth-child(2) > th,
.mw-body .mw-body-content .wikitable.tierlist.default tbody > tr:nth-child(3) > th {
    background-color: var(--main-bg-lighter) !important;
	border: 1px solid !important;
    border-color: #AAA !important;    
 	color: var(--main-title) !important;    
}

.mw-body .mw-body-content .wikitable.tierlist.default tbody td {
    border: 1px solid !important;
    border-color: #AAA !important;    
}

.mw-body .mw-body-content .wikitable.tierlist tbody td:first-child {
    background-color: var(--main-bg-lighter) !important;
    color: var(--main-title) !important;
}

.mw-body .mw-body-content .wikitable.tierlist tbody td:not(:first-child) {
    background: var(--main-bg) !important;
    border-left-color: var(--main-title) !important;
}

/* CARGOTABLE */

.mw-body .mw-body-content .cargoTable thead th {
    background-color: var(--main-bg-lighter) !important;
	border: 1px solid !important;
    border-color: #AAA !important;
}

.mw-body .mw-body-content .cargoTable tbody td {
    background: var(--main-bg) !important;
}

/* NAVTABS */

.mw-body .mw-body-content > #mw-content-text > table:not(.wikitable) td {
    background-color: var(--main-bg) !important; 
}

.mw-body .mw-body-content > #mw-content-text > table:not(.wikitable) td a {
    /*color: var(--main-title) !important;*/
}

.mw-body .mw-body-content .tabber .tabbernav li:not(.tabberactive) > a {
    background-color: var(--main-bg) !important;
    color: var(--main-link) !important;
    font-weight: lighter;
}

.mw-body .mw-body-content .tabber .tabbernav li.tabberactive > a {
    background-color: var(--main-bg) !important;    
    color: var(--main-link) !important;
}

/* TOC */

.mw-body .mw-body-content .toc,
.mw-body .mw-body-content #filetoc {
    border: 1px solid !important;
    border-color: var(--main-title) !important;
    background-color: var(--main-bg) !important;
}

.mw-body .mw-body-content .toc .toctitle {	
    color: var(--main-title) !important;
    background-color: var(--main-bg-lighter) !important;
}

.mw-body .mw-body-content .toc .toctitle h2 {
    color: var(--main-title) !important;
}

.mw-body .mw-body-content .toc .tocnumber {
    color: var(--main-sub) !important;    
}

/* HEADLINE */

.mw-body .mw-body-content .h2-green,
.mw-body .mw-body-content .h2-gray {
    color: var(--main-title) !important;
    background-color: var(--main-bg-lighter) !important;
    border: 1px solid !important;
    border-color: var(--main-title) !important; 
    margin-bottom: 15px;
}

.mw-body .mw-body-content h2,
.mw-body .mw-body-content h2 .mw-headline {
    color: var(--main-title);
}

.mw-body .mw-body-content h3,
.mw-body .mw-body-content h3 .mw-headline,
.mw-body .mw-body-content h4,
.mw-body .mw-body-content h4 .mw-headline,
.mw-body .mw-body-content h5,
.mw-body .mw-body-content h5 .mw-headline,
.mw-body .mw-body-content h6,
.mw-body .mw-body-content h6 .mw-headline,
.mw-body .mw-body-content dd {
    color: var(--main-sub);
}

/* SELECT */

.mw-body .mw-body-content select {
    border: 1px solid !important;
    border-color: var(--main-title) !important;
    border-radius: 0 !important;
    background-color: var(--main-bg) !important;
    padding: 3px 5px;
    color: var(--main-title);
}

/* THUMB */

.mw-body .mw-body-content .thumbinner {
    background-color: var(--main-bg-lighter) !important;
}

.mw-body .mw-body-content .thumbinner .thumbcaption {
    color: var(--main-title);
    margin-top: 5px;
}

.mw-body .mw-body-content .thumbinner .thumbcaption .magnify {
    margin-top: 2px;
}

/* NAVBOX */

.mw-body .mw-body-content .navbox {
    background: var(--main-bg) !important;
}

.mw-body .mw-body-content .navbox th.navbox-title {
    border-color: var(--main-title) !important;
    background-color: var(--main-bg-lighter) !important;
}

/* NAVBOX */

.mw-body .mw-body-content .navbox th.navbox-group {
    border-color: var(--main-title) !important;
    background-color: var(--main-bg-lighter) !important;
}

.mw-body .mw-body-content .navbox td.navbox-list {
    border-color: var(--main-title) !important;
    background-color: var(--main-bg) !important;
}

/* STAT CALCULATOR */

.mw-body .mw-body-content .panel.stat-calculator-panel {
    border-color: var(--main-title) !important;
    background-color: var(--main-bg-lighter) !important;
}

.mw-body .mw-body-content .panel.stat-calculator-panel .mw-headline {
    color: var(--main-title) !important;
}

/* CONTENT */

.mw-body .firstHeading {
    color: var(--main-title);
}

.mw-body .mw-body-content,
.mw-body .mw-body-content blockquote,
.mw-body .mw-body-content blockquote span,
.mw-body .mw-body-content ul,
.mw-body .mw-body-content p {
    color: var(--main-title) !important;
}

.mw-body #pagehistory li.selected {
    color: var(--main-title) !important;
    background-color: var(--main-bg-lighter) !important;
    border: 1px solid !important;
    border-color: var(--main-title) !important; 
}

/* MAINPAGE */

.mw-body .mw-body-content .mpflex .panel .panel-heading {
    border-color: var(--main-title) !important;
    background-color: var(--main-bg-lighter) !important;
	color: var(--main-title) !important;    
}

.mw-body .mw-body-content .mpflex .panel .panel-body {
    background-color: var(--main-bg) !important;
}

.mw-body .mw-body-content .mpflex #mpherogrid .panel-body > div:nth-child(2) {
    background: var(--main-bg-lighter) !important;
    border: 1px solid !important;
    border-color: var(--main-title) !important;     
    margin-bottom: 15px !important;                                            
}

.mw-body .mw-body-content .fpbox {
    background-color: var(--main-bg) !important;
    border: 1px solid !important;
    border-color: var(--main-title) !important;    
}

.mw-body .mw-body-content .fpbox .plainlinks {
    color: var(--main-title) !important; 
}

.mw-body .mw-body-content .fpbox small {
    color: var(--main-sub) !important;
}

.mw-body .mw-body-content .fpbox .welcome {
    background: var(--main-bg-lighter) !important;
    border: 1px solid !important;
    border-color: var(--main-title) !important; 
	color: var(--main-title) !important;        
}

.mw-body .mw-body-content #mw-content-text > div {
    background: var(--main-bg) !important;
}
}
