css:
@-moz-document url-prefix("https://www.coinkeeper.me"), url-prefix("http://www.coinkeeper.me"), url-prefix("https://coinkeeper.me"), url-prefix("http://coinkeeper.me")
{
  	/* Фон */
  .ck-page {background-image:none;}
  .ck-page_background {background-image:none;background-color: #9a9a9a;}
	/* Шапка */
  .ck-header_old {background-color: #565656;}
  .ck-header_profile {color: #d8d8d8;}
  .ck-bank-import,.ck-header_title {display:none;}/*Прячем кнопку подключения банка, название (ибо оно серое)*/
.ck-bank-import,.ck-header_title:hover {display:block;}
  /*Меню в шапке*/
  .ck-header_profile .ck-header_dropdown-menu {background-color: #5d5d5d;}
   
  /* Контент */
  /* Форма поиска */
  .ck-add-transaction {background-color: #5d5d5d;color: #d4d4d4;}
  .ck-add-transaction__submit[disabled] {opacity: unset;}
  .ck-add-transaction__submit {background-color: #00d296;}
  /* Форма поиска: активна */
  .ck-fade {background-color: rgba(37, 37, 37, 0.8);}
  /* Выпадающее меню в поиске */
  .ck-dropdown {background-color: #5d5d5d;}
  .ck-dropdown-list__item {color: #d4d4d4;}
  .ck-add-transaction input {color: #ffffff;text-shadow: 1px 1px 1px #3e3e3e;}
  /* Блоки с контентом*/
  .ck-category-block__header {background-color: #5d5d5d;border-bottom: 1px solid #ffffff;color: #d4d4d4;}
  .ck-category__title {color: #e0e0e0;}
  .ck-category-block__body {background-color: #757575;}
  .ck-category_account .ck-category__fill, .ck-category_debtaccount .ck-category__fill, .ck-category_classicgoal .ck-category__fill {    background-color: #444b56;}
  .ck-category_account .ck-category__actual-amount, .ck-category_debtaccount .ck-category__actual-amount, .ck-category_classicgoal 
  .ck-category__actual-amount {color: #c1c1c1;}
  .ck-category_account .ck-category__icon-wrapper:hover .ck-category__fill, .ck-category_debtaccount .ck-category__icon-wrapper:hover .ck-category__fill, .ck-category_classicgoal 
  .ck-category__icon-wrapper:hover .ck-category__fill {background-color: #3c3c3c;}
  .ck-category_account .ck-category__icon-wrapper:active .ck-category__fill, .ck-category_debtaccount .ck-category__icon-wrapper:active .ck-category__fill, .ck-category_classicgoal 
  .ck-category__icon-wrapper:active .ck-category__fill {background-color: #545454;}
  .ck-category-block__trigger {background-color: #5d5d5d;padding-top: 5px;}
  /* Лента покупок */
  .ck-transaction {background-color: #757575;}
  .ck-day-slice__date {color: #d6d6d6;}
  .ck-transaction__source {color: #ffffff;}
  .ck-transaction__destination {color: #e2e2e2;}
  .ck-transaction__comment {color: #eaeaea;font-style: NORMAL;}
  .ck-day-slice__footer {background-color: #5d5d5d;border-top: 1px solid #ffffff;}
  .ck-day-slice__footer-value {color: #d4d4d4;}
  .ck-transaction__tags-wrapper {color: #b5b5b5;font-style: normal;}
  /*Цвет цены красный*/
  .ck-transaction_type_negative .ck-transaction__amount {color: #ff8784;}
  .ck-day-slice__footer_negative .ck-day-slice__total .ck-day-slice__footer-value {color: #e2e2e2;}
  /* График доходов/расходов*/
  .Card {background-color: #5d5d5d;}
  .Dropdown .Dropdown-placeholder {color: #d4d4d4;}
  .Legend .LegendBody__row .item.clickable {color: #cccccc !important;}
  .Legend .LegendBody__row .right-item {color: #ff8784 !important;}
  .Legend .LegendBody__row:last-child .right-item {color: #81ca81 !important;}
  .Legend .LegendBody__row .middle-item {color: #bfbfbf;}
  .Chart .stage .axis text.active, .Chart .stage .axis text {fill: #ffffff;}
  .Chart .stage .base {fill: #757575;}
  .Chart .stage .zoomIn .circle, .Chart .stage .zoomOut .circle {fill: rgb(78, 78, 78);}
  .Chart .stage .zoomIn .label,.Chart .stage .zoomOut .label {fill: #ececec;}
  .Chart .stage .axis .tickHTML .tickHTML__DIV {color: #e8e8e8;}
  .Legend .LegendHeader__selectItem:not(.empty) {border: 1px solid #f3f2f2;background-color: #505050;}
  .Dropdown .Dropdown-menu {background-color: #5d5d5d;}
  .Dropdown .Dropdown-option {color: #d4d4d4;}
  .Chart .stage .line {opacity: 0.8;}
  /* Модалка счетов*/
  .ck-popup__block--account {background-color: #969696;}
  .ck-tap--account, .ck-tap--debt-account {background-color: #868686;}
  .ck-tap--account .ck-tap__item, .ck-tap--debt-account .ck-tap__item {background-color: #adadad;}
  .ck-card-item_icon--account, .ck-card-item_icon--debtaccount, 
  .ck-card-item_icon--classicgoal {background-color: #444b56;}
  .ck-card-item_icon--account:hover, .ck-card-item_icon--debtaccount:hover, .ck-card-item_icon--classicgoal:hover,
  .ck-card-item_icon--account:active, .ck-card-item_icon--debtaccount:active, .ck-card-item_icon--classicgoal:active,
  .ck-icon-picker__wrapper .ck-card-item_icon--account:hover {background-color: #484848;}
  /* Модалка доходов */
  .ck-popup__block--income {background-color: #969696;}
  .ck-card-item_icon--income {background-color: #444b56;}
  .ck-card-item_icon--income:hover, .ck-card-item_icon--income:active,
  .ck-icon-picker__wrapper .ck-card-item_icon--income:hover {background-color: #484848;}
  /*Модалка расходов */
  .ck-popup__block--expense {background-color: #969696;}
  .ck-tap__item--inactive {background-color: #868686!important;}
  .ck-tap--expense .ck-tap__item {background-color: #adadad;}
  .ck-card-item_icon--expense {background-color: #444b56;}
  .ck-icon-picker__wrapper .ck-card-item_icon--expense:hover,
  .ck-card-item_icon--expense:hover,
  .ck-card-item_icon--expense:active {background-color: #484848;}
  /* Модалка календаря */
  .ck-popup-message,.Datepicker_development__backgroundOverlay, .Datepicker_production__backgroundOverlay {background: rgba(37, 37, 37, 0.8);}
  /* Настройки */
  .Settings__header-title {color: #ececec;}
  .Settings .block {color: #c1c1c1;border-bottom: 2px solid #c5c5c5;}
  .Settings .color-dark {color: #b9b9b9;}
  /* Футер */
  .ck-footer {background-color: #383838;}
}
