css:
/** 

made by toddi & snjomorkr

**/







/** cs-manager.com only: **/

@-moz-document domain(cs-manager.com) {



#header {

	margin: 0 auto !important;

}

#container, #footer_container,.post,#content {

	margin: 0 auto !important;

}



body {	background: #3f4242 !important;  /** not really used as bg is white **/

}



a {

	color: #3f4242!important;

/*	font-style: italic !important; */

}



/** Add remote logo as bg and change colors of header **/

#header {

	background-color: #3f4242 !important;

	background-image: url("http://s2jeux.free.fr/csm/banner_theme_1.jpg") !important;

	background-repeat: no-repeat !important;

	border-right: 1px solid #3f4242 !important;

	border-bottom: 1px solid #3f4242 !important;

	border-left: none;

	border-top: none;

}





/** Hide original logo: **/

#header img {	visibility: hidden !important;} /** ingame **/

#header a img {	visibility: hidden !important;} /** front page **/





/** box containing it all and a few elements, that shouldnt have any specific colour **/

#container, #footer_container,.post,#content {

	color: #e3ca6d !important;

	background: #3f4242 !important;

	border-right: none;

	border-bottom: none; /** good manners to specify all borders **/

	border-left: none;

	border-top: none;

}



/** adding borders to some tables **/ 

table.rank,table.login {

	border: 1px solid #3f4242 !important; 

}

/** adding background to some tables **/ 

table.rank,table.login,table.mail,table.info {

	background: #3f4242 !important;

}

/** All sorts of 'small' elements needing colour **/

td.space,div.space {

	background: #3f4242 !important;

	border:none !important;

}

a.player,td.player,td.top,td.tactic,a.tactic {

	background: #3f4242 !important;

	color: #e3ca6d !important;

}

a.tactic:hover {

	background: #3f4242 !important;

}

td.position {

	background: #3f4242 !important;

}



/** Adding borders and bg-colour to headers **/

td.head, h2, h3 {

	color: #3f4242 !important; /** Changed by zAiw00t #666666 (Standard text and menu links) before **/

	background-color: #e3ca6d !important;

	background-image: none !important;

	border-top: 2px solid #3f4242 !important;

	border-left: 2px solid #3f4242 !important;

	border-right: 2px solid #3f4242 !important;

	border-bottom: 2px solid #3f4242 !important;

}



/** All sorts of links. (tabs, menu etc) **/

li a {

	background: #e3ca6d !important;

	color: #3f4242 !important;

	font-style: normal !important;

}



/** menu specific borders **/

ul#menu li a {

	border-bottom: 1px solid #3f4242 !important;

	border-right: none !important;

	border-left: none !important;

	border-top: none !important;

}



/** tab-specific borders **/

ul#tab li a {

	border: 1px solid #3f4242 !important;

}



li a:hover {

	background: #3f4242 !important;

	font-style: normal !important;

}



/** Selected tabs **/

ul#tab li span {

	background: #3f4242 !important;

	color: #3f4242 !important;

	font-style: normal !important;

	border-bottom: #3f4242 !important;

}



/** other spans (eg online in freinds list) **/

li span {

	color: #3f4242 !important;

}



/** color of areas surrounding maillist **/

table.mail tr.head td, table.mail td.select, table.mail td.delete {

	background-color: #3f4242 !important;

	color: #3f4242 !important;

}



/** dark area below the left and right menus **/

#menu, #footer_left, #footer_right, div#container>div#info {

	background: #3f4242 !important;

}



/** search area **/

form#search {

	border-bottom: 1px solid #3f4242 !important;

	background: #3f4242 !important;

}



/**input fields and buttons **/

input,select,textarea,radio {

	background: #3f4242 !important;

	border: 1px solid #3f4242 !important;

	color: #3f4242 !important;

}

.buttonsmall, .button {

	background: #3f4242 !important;

	color: #3f4242 !important;

	border-top: 2px solid #3f4242 !important;

	border-left: 2px solid #3f4242 !important;

	border-right: 2px solid #3f4242 !important;

	border-bottom: 2px solid #3f4242 !important;

	font-size: 10px !important;

	padding: 0px !important;

}



/** news items (community news) **/

td.value {

	background: #3f4242 !important;

	color: #3f4242 !important;

}

td.foot,td.info {

	background: #3f4242 !important;

	color: #3f4242 !important;

}



/** Training room colours **/

#training_map,#training_player,#skill,#trainer,#training_players {

	background: #3f4242 !important;

}

div#training_type {

	background: #3f4242 !important;

}



/** FORUM **/

tr.even {

	background: #3f4242 !important;

}

tr.odd {

	background: #3f4242 !important;

}

div.modify {

	background: #3f4242 !important;

}



table.dev tr td[style="color: green;"] {

	color: #3f4242 !important;

}



table.dev tr td[style="color: red;"] {

	color: #3f4242 !important;

}



}
