css:
@-moz-document url("https://theriver.jp/") {
nav.main-nav,
.main-banner,
.sub-banner,
.heading-major,
.article-info-wrapper,
.ranking,
.large-screens-only,
.app-banner-lg,
body>footer,
.two-col-wrapper,
.pr-content,
.premium-banner-pc
{display:none !important;}

.new-arrivals .article-item:first-child
{flex-basis: 33.333333%;}
.new-arrivals .article-item:first-child .flex-wrapper
{display: block;}
.new-arrivals .article-item:first-child .flex-wrapper h3.title
{font-size: 1.6rem;}
.new-arrivals .article-item:first-child .thumbnail-wrapper
{padding:0; margin: 0 0 1rem;}
.new-arrivals .article-item:first-child .flex-wrapper h3.title
{padding:0; line-height:2.6rem; max-height:12.48rem; height:auto;}
}

@-moz-document regexp("https://theriver.jp/\\w*-\\w*/"), regexp("https://theriver.jp/\\w*-\\w*-\\w*/"), regexp("https://theriver.jp/\\w*-\\w*-\\w*-\\w*/"), regexp("https://theriver.jp/\\w*-\\w*-\\w*-\\w*-\\w*/"), regexp("https://theriver.jp/\\w*-\\w*-\\w*-\\w*-\\w*-\\w*/"), regexp("https://theriver.jp/\\w*-\\w*/\\d/"), regexp("https://theriver.jp/\\w*-\\w*-\\w*/\\d/"), regexp("https://theriver.jp/\\w*-\\w*-\\w*-\\w*/\\d/"), regexp("https://theriver.jp/\\w*-\\w*-\\w*-\\w*-\\w*/\\d/"), regexp("https://theriver.jp/\\w*-\\w*-\\w*-\\w*-\\w*-\\w*/\\d/") {

nav.main-nav,
.sub-column,
.share-block,
.lodeo-ad-wrapper,
.related-posts,
.author-block,
.post-comments,
.the-content .second-paragraph-advertisment,
#logly-lift-widget,
.logly-lift-credit2,
.non-ads,
.premium-banner-pc
{display:none !important;}

.tags-block
{margin-bottom:100px !important;}

body.post article .title
{font-size:30px; margin:40px 0;}

.the-content p iframe
{width:730px; height:410px;}

body.post article img,
.the-content div.wp-caption.aligncenter img, .the-content figure.aligncenter img
{width:100%;}

.two-col-wrapper .main-column
{max-width:100%;}
}
