css:
@-moz-document domain("youtube.com") {
ytd-watch:not([flexy-fit-to-video_]) #related.ytd-watch {
    padding-left: 24px;
   margin-top: 500px;
}
#movie_player > div.html5-video-container > video {
    margin-left: 0px
}
#ytd-player{
    margin-left: 200px                                           
}
#movie_player > div.ytp-chrome-bottom {
    margin-left: 0px;
}
ytd-watch:not([transparent-player-background_]) #player-container.ytd-watch, ytd-watch[flexy-fit-to-video_]:not([is-currently-flexible_]) #player-container.ytd-watch, ytd-watch[theater] #player-container.ytd-watch {
    background: transparent
}

#show-hide-button > ytd-toggle-button-renderer {
    background: transparent
}
ytd-searchbox[mode=legacy] #container.ytd-searchbox, ytd-searchbox[mode=legacy-centered] #container.ytd-searchbox{
    background: #000000;
    border: #121212
}
#container.ytd-playlist-panel-renderer {
    margin-left: 15px;
    margin-top: 500px;
}
#chat.ytd-watch, #playlist.ytd-watch, #related.ytd-watch, #transcript.ytd-watch {
     position: unset;
}
ytd-watch:not([flexy-fit-to-video_]) #chat[increased-min-height].ytd-watch {
    margin-top: 500px
}
}
