css:
@-moz-document domain(www.myfitnesspal.com) {
#header #nav-bg {
        background-color:#4444;
    }
    #header #nav .nav_button:hover, #header #nav .nav_button.active {
        color:#fff !important;
        background:#003355;
    }
    #header #subNav-bg {
        background-color:#003355;
    }
    body {
        background:#222;
        color:grey;
    }
    .globalTopNav-2GCw3 {
        display: none;
    }
    body#food-diary table.table0 tbody tr td {
        background:#333;
        color:#aaa;
        text-align:center;
        padding:4px 10px
    }
    body#food-diary table.table0 tbody tr td a {
        color:#aaa
    }
    body#food-diary table.table0 tbody tr.bottom td {
        background-color:#555
    }
    body#food-diary table.table0 tbody tr.bottom td a {
        font-size:13px;
        color:#333
    }
    body#food-diary table.table0 tr.total td.first {
        text-align:right;
        background-color:#333;
        padding:4px 10px;
        border-bottom:0
    }
    body#food-diary table.table0 tr.spacer td {
        background-color:#333;
        border-bottom:0;
        line-height:8px;
        height:8px
    }
    body#food-diary table.table0 tr.total td.empty {
        background-color:#333;
        border-bottom:0
    }
    body#food-diary table.table0 tfoot tr td.first, body#food-diary table.table0 tfoot tr td.empty {
        background:#222;
        border:0
    }
    table.table0 tbody tr td {
        background-color:#f6f6f6;
        border-bottom:1px solid #e6e6e6;
        border-right:0;
        font-family:Arial, Helvetica, sans-serif;
        text-align:right;
        padding:6px 10px;
        margin-top:2px;
        vertical-align:middle
    }
    body#food-diary table.table0 tbody tr.meal_header td.alt {
        background:#222;
        color:#fff;
        padding:5px 10px 5px 10px;
        border:0;
        font-size:12px;
        font-weight:normal;
        text-align:center;
        text-transform:capitalize
    }
    #footer-wrapper {
        display:none;
    }
    #date_controls .date {
        background:#222;
        color:#fff;
        display:inline;
        font-size:15px;
        font-weight:bold;
        margin:0 10px 0 5px;
        padding:9px 35px 9px 35px;
        position:relative;
        border-radius:5px
    }
    div.diary h1 {
        margin-top:0;
        color:#lightgray;
        display:inline;
        font-size:16px;
        font-weight:bold
    }
    body#food-diary table.table0 tbody tr.bottom td {
     background-color:#555
    }

    body#food-diary table.table0 tbody tr.bottom td a {
     font-size:13px;
     color:#333
    }

    body#food-diary table.table0 tr.total td.first {
     text-align:right;
     background-color:#333;
     padding:4px 10px;
     border-bottom:0
    }

    body#food-diary table.table0 tr.spacer td {
     background-color:#333;
     border-bottom:0;
     line-height:8px;
     height:8px
    }

    body#food-diary table.table0 tr.total td.empty {
     background-color:#333;
     border-bottom:0
    }

    body#food-diary table.table0 tfoot tr td.first,body#food-diary table.table0 tfoot tr td.empty {
     background:#222;
     border:0
    }

    table.table0 tbody tr td {
     background-color:#f6f6f6;
     border-bottom:1px solid #e6e6e6;
     border-right:0;
     font-family:Arial,Helvetica,sans-serif;
     text-align:right;
     padding:6px 10px;
     margin-top:2px;
     vertical-align:middle
    }

    body#food-diary table.table0 tbody tr.meal_header td.alt {
     background:#222;
     color:#fff;
     padding:5px 10px 5px 10px;
     border:0;
     font-size:12px;
     font-weight:normal;
     text-align:center;
     text-transform:capitalize
    }

    #footer-wrapper {
     display:none;
    }

    #date_controls .date {
     background:#222;
     color:#fff;
     display:inline;
     font-size:15px;
     font-weight:bold;
     margin:0 10px 0 5px;
     padding:9px 35px 9px 35px;
     position:relative;
     border-radius:5px
    }

    div.diary h1 {
     margin-top:0;
     color:#lightgray;
     display:inline;
     font-size:16px;
     font-weight:bold
    }

    body#food-add h3.secondary-title {
     color:#777;
     display:block;
     position:relative
    }

    body#food-add table.table0 input.text {
     color:#000;
     margin-right:5px;
     width:40px
    }

    input.text,select.select,textarea.textarea {
     background:#555;
     border:0;
     border-bottom:1px solid #666;
     border-right:1px solid #777;
     color:#00;
     margin:3px 0;
     padding:5px;
     font-family:Arial,Helvetica,sans-serif;
     width:90px
    }

    body#food-add table.table0 input.text {
     color:#888;
     margin-right:5px;
     width:40px
    }

    body#food-add table.table0 select.select {
     color:#888;
     margin-left:5px;
     width:230px
    }

    div#diary {
        border: 0;
    }

    h4 {
        color: #888;
    }

    input {
        background-color: #333;
        border-bottom: 1px solid #555;
        border-right: 1px solid #444;
        border-top: 0;
        border-left: 0;
        color: #999;
    }

    body#excerciseSearch div.block-1 form {
     float:left;
     padding:0;
     width:360px !important
    }

    input.text,select.select,textarea.textarea {
     color:lightgrey;
    }

    body#excerciseSearch.searchadd div#content-block.block-4 div.col-2.block-3 {
     height:225px;
     width:330px;
     background-color:#333;
    }

    body#excerciseSearch div#loaded_item p.food-description {
     color:lightgrey;
     font-weight:bold;
     margin-bottom:3px;
     line-height:1.2em;
     font-size:1.2em
    }

    ul#matching li:hover {
     cursor:pointer;
     background:#333;
     color:#0f73ab;
     padding-left:5px
    }

    .water-notes-v2.block .note-container .note {
     background-color:#444;
     border-top:0;
     height:150px;
     overflow:auto;
     padding:12px;
     line-height:14px
    }

    .water-notes-v2.block h4 {
     font-size:16px;
     font-weight:bold;
     color:grey;
    }

    body#food-add table.table0 tbody td {
     background:#333;
     border:0;
     border-bottom:1px solid #555;
     border-top:1px solid #555;
     text-align:left;
     padding:1px 0 1px 10px
    }

.main-title,.main-title-2 {
 color:#0a5282;
 font-size:22px;
 font-weight:bolder;
 margin:17px 0 20px;
 padding-bottom:11px;
 border-bottom:0
}

div.pagination.alt a {
 border:1px solid #444;
 padding:4px 7px
}

div.diary {
 border-bottom:0;
 height:38px;
 margin:20px 0;
 padding:3px 0 7px;
 text-align:left
}

}
