    position: relative;
    padding: 0
}

.ui-autocomplete,.ui-menu .ui-menu,.ui-slider-handle {
    position: absolute
}

.ui-slider-handle {
    width: 36px;
    height: 16px;
    margin-left: -18px;
    margin-top: -6px;
    z-index: 200;
    background: #404040;
    box-shadow: 0 1px 3px 0 rgba(0,0,0,.6);
    border-radius: 5px;
    outline: 0;
