}

.ui-autocomplete {
    top: 0;
    left: 0;
    cursor: default;
    z-index: 999;
    overflow-y: auto
}

.ui-menu {
    list-style: none;
    padding: 0;
    margin: 0;
    display: block;
    background-color: #f9f9f9;
    border: 1px solid #26a
}

.ui-menu .ui-menu-item {
    position: relative;
    margin: 0;
    padding: 0;
    zoom:1;float: left;
