    padding: 10px 20px;
    color: #fff;
    border-radius: 10px;
    font: 700 14px "Helvetica Neue",Sans-Serif;
    box-shadow: 0 0 8px #000
}

.arrow {
    width: 70px;
    height: 16px;
    overflow: hidden;
    position: absolute;
    left: 50%;
    margin-left: -35px;
    bottom: -16px
}

.arrow.top {
    top: -16px;
    bottom: auto
}

.arrow.left {
    left: 20%
}

.arrow:after {
    content: '';
    position: absolute;
    left: 22px;
    top: -25px;
    width: 25px;
    height: 25px;
    -webkit-transform: rotate(45deg);
    -moz-transform: rotate(45deg);
    -ms-transform: rotate(45deg);
    -o-transform: rotate(45deg);
    transform: rotate(45deg)
}

.arrow.top:after {
    bottom: -25px;
    top: auto
}

