.spoiler {
    border: 1px dotted #d3d3d3;
    color: grey;
    padding: 2px;
    background: #e0e3e8
}

.spoiler:not(.expnded)::before {
    content: '>';
    padding: 0 8px
}

.spoiler:not(.expnded)::after {
    content: ' (click to show)'
}

.spoiler.expnded::before {
    content: 'v';
    padding: 0 8px
}

.spoiler.expnded::after {
    content: ' (click to hide)'
}

.spoiler-cnt {
    display: none;
    border-left: 1px solid #d3d3d3;
    border-bottom: 1px solid #d3d3d3;
    border-right: 1px solid #d3d3d3;
    padding: 2px
}

.spoiler-cnt::after {
    content: ".";
    display: block;
    clear: both;
    visibility: hidden;
    line-height: 0;
    height: 0
}

