}

.svgsparkle .spkl-small {
    animation: spkl-small 2.5s infinite
}

@keyframes spkl-large {
    0% {
        opacity: 0;
        transform: rotate(0) scale(0)
    }

    25% {
        opacity: 1
    }

    100%,50% {
        opacity: 0;
        transform: rotate(360deg) scale(1.5)
    }
}

@keyframes spkl-small {
    0%,20% {
        opacity: 0;
        transform: rotate(0) scale(0)
    }

    45% {
        opacity: 1
    }

    100%,70% {
        opacity: 0;
        transform: rotate(-360deg) scale(1.5)
    }
}

