css:
@-moz-document domain("igdb.com") {
/*
* Author        :  Hille
* URL           :  http://userstyles.org/users/24096
* Title         :  IGDB.com - Nightmode
* Version       :  1.1.0
*/
/***** colors used *******************************************
Background 1:		#1a1a1a
Background 2:		#202020
Background 3:		#282828
Borders:		#323232
Font-color:		#ccc
Link-color:		#1D7341
Link-color:             #0187c5
*************************************************************/

  .fa-comments:before,
.fa-circle:before
  {
    -webkit-filter: invert(.8);
  }

/***** border ***************************************/

/** #323232 **/

  #searchbar #searchButton input, 
#searchButton button,
#boxart,
aside h4, hr,
blockquote,
pre,
h4.head-title,
aside,
ul.social-grid li a,
.form-control,
.btn-default,
.panel,
.redactor-editor,
.modal-content,
.modal-header,
.tab-content,
.nav>li>a:hover, 
.nav>li>a:focus,
.pagination>.disabled>span, 
.pagination>.disabled>span:hover, 
.pagination>.disabled>span:focus, 
.pagination>.disabled>a, 
.pagination>.disabled>a:hover, 
.pagination>.disabled>a:focus,
.pagination>li>a,
.sorting-area,
.chosen-container-multi .chosen-choices,
.border-top,
.notif ul li:not(:last-child),
.chosen-container-single .chosen-single,
.chosen-container-single .chosen-drop,
.notif ul li:not(:last-child), 
.friends ul li:not(:last-child),
.well,
.page-header,
.nav-tabs-justified>li>a, 
.nav-tabs.nav-justified>li>a,
.game-changes #change_list ul li,
.table>thead>tr>th, 
.table>thead>tr>td, 
.table>tbody>tr>th, 
.table>tbody>tr>td, 
.table>tfoot>tr>th, 
.table>tfoot>tr>td,
.list-group-item,
.timeline,
.customisable-border,
.table-bordered tr,
.tweet-box-button,
.panel-default>.panel-heading,
.modal-footer,
.table tr,
.nav-pills li a,
.panel-autocomplete .panel-heading,
.fac-submenu,
.slick-slider .slick-caption p,
.panel-default>.panel-heading+.panel-collapse>.panel-body,
.panel-group .panel-heading+.panel-collapse>.panel-body, 
.panel-group .panel-heading+.panel-collapse>.list-group,
.slick-coverflow .slick-slide .slick-caption,
.game-card-caption,
.external-review-item,
.thumbnail,
.pulse-thumbnail,
.compwnent-tabs-list li a,
.gamepage-tabs,
.compwnent-tabs-list,
.gamepage-scrollspy,
.feed-item,
.comment-content,
.love,
.feed-item-right,
.feed-userbar-link:first-child,
.feed-userbar-link
  {
    border-color: #323232 !important;
  }

  path
  {
    stroke: #323232;
  }

  .media-thumbs-item.active
  {
    box-shadow: 0 0 0 2px #323232;
  }

/** #27AE60 **/

  #searchbar #searchButton button
  {
    border-color: #27AE60 !important;
  }

/** add border **/

  .modal-content
  {
    border: 1px solid #323232;
  }

/***** backgrounds **********************************/

/** #1a1a1a **/

  #searchbar,
.panel .panel-heading,
.redactor-toolbar,
.nav-alt.nav-tabs>li,
.slick-slider .slick-caption p,
.modal-fullscreen,
.love
  {
    background: #1a1a1a;
  }

/** #202020 **/

  pre,
table,
#topic_title.form-control,
.btn-default,
.panel,
.dropdown-menu,
.tt-dropdown-menu,
.breadcrumb,
.redactor-editor,
.tab-content,
.nav.nav-tabs,
.chosen-container .chosen-results li.no-results,
.well,
.table-striped tr:nth-child(even),
.list-group-item,
.timeline,
.table tr:nth-child(even),
.nav-pills li.active a, 
.nav-pills li.active a:hover, 
.nav-pills li.active a:focus,
.nav-pills li a,
.comments-form
  {
    background: #202020;
  }

/** #202020 !important **/

  .alert-success
  {
    background: #202020 !important;
  }

/** #282828 **/

  .table,
#searchbar #searchType select,
#searchbar #searchType input,
footer #footer-small,
ul.social-grid li a,
a.list-group-item:hover, 
a.list-group-item:focus,
.btn-default:hover, 
.btn-default:focus, 
.btn-default.focus, 
.btn-default:active, 
.btn-default.active, 
.open>.btn-default.dropdown-toggle,
.modal-content,
.form-control,
.nav>li>a:hover, 
.nav>li>a:focus,
.pagination>.disabled>span, 
.pagination>.disabled>span:hover, 
.pagination>.disabled>span:focus, 
.pagination>.disabled>a, 
.pagination>.disabled>a:hover, 
.pagination>.disabled>a:focus,
.pagination>li>a,
.pagination>li>a:hover, 
.pagination>li>a:focus, 
.pagination>li>span:hover, 
.pagination>li>span:focus,
.chosen-container-multi .chosen-choices,
.chosen-container-single .chosen-single,
.chosen-container-single .chosen-drop,
.chosen-container .chosen-drop,
.game-changes #change_list ul li:nth-child(odd),
.table-striped>tbody>tr:nth-of-type(odd),
.tweet-box-button,
.dropdown-menu .divider,
.game-changes .contributor,
.table tr:nth-child(odd),
.fac-all-suggest:hover, 
.fac-all-suggest.active,
.panel-forum .panel-body,
.timeline-Widget,
.toplists .panel,
.game-card-caption,
.thumbnail,
.pulse-thumbnail,
.feed-userbar-label
  {
    background: #282828;
  }

  circle
  {
    fill: #282828;
  }

/** #282828 !important **/

  body,
#search,
.input-group .form-control
.table>thead>tr>td.active, 
.table>thead>tr>th.active, 
.table>thead>tr.active>td, 
.table>thead>tr.active>th, 
.table>tbody>tr>td.active, 
.table>tbody>tr>th.active, 
.table>tbody>tr.active>td, 
.table>tbody>tr.active>th, 
.table>tfoot>tr>td.active, 
.table>tfoot>tr>th.active, 
.table>tfoot>tr.active>td, 
.table>tfoot>tr.active>th,
.form-control.tt-input
  {
    background: #202020 !important;
  }

/** #323232 **/

  .table-hover>tbody>tr:hover,
.forum-comment:before,
.member-review-game:not(:last-child):after
  {
    background: #323232;
  }

/** #27AE60 **/

  #searchbar #searchButton input, 
#searchbar #searchButton button,
#searchButton button
  {
    background: #27AE60;
  }

/** #34373B **/

  .alert
  {
    background: #34373B;
  }

/** transparent **/

  .panel .panel-heading .dropdown-toggle
  {
    background: transparent;
  }

/** Gradient **/

  .slick-coverflow .slick-arrow-prev, 
.slick-coverflow .slick-arrow-next
  {
    background-color: rgba(64, 64, 64, 0.75);
  }

/***** font *****************************************/

/** #ccc **/

  body,
#searchbar #searchType select,
#searchbar #searchType input,
#search,
footer #footer-small,
h4.head-title,
h4, .h4,
h2, .h2,
a.list-group-item,
a.list-group-item:hover, 
a.list-group-item:focus,
html, .SandboxRoot, 
h3.panel-title,
.btn-default,
.btn-default:hover, 
.btn-default:focus, 
.btn-default.focus, 
.btn-default:active, 
.btn-default.active, 
.open>.btn-default.dropdown-toggle,
.dropdown-menu>li>a,
.tt-dropdown-menu a,
.redactor-toolbar li a,
.nav-alt.nav-tabs>li a,
.form-control,
.chosen-container-single .chosen-single,
.chosen-container .chosen-results,
.title,
.p-author .profile .p-name, 
.custom-timeline-owner-profile .p-name, 
.cards-base p, .cards-base p a, 
.timeline-header .custom-timeline-summary, 
.timeline-header .custom-timeline-summary a:link, 
.timeline-header .custom-timeline-summary a:visited, 
.timeline-header .summary, 
.timeline-header .summary a:link, 
.timeline-header .summary a:visited, 
.p-author a.profile:hover .p-name, 
.p-author a.profile:focus .p-name,
.nav-pills li a,
.table tr.active th,
.link-dark-simple,
.table tr td,
.alert,
.fac-all-suggest,
.fac-submenu a,
.article,
.slick-slider .slick-caption p,
.article-index p,
.article-index #footer,
.game-card-caption,
.external-review-item,
.pulse-thumbnail h3,
.thumbnail .caption,
.underscratch>a,
.love .love-count,
.feed-userbar-label
  {
    color: #ccc;
  }

  .gauge text
  {
    fill: #ccc;
  }

/** #ccc !important **/
  .chosen-container-active .chosen-choices li.search-field input[type="text"],
span[style="color: rgb(0, 0, 0);"]
  {
    color: #ccc !important;
  }

/** #1D7341 **/
	
  .forum-title a:visited,
.chosen-container .chosen-results li.no-results
  {
    color: #1D7341;
  }

/** #0187c5 **/

  .link-dark,
.link-dark-simple:hover
  {
    color: #27AE60;
  }

/** #2ECC71 **/

  .fac-all-suggest:hover, 
.fac-all-suggest.active,
.article-index a h3
  {
    color: #2ECC71;
  }

/** #888 **/

  #forum .forum-row .forum-index-title a
  {
    color: #888;
  }

/** api colors **/

  .pln
  {
    color: #ccc;
  }

  .typ
  {
    color: #a94442;
  }

  .kwd
  {
    color: #337ab7;
  }

  .pun, .opn, .clo
  {
    color: #FFA100;
  }

  .com
  {
    color: #636363;
  }

  .lit
  {
    color: #46A048;
  }

/********************************************************************************************/

/*Small additions by Hekutoru*/

.game-card-overlay{
    background: rgba(0,0,0,0.5) !important;
}

.review-panel:hover:after{
    background: rgba(0,0,0,0.25) !important;
}

.review-panel:after{
    background: none;
}

.navbar-inverse{
    background-color: #333 !important;
}

.navbar-static-top-header, .dropdown-menu{
    background-color: #333 !important;
}
}
