css:
@-moz-document url-prefix(https://www.youtube.com) {
  ytd-watch, ytd-browse, ytd-search {
    background-color: #222;
    --yt-metadata-color: white;
    --ytd-video-publish-date-color: white;
    --yt-icon-color: white;
    --yt-button-color: white;
    --yt-placeholder-text-color: white;
    --yt-button-text-color: white;
    --ytd-comment-text-color: white;
    --iron-icon-fill-color: white;
    --paper-toggle-button-checked-button-color: white;
    --yt-main-app-background-tmp: #222;
    --yt-app-background: #222;
    --yt-channel-header-background: #282828;
    --yt-dialog-background: #222;
    --yt-menu-hover-backgound-color: #666;
    --yt-button-color: hsla(206.1, 79.3%, 52.7%);
  }
  ytd-sentiment-bar-renderer {
    margin-top: -4px;
  }
  .ytd-app {
    background-color: #333;
    --yt-primary-color: white;
    --yt-endpoint-color: white;
    --yt-spec-text-primary: white;
    --yt-expand-color: white;
    --yt-copyright-text: white;
    --yt-primary-text-color: white;
    --ytd-owner-badge-color: white;
    --yt-metadata-color:white;
    --yt-playlist-message-text:hsl(0, 0%, 53.3%);
    --ytd-badge-disabled-color: #333;
    --ytd-simple-badge-color: #333;
    --yt-playlist-background-header: #333;
  }
  ytd-popup-container ytd-multi-page-menu-renderer {
    background-color: #444;   
    --yt-simple-menu-header-background: #333;
  }
  ytd-popup-container yt-icon {
    color: white;   
  }
  #contentWrapper .dropdown-content {
    background-color: #333;
  }
  .style-default-active {
    --yt-icon-active-color: #999999;
    --iron-icon-fill-color: #999999;
  }
  ytd-search {
    --yt-button-color: white;
  }
  .ytd-app ytd-playlist-panel-renderer{
	--yt-spec-text-primary: white;
    --yt-endpoint-color: white;
  }
  div.ytd-app {
	background: #222;
  }
  paper-dialog {
    background-color: #222;
    --yt-secondary-text-color: white;
    --primary-text-color: white;
    --yt-std-surface-200: #333;
    --yt-dialog-background: #444;
    --yt-std-body-300: #555;
  }
}
