style_setting_options.value:
/* Move left column to right */
/*.page-dashboard*//*.min-width-0*/.logged-in.env-production.page-responsive.full-width:not(.project-page) .application-main div.d-md-flex.color-bg-canvas-inset .team-left-column:first-child {
	position: absolute;
    margin-left: 650px;	
}

/* Make news less wide */
/*.page-dashboard*//*.min-width-0*/.logged-in.env-production.page-responsive.full-width:not(.project-page) .application-main div.d-md-flex.color-bg-canvas-inset .col-lg-8 {
	width: 47.2%;	
}

/* Still show alert boxes */
/*.page-dashboard*//*.min-width-0*/.logged-in.env-production.page-responsive.full-width:not(.project-page) .application-main div.d-md-flex.color-bg-canvas-inset [aria-label="Explore"] {
	position: absolute;
    margin-left: 1000px;
	border-bottom: 0px!important;
}

/* Remove border */
/*.page-dashboard*//*.min-width-0*/.logged-in.env-production.page-responsive.full-width:not(.project-page) .application-main div.d-md-flex.color-bg-canvas-inset [aria-label="Account"] {
	border-right: 0px!important;
}

/* Center header */
.Header,.Header-old > div.px-3 {
	margin: auto;
	width: 1280px;
}
.js-header-wrapper {
    background-color: var(--color-header-bg);
}

/* center notification view */
.width-full.js-notifications-container {
	margin: auto;
	width: 1040px!important;
}
@media (min-width: 1280px) {
	.width-full.js-notifications-container .col-xl-2 {
		min-width: 230px;
	}
}

/* Less wide commit log */
.commits-listing, .file-navigation {
	max-width: 1280px;
    margin: auto;
}

/* Less wide head */
.hx_page-header-bg > div, .hx_page-header-bg > nav,	
.pt-3.hide-full-screen.mb-5 > div, .pt-3.hide-full-screen.mb-5 > nav {	
	max-width: 1280px;
    margin: auto;
}

.hx_page-header-bg, .pt-3.hide-full-screen.mb-5 {
	box-shadow: inset 0 -1px 0 var(--color-border-muted)
}

@media (min-width: 1720px) {
	.js-selected-navigation-item {
		visibility: unset!important;
	}
	
	.js-responsive-underlinenav-overflow {
		visibility: hidden;	
	}
}

/** Center news **/
/*.page-dashboard*//*.min-width-0*/.logged-in.env-production.page-responsive.full-width:not(.project-page) .application-main .col-12.col-md-8.col-lg-6 {
	max-width: 650px!important;
}

/*.page-dashboard*//*.min-width-0*/.logged-in.env-production.page-responsive.full-width:not(.project-page) .application-main {
	padding-left: 50%;
    margin-left: -25%;
}

/*.page-dashboard*//*.min-width-0*/.logged-in.env-production.page-responsive.full-width:not(.project-page) .application-main div.d-flex.flex-wrap.bg-gray {
	width: 1402px;
}
