}

/* Leiste für "zuletzt bearbeitet" verschieben */

.message-lastEdit {
    margin-top: 2.5em;
    color: #636363;
    font-size: 11px;
    text-align: right;
}

/* einstellknopf für Tickeransicht */

.homepage-settings-button {

    background-color: #2d2d2d;
    border-radius: 0 0 4px 4px;
    border: 1px solid;       
    border-top-color: #393838;
    border-right-color: black;
    border-bottom-color: black;
    border-left-color: #5f5f5f;
}

.homepage-settings-button:hover {
    background-color: #575757;
    border-left-color: #878787;
    border-top-color: #575757;
}

/* Knopf für RSS-Feed */

.share-link--feed {
    background-color: #ba7a54;
}

/* Header! */

/* Buttons im Header bei Hover+Klick färben */

.mainmenu__a:hover, .mainmenu__a[aria-expanded="true"] {
    background-color: #696969;
    color: #fff
}

.mainmenu__a {
    color: #eee;
    display: block;
}

.tagnav__a:hover {
 color:#ba7a5a;
}

.tagnav__a {
    background-image: none;
}



.header {
    background: #363636;
    border-left: 1px solid #363636;
    border-top: 1px solid #121212;
    box-shadow: 0 0 15px rgba(21, 21, 21, 0.3);
}

.big-header {
    background: #000;
}

.subheader {
    background: #484848;
    box-shadow: 0 10px 10px -10px rgba(0,0,0,.5);
}

.subheader::before {
    background: #363636;
}

.subNodeLink::before {   
    color: #959595;
    text-shadow: 1px 1px 0 rgba(47, 47, 47, 0.5);
}

