    background-image: url(data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%22960%22%20height%3D%2224%22%20viewBox%3D%220%200%20960%2024%22%3E%3Cpath%20d%3D%22M92%2024c60%200%2040-24%20100-24h768v24z%22%20fill%3D%22%23484848%22%2F%3E%3C%2Fsvg%3E);
