.box--blue {
    background: #262626;
    color: #fff;
}


/* Downloads-Box rechts */

.box--sidebar {
    background: #262626;
}

/* Meistgelesene News rechts */

.box-white {
    background: #262626;
    border-color: #000;
}

.block1--breakout, .block1--breakout-2 {
    background: #232222;
