.inline-search-form__query {
    background-color: #2c2c2c;
    border-color: #2f2f2f;
    color: white;
}

.geizhals-search__query {
    background-color: #515151;
    border-color: #414141;
    color: white;
}

#ghbody .box10h {
    background: #a4a4a4;
    color: #2c5b9c;
}

/* Schrift Geizhals */

.geizhals__title, .geizhals__subtitle {
    color: #ba7a5f;
}

/* Dropdown-menü für Geizhals-items */

