/* Follower-Links Position einstellen */

.footer2-follow-links {
    margin: -.5em -.5em 1em;
    padding-top: 15px;
    padding-left: 0;
    padding-right: 0;
    padding-bottom: 15px;
    background-color: #414141;
}

/* Promobar entfernen */

.promobar {
    display: none;
}

/* Dropdown Menü "Themen" färben + Schatten */

.tags-menu {
    background: #3c3c3c;
    border-radius: .25em;
    box-shadow: 0 0 25px 2px rgb(96, 63, 49);
    -moz-box-sizing: border-box;
}

/* Dropdown Menü "Themen" Hashtags entfernen */

.tags-menu__a::before {
    display: none;
}

/* Artikel / Tests Textcontainer färben */

.article-view {
    background: #000;
}

/* Artikel / Tests Inhaltsverzeichnis einfärben */

.toc__link {
    color: #7d7d7d;
}

/* Artikel / Tests Social-Media-Verlinkungen unterm Text entfernen */

.article-view__share-links-bottom {
    display: none;
}

/* Artikel Twitter-embed-Posts umfärben (Schriftart) */

.Tweet-text[dir="ltr"] {
    color: #545454;
}

/* Artikel Datenblatt und andere Tabellen färben */

.table tbody tr:nth-child(2n+1) {
    background: #686868;
}

.table thead tr {
    background: #3b3b3b;
}

.h3, .text-h3, .text-h4, .text-h5 {
    color: #666;
    font-size: 1.125em;
    padding-top: 1em;
    padding-bottom: 0.3em;
}

.toc__link {
    border-left: .1875rem solid transparent;
    border-right: .1875rem solid transparent;
    color: #7d7d7d;
    display: block;
    padding: .125rem 1em;
        padding-left: 1em;
    padding-left: 3.125em;
    position: relative;
}

.toc__link--current-page {

    background: #ddd;
    border-color: #e84d0e;
        border-right-color: rgb(232, 77, 14);
        border-left-color: rgb(232, 77, 14);

}

/* Artikel Diagramme und Vergleichsbalkendiagramme anpassen */


/* Balken färben */

.chart-bar-c12 {
    background: #fc6621;
}
.chart-bar-c5 {
    background: #2c5b9c;
}

.chart-row--hover .chart-item, .chart-row--focus .chart-item {
    background: #fc6621;
}

/* Falls man einen Balken markiert hat, Hintergrundfärbung */

.chart-row--hover .chart-bar-wrapper, .chart-row--focus .chart-bar-wrapper {
    background: -webkit-gradient(linear,left top,right top,color-stop(5%,#ebebeb),color-stop(15%,rgba(255,255,255,0)));
    background: -webkit-linear-gradient(left,#ebebeb 5%,rgba(255,255,255,0) 15%);
    background: linear-gradient(to right,#fc6621 5%,rgba(255,255,255,0) 15%);
}

/* Watermark in Balkendiagramm ändern! */

.chart {
    background: url("http://abload.de/img/logo7zux1.png") no-repeat bottom 1em right 1em;
}


/* Autorkasten am Ende eines Artikels formatieren */

.authorlist {
    margin: 0;
    padding-left: 0;
    padding-right: 0;
    padding-top: 14px;
    padding-bottom: 17px;

}

.author__name {
    color: #8d8d8d;
    margin-right: .125em;
}

/* Navigation am ende eines Tests formatieren */

.entry-nav__item {
    background: #535353;
}

.article-view__entry-nav {
    padding-top: 1.2em;
    padding-bottom: 1.2em;
}

.entry-nav__prev::before {
    border-right: solid #dadada;
        border-right-width: medium;
    border-width: 12px 10px 12px 0;
    left: 8px;
}

.entry-nav__link:hover {
 background:#000;
 text-decoration:none
}

.entry-nav__next::before {
    border-left: solid #dadada;
        border-left-width: medium;
    border-width: 12px 0 12px 10px;
    right: 8px;
}

/* Social-Media Links und Boxen entfernen */

.article-view__share-links-top {
    display: none;
}

/*Download Sektion Boxen färben */

.download-home__cats {
    background: #000;
    border-right: 1.875em solid #2d2d2d;
}

.download-home__featured {
    background: #000;
    padding: .625em .625em 0;
}

.download-cats-a1 {
    background: #4d4d4d;
}

.download-cats-icon {
    background: #a5a4a4;
    border: 2px solid #7d7d7d;
}

.download-featured {
    border-top: 1px solid #2d2d2d;
}

.related-box {
    background: #414141;
}

.download__versions {
    color: #666;
}

.download-notify-box__p {
    font-size: .875em;
    color: #ccc;
}

.download-notify-box:hover {
 background:#000;
 text-decoration:none
}

/*Auf Download-pages der Software Blöcke färben */

.block1--top::before {
    background: #272727;
}

.block1--top {
    background: #414141;
}

.row--download-view {
    background: #181818;
}

body {
    color: #d8d8d8;

}

.article-view__breadcrumbs {
    color: #848484;
}

.download-url__link {
    background: #aa5432;

}

.figure-title {
    color: #2c5b9c;
    font-weight: 700;
    padding-bottom: 1.25em;
    padding-top: 1.0em;
}

.gallery-current {
    background: #181818;
    color: #666;
}

.gallery-next--inline {
    border-left: solid #909090;
        border-left-width: medium;
    border-width: 22px 0 22px 20px;
    right: -.625em;
}

.gallery-prev--inline {
    border-right: solid #909090;
        border-right-width: medium;
    border-width: 22px 20px 22px 0;
    left: -.625em;
}

.related-list {
    background: #181818;
    -webkit-column-rule: 1.25em solid #ddd;
    -moz-column-rule: 1.25em solid #ddd;
    column-rule: 1.25em solid #232222;
    padding: 0 1.25em;
}

/* SCHRIFTEN */

/*IntroTeaser */

.article__intro {
    color: #b4b4b4;
}

.article__meta a:hover {
    color: #b1b1b1;
    text-decoration: none;
}

.article__context, .teaser__context, .slide__context, .charts__context {
    color: #eaeaea;
}

.charts__link:hover {
 background:#000;
 text-decoration:none
}

.teaser__label, .slide__label, .article__label {
    background: #484848;
}

/* Ticker Newstitel färben (auch Hover) */

a {
    color: #ba7a5f;
    text-decoration: none;
}

.ticker__article-label {
    background: #687f9e;
    color: #000;
}

.article__update, .ticker__update {
    color: #b13909;    
}

