css:
@-moz-document domain("yts.ag"), domain("yts.am")
{
footer {display: none}
#background-image,
.modal-container {background: #000000 !important}
.modal-content .modal-torrent {border-right: 1px solid #000}
.browse-movie-link {border: none}
.non-touch .browse-movie-link:hover {border: none}
img {vertical-align: middle; border-radius: 4px}
figure {overflow: hidden; position: relative; width: 100%; margin: 0; box-shadow: 2px 2px 10px 0px #000000}
.nav-bar {margin-right: auto; margin-left: auto; width: 100%; top: 0; position: fixed; z-index: 8; background: #1A1A1A; overflow: hidden; border-bottom: none; backface-visibility: hidden}
#quick-search-input {width: 500px; height: 35px; font-size: .85em; font-weight: bold; color: #919191; border: none; border-radius: 4px; -moz-border-radius: 20px; padding: 4px 30px; background: #00000000 url("https://yts.ag/assets/images/website/icon-search.svg") no-repeat 9px 12px}
.content-dark {background: #1A1A1A; width: 100%}
.browse-movie-tags {overflow: hidden; display: none}
.browse-content {background-color: #1A1A1A}
#main-search {padding: 50px 0}
.browse-content h2 {display: none}
ul.tsc_pagination li {margin-left: 10px}
ul.tsc_paginationA li a,
.pagination-bordered {border: none}
footer {display: none}
.addthis-smartlayers, div#at4-follow, div#at4-share, div#at4-thankyou, div#at4-whatsnext {display: none}
#movie-poster img {border-radius: 8px; -webkit-border-radius: 8px; -moz-border-radius: 8px}
ul.tsc_pagination li a,
.pagination-bordered {padding: 4px 10px !important}
ul.tsc_paginationA06 li.unavailable {border: none}
.nav-logo {display: none}
.header-slogan {display: none}
#main-search {padding: 10px 0px}
.main-nav-links {right: auto; left: auto; display: -webkit-inline-box; float: none; margin-right: -190px}
.ac-results {width: 500px !important}
#movie-poster img {border: none}
#movie-related a, #info-page .recent-downloads a {border: none}
.non-touch #movie-related a:hover,
.non-touch #info-page .recent-downloads a:hover {border: none; opacity: 0.5}
.browse-content section {margin-top: -10px}
.browse-movie-wrap {-webkit-animation-name: bounceIn; animation-name: bounceIn; -webkit-animation-duration: 1s; animation-duration: 1s; -webkit-animation-fill-mode: both; animation-fill-mode: both}
@-webkit-keyframes bounceIn {from, 20%, 40%, 60%, 80%, to {-webkit-animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000); animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000)}
0% {opacity: 0; -webkit-transform: scale3d(.3, .3, .3); transform: scale3d(.3, .3, .3)}
20% {-webkit-transform: scale3d(1.1, 1.1, 1.1); transform: scale3d(1.1, 1.1, 1.1)}
40% {-webkit-transform: scale3d(.9, .9, .9); transform: scale3d(.9, .9, .9)}
60% {opacity: 1; -webkit-transform: scale3d(1.03, 1.03, 1.03); transform: scale3d(1.03, 1.03, 1.03)}
80% {-webkit-transform: scale3d(.97, .97, .97); transform: scale3d(.97, .97, .97)}
to {opacity: 1; -webkit-transform: scale3d(1, 1, 1); transform: scale3d(1, 1, 1)}}
@keyframes bounceIn {from, 20%, 40%, 60%, 80%, to {-webkit-animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000); animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000)}
0% {opacity: 0; -webkit-transform: scale3d(.3, .3, .3); transform: scale3d(.3, .3, .3)}
20% {-webkit-transform: scale3d(1.1, 1.1, 1.1); transform: scale3d(1.1, 1.1, 1.1)}
40% {-webkit-transform: scale3d(.9, .9, .9); transform: scale3d(.9, .9, .9)}
60% {opacity: 1; -webkit-transform: scale3d(1.03, 1.03, 1.03); transform: scale3d(1.03, 1.03, 1.03)}
80% {-webkit-transform: scale3d(.97, .97, .97); transform: scale3d(.97, .97, .97)}
to {opacity: 1; -webkit-transform: scale3d(1, 1, 1); transform: scale3d(1, 1, 1)}}
#main-search-btn {margin-top: 48px !important}
.home-content h1 {display:none}
.home-content p {display:none}
.home-content #popular-downloads h2 {display:none}
.ibox-bordered {display:none}
body {background-color: #000000}
.content-dark {background: #000000}
#background-overlay {display: none}
.nav-bar {background: #0000008c}
.home-content .home-movies h2 a {color: #fff}
.non-touch .button-green-download2-big:hover {background: #fff; color: #000}
.button-green-download2-big {color: #fff; background: #0000008c}
figcaption span.icon-star {color: #fff}
figcaption span.icon-star:hover {color: #000}
.non-touch .nav-links li a, .non-touch .admin-nav a {color: #fff}
.non-touch .nav-links li a:hover, .non-touch .admin-nav a:hover {color: #202020}
.home-content figure img.quality-banner, .non-touch .browse-movie-link:hover figure img.quality-banner {opacity: 0}
#quick-search-container {float:right}
.nav-bar {width: 85%}
.ac-item-selected {background-color: #101010}
.ac-results li {border-top: 1px solid #00000000}
.ac-results ul {background: #000000}
#comments {display:none}
#comments .comment, #comments #no-comments, #comments #load-comments, #comments-box {display:none}
#movie-bottom #comment-count {display:none}
#movie-bottom span.icon-star {color:#fff}
#movie-bottom span.icon-star:hover {color:#101010}
#movie-bottom span.icon-comment {display:none}
.col-md-pull-11 {display:none}
.col-md-offset-1 {display:none}
#movie-tech-specs {display:none}
em {display:none}
.green-text-link {display:none}
#movie-sub-info .list-cast-info .name-cast{color: #fff}
.container {width: 1350px}
.col-lg-offset-1 {margin-left: -1%}
.col-lg-8 {width: 36%}
.col-md-6 {width: 36%}
.col-lg-5 {width: 22%}
.v211 {display:none}
#movie-info p a, .quality-tags {background: #00000000; border: 1px solid rgba(255, 255, 255, 0); color: #fff}
#movie-info p a:hover, .quality-tags:hover {background: #00000000; border: 1px solid #fff; color: #fff}
#movie-sub-info .list-cast-info .name-cast {Color:#fff}
#movie-sub-info:hover .list-cast-info:Hover .name-cast:hover {Color:#101010}
.browse-content {background-color: #00000000}
.non-touch ul.tsc_paginationA06 li a:hover, ul.tsc_paginationA06 li a.current {color: #fff; background: #000000; border-color: #101010}
.non-touch ul.tsc_paginationA06 li a:hover {color: #000; background: #fff; border-color: #ececec}
ul.tsc_paginationA06 li a.current {color: #fff; background: #101010; border-color: #101010}
.selects-container select {color: #ffffff; background: #000}
.selects-container select:hover {color: #000000; background: #fff}
#main-search-fields input {color: #ffffff; border-color: #fff; background: #000000}
#main-search-fields .term {color: #ffffff}
.selects-container p, #info-page .selects-container p {margin-bottom: 10px; font-weight: 700; font-size: .9em; color: #ffffff}
*::-webkit-scrollbar {width: 10px ! important; height: 10px ! important; background: #000 ! important; border: 0px solid #252525 ! important}
*::-webkit-scrollbar-button {display: none ! important}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {border: 0 ! important; box-shadow: none ! important;}
*::-webkit-scrollbar-thumb {min-height: 28px ! important; background: #202020 ! important}
*::-webkit-scrollbar-thumb:hover {background: #505050!important}
*::-webkit-scrollbar-thumb:active {background: #505050!important}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {background: #000 ! important}
}
