css:
@-moz-document domain("mkvcage.com"),domain("mkvcage.ws") {
/*----------Color Change-----------*/
body {background: #000}
.site-header {background: #000}
#main-navigation li a, #main-navigation li.sfHover > a, #main-navigation li.current_page_item > a, #main-navigation li.current-menu-item > a, #main-navigation li.current-menu-parent > a, #main-navigation li.current-page-parent > a, #main-navigation li.current-page-ancestor > a, #main-navigation li.current_page_ancestor > a {color: #fff; background: #000}
#main-navigation li a:hover, #main-navigation li.sfHover > a, #main-navigation li.current_page_item > a, #main-navigation li.current-menu-item > a, #main-navigation li.current-menu-parent > a, #main-navigation li.current-page-parent > a, #main-navigation li.current-page-ancestor > a, #main-navigation li.current_page_ancestor > a {color: #fff; background: #202020}
#main-navigation {border-bottom: 0px solid #00000000}
#main-navigation .sf-menu {background: #00000000}
#main-navigation li {border-right: 0px solid #00000000}
.site-content {border-right: 0px solid #00000000; border-left: 0px solid #00000000}
.entry-title a:hover {color: #202020}
.entry-title a {color: #fff}
.entry-meta {border-top: 0px Solid #00000000; border-bottom: 0px Solid #00000000; color: #fff}
.entry-meta a {color: #fff}
pre {border: 0px solid #000; background: #000}
body {color: #fff}
a:visited {color: #353535}
.comments-link a {background: #00000000}
#main-navigation li:hover ul, #main-navigation li.sfHover ul {background: #000}
#main-navigation li:hover li a:hover, #main-navigation li.sfHover li a:hover {background: #202020; color: #fff}
a {color: #fff}
.blue {color: #fff!important; border: 0px solid #00000000; background: #00000000}
.watch {color: #fff!important; border: 0px solid #00000000; background: #00000000}
.torrent {color: #fff!important; border: 0px solid #00000000; background: #45970f00}
.magnet { color: #fff!important; border: 0px solid #00000000; background: #00000000}
.site-content [class*="navigation"] a:hover {background: #202020}
.site-content [class*="navigation"] a {background: #000}
#main-navigation .sf-menu {background: #00000000}
.sim img {-webkit-box-shadow: 0 0 0px 0px #fff0}
pre {text-align: center; border: 0px solid #000; background: #000}
.br-720p img {overflow: hidden; background: #0000 top left no-repeat; border-radius: 0px; border: 0px solid #000; box-shadow: 0 0 0px 0px #000; -moz-box-shadow: 0 0 0px 0px #000; -webkit-box-shadow: 0 0 0px 0px #000}
#main-navigation li.current_page_item > a, #main-navigation li.current-menu-item > a {background: #fff; color: #000}
.br-1080p img {background: #0000 top left no-repeat; border-radius: 0px; border: 0px solid #000; box-shadow: 0 0 0px 0px #000; -moz-box-shadow: 0 0 0px 0px #000; -webkit-box-shadow: 0 0 0px 0px #000}
.site-sidebar .widget_search .search-field {background: #000; color: #fff}
.site-sidebar:hover .widget_search:hover .search-submit:hover {color: #000; background: firebrick}
.hd-dvd img {background: #0000 top left no-repeat; border-radius: 0px; border: 0px solid #000;  box-shadow: 0 0 0px 0px #000; -moz-box-shadow: 0 0 0px 0px #000; -webkit-box-shadow: 0 0 0px 0px #000}

/*----------Position Change-----------*/
.container {width: 1330px}
.entry-header {margin: 0 0 10px; text-align: center}
.entry-meta .cat-links {text-align: center}
.entry-title a {padding-left: 380px}
.br-720p img {padding-left: 315px}
.entry-meta {padding-left: 315px}
pre {width: 1270px}
.br-1080p img {padding-left: 315px}
.sim img {padding-left: 315px}
#main-navigation {padding-left: 210px}
.hd-dvd img {padding-left: 315px}


/*----------Hidden Objects-----------*/
#main-navigation li.current_page_item > a, #main-navigation li.current-menu-item > a {display: none}
.site-footer {display:none}
.widget_text .textwidget {display: none}
.widget ul {display: none}
#reply-title {display:none}
#commentform {display:none}
P {display:none}
.comment-list {display: none}
.entry-footer {display: none}
.widget-title span {display: none}
.widget-title {display: none}

/*----------Misc-----------*/
*::-webkit-scrollbar {width: 10px ! important; height: 10px ! important; background: #000 ! important; border: 0px solid #252525 ! important}
*::-webkit-scrollbar-button {display: none ! important}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {border: 0 ! important; box-shadow: none ! important;}
*::-webkit-scrollbar-thumb {min-height: 28px ! important; background: #202020 ! important}
*::-webkit-scrollbar-thumb:hover {background: #505050!important}
*::-webkit-scrollbar-thumb:active {background: #505050!important}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {background: #000 ! important}
}
