css:
@-moz-document domain("secura.e-sim.org") {
@font-face {
	font-family: 'Material Icons';
	src: url(https://fonts.gstatic.com/s/materialicons/v16/2fcrYFNaTjcS6g4U3t-Y5ZjZjT5FdEJ140U2DJYC3mY.woff2) format('woff2'),
		 url(http://onox.qiniudn.com/maverick/materialicons_safari_v16.woff) format('woff');
}

body {
    background: url(https://i.imgur.com/gQRyjuc.jpg)!important;
    
    /*background: url(https://i.imgur.com/7IN20Bk.jpg)!important;*/
}

body,
body .foundation-style,
div.switch.foundation-style label {
	font-family: "微软雅黑", "Microsoft Yahei", Helvetica, 'Helvetica Neue', Tahoma, Arial, sans-serif;
    -webkit-font-smoothing: antialiased;
}

::-webkit-scrollbar{width:6px!important;height:6px!important;}
body::-webkit-scrollbar{width:6px!important;height:6px!important;}

::-webkit-scrollbar-track{background:rgba(255,255,255,0.22)!important;border-radius:8px!important;}


::-webkit-scrollbar-thumb{background-color:#343434 !important;min-height:50px;border-radius:5px!important;}


@keyframes fadein {
0% {opacity: 0;}
100% {}
}

body {
animation-name: fadein;
animation-duration: 0.4s;
}

scrollbar *
  scrollbar scrollbarbutton{visibility:collapse!important;}
  scrollbar scrollbarbutton{display:none!important;}
 
  scrollbar[orient="vertical"]{background:rgba(255,255,255,0.22) url(https://pbs.twimg.com/media/CKecQtCWoAA8keI.png)!important;-moz-appearance:none!important;min-width:2px!important;max-width:2px!important;border-radius:5px;}
  scrollbar[orient="vertical"]:hover{-moz-appearance:none!important;}
  scrollbar thumb[orient="vertical"]{background:#6B6B6B!important;-moz-appearance:none!important;border-radius:4px!important;min-height:25px!important;min-width:2px!important;max-width:2px!important;border:1px!important;opacity:0.87;}
  scrollbar thumb[orient="vertical"]:hover{background:#6B6B6B!important;border-radius:4px!important;border:0px!important;opacity:1;}
  
  scrollbar[orient="horizontal"]{background:rgba(255,255,255,0.22) url(https://pbs.twimg.com/media/CKecQtCWoAA8keI.png)!important;-moz-appearance:none!important;background-color:transparent!important;opacity:.75!important;min-height:2px!important;max-height:9px!important;border-radius:5px;}
  scrollbar[orient="horizontal"]:hover{-moz-appearance:none!important;background-color:transparent!important;min-height:9px!important;max-height:9px!important}
  scrollbar thumb[orient="horizontal"]{background:#6B6B6B!important;-moz-appearance:none!important;border-radius:4px!important;min-height:7px!important;max-height:7px!important;border:1px!important;margin-left:0px!important;opacity:0.87;}
  scrollbar thumb[orient="horizontal"]:hover{background:#6B6B6B!important;border-radius:4px!important;border:0px!important;opacity:1;}

scrollcorner{opacity:0!important}
scrollbar scrollcorner:hover{background:transparent!important}

/*  global css  */

.canvaback {
    background: none!important;
}

.lightback2 {
	background: #e9e9e9!important;
}

.premiumStar {
	color: #ff9800!important;
	text-shadow: none;
}

.foundation-shadow,
.foundation-style input,
.foundation-style select,
.foundation-style textarea {
	box-shadow: none!important;
}

.foundation-style .testDivblue input,
.foundation-style .testDivblue select,
.foundation-style .testDivblue textarea {
	border: none !important;
	box-shadow: none !important;
	background: rgba(0, 0, 0, .08)!important;
	border-bottom: 4px solid rgba(0, 0, 0, .06) !important;
}

.foundation-style .testDivblue input:hover,
.foundation-style .testDivblue select:hover,
.foundation-style .testDivblue textarea:hover,
.foundation-style .testDivblue textarea:focus{
	border-bottom: 4px solid rgba(0, 0, 0, .17) !important;
	transition-property: border!important;
    transition-duration: .3s!important;
    transition-timing-function: ease!important;
}

input[type="button"],
input[type="submit"],
button.foundation-style,
.button.foundation-style,
form[action="search.html"] button.postfix.foundation-style {
	color: #666!important;
    background: rgba(0, 0, 0, .06)!important;
    border-radius: 6px;
    margin: 2px!important;
    border: none !important;
    border-bottom: 4px solid rgba(0, 0, 0, .06) !important;
    transition-property: border!important;
    transition-duration: .3s!important;
    transition-timing-function: ease!important;
    text-shadow: none!important;
}

input[type="button"]:hover,
input[type="submit"]:hover,
button.foundation-style:hover,
.button.foundation-style:hover,
form[action="search.html"] input:hover,
form[action="search.html"] input:focus,
form[action="search.html"] button.postfix.foundation-style:hover {
    border-bottom: 4px solid rgba(0, 0, 0, .17) !important;
	transition-property: border!important;
    transition-duration: .3s!important;
    transition-timing-function: ease!important;
}

hr.foundation-divider {
	border-top: none !important;
    border-bottom: 1px solid #999;
    margin: 10px 8px 15px;
}

.foundation-style.foundation-text-center.foundation-base-font.small-10.columns.column-margin-both.column-margin-vertical-down {
	display: none;
}

#userName,
.smallhelp,
.panelPlate,
.timeCountdown,
#limitStartText,
#stats > .progress,
.foundation-base-font-dark,
.sidebar-progres-description,
.sidebar-header,
.sidebar-clock .time,
.sidebar-clock b,
.sidebar-labeled-information span,
div.switch.foundation-style label {
	color: #777!important;
    text-shadow: none!important;
}

.sidebar-header {
	font-weight: 800;
}

/*  Nav  */

.top-bar {
    height: 49px;
    box-shadow: none;
    background: #f1f1f1!important;
    border-bottom: 4px solid #dbdbdb !important;
}

.top-bar-section .has-dropdown>a {
    padding-left: 20px!important;
    padding-right: 20px!important;
}

.top-bar-section ul li > a {
    color: #777;
    font-weight: 800;
    font-size: 16px!important;
    text-shadow: none!important;
    transition-property: border;
    transition-duration: .3s;
    transition-timing-function: ease;
}

.top-bar-section > ul > li:hover > a {
    background: #e5e5e5!important;
    border-bottom: 4px solid #bababa !important;
}

.top-bar-section .dropdown {
    background: #f3f3f3!important;
    border-top: 1px solid #9e9e9e;
}

.top-bar-section .dropdown li {
    border-left: 1px solid #9e9e9e;
    border-right: 1px solid #9e9e9e;
    border-bottom: 4.1px solid #dbdbdb !important;
}

.top-bar-section .dropdown li:hover a {
    background: #e5e5e5!important;
}

.top-bar-section .dropdown li:last-child {
    border-bottom: 1px solid #9e9e9e;
}

.top-bar-section .dropdown li a {
    color: #777;
    background: #f3f3f3!important;
}

.top-bar-section .dropdown li:last-child a {
    border-bottom: none!important;
}

#discordLink,
#minimalChatpanel,
.top-bar-section>ul>.divider,
.top-bar-section>ul>[role="separator"],
.top-bar-section .has-dropdown>a:after {
    display: none!important;
}

.column-margin-right.column.foundation-style,
.column-margin-right.columns.foundation-style {
    padding-right: 0!important;
}

.column-margin-vertical-up.columns.foundation-style {
    padding-top: 0!important;
}
/*  Nav right side  */

#ehIcon2 a img {
    height: 38px;
    width: 38px;
}

#userAvatar>a:first-child>img {
    margin: 0!important;
}

.active-icon>[class*="icon-"] {
    color: #888;
}

.row.foundation-style {
    margin-top: 20px;
}

.foundation-radius {
    border-radius: 20px!important;
}

#app-information-container {
    /*background-image: url(https://i.imgur.com/wIPQWNg.jpg);
    height: 198px;*/
    
    border-radius: 20px 20px 0 0;
}

#userMenu {
    padding: 0!important;
    background: rgba(0, 0, 0, 0) !important;
}

#userMenu .foundation-radius {
    background: #f1f1f1!important;
}


/* search player */

form[action="search.html"] .collapse {
    height: 44px !important;
    padding: 6px!important;
}

form[action="search.html"] .small-7 {
    width: 137px!important;
    float: none !important;
    height: 30px !important;
    display: inline-block;
}

form[action="search.html"] input {
    background: rgba(0, 0, 0, .06);
    box-shadow: none!important;
    border: none !important;
    outline: none !important;
    padding: 0 8px !important;
    border-bottom: 4px solid rgba(0, 0, 0, .04) !important;
    box-sizing: border-box;
    color: #666 !important;
    font-size: 12px !important;
    line-height: 26px !important;
}

form[action="search.html"] .small-3 {
    position: relative;
    width: 30px!important;
    height: 30px !important;
    margin-left: 4px;
    display: inline-block;
    box-sizing: border-box;
    float: none !important;
}

form[action="search.html"] button.postfix.foundation-style {
    color: #666 !important;
    height: 30px!important;
    padding: 0 !important;
    top: -5px!important;
    vertical-align: top;
	border-radius: 0 4px 4px 0;
}

#userMenu .icon-search {
	line-height: 30px;
}

form[action="editCitizen.html#chooseLanguage"] {
	display: none;
}

/* Daily task */

#dailyTaskSection,
#gsnbtn {
	margin-top: 15px!important;
}

#dailyButton {
 padding: 1px;
}

#dailyButton .button.foundation-style {
	background: #e5e5e5!important;
    border-radius: 3px;
}

#storageTable {
	padding-left: 6px;
}


/*  User Imformation  */

#userName {
    line-height: 25px;
    text-transform: capitalize;
}

.sidebar-labeled-information {
	height: 25px;
    line-height: 24px;
    border: 1px rgba(0, 0, 0, .1) solid !important;
    border-radius: 4px;
    background: rgba(0, 0, 0, .04);
    overflow: hidden;
    display: inline-block;
}

.sidebar-labeled-information > span:first-child {
    width: 36px;
    color: transparent!important;
    text-align: center;
}

.sidebar-labeled-information > *:nth-child(2) {
	height: 100% !important;
    background: rgba(0, 0, 0, .3) !important;
    color: #fff !important;
    padding: 0 4px;
    text-align: center;
    min-width: 24px;
}

#levelMission > span:first-child::before {
    content: "等級";
	color: #4C4C4C;
    
}

#currRankText > span:first-child::before {
    content: "軍銜";
	color: #4C4C4C;
}

#userMenu > div > div:nth-child(9) > div:nth-child(10) > div:nth-child(1) > span:first-child::before,
#userMenu > div > div:nth-child(11) > div:nth-child(10) > div:nth-child(1) > span:first-child::before {
	content: "經濟";
}

#userMenu > div > div:nth-child(9) > div:nth-child(10) > div:nth-child(2) > span:first-child::before,
#userMenu > div > div:nth-child(11) > div:nth-child(10) > div:nth-child(2) > span:first-child::before {
	content: "力量";
}

#userMenu > div > div:nth-child(9) > div:nth-child(10) > div:nth-child(3) > span:first-child::before,
#userMenu > div > div:nth-child(11) > div:nth-child(10) > div:nth-child(3) > span:first-child::before {
	content: "地點";
}

#userMenu > div > div:nth-child(9) > div:nth-child(10) > div:nth-child(4) > span:first-child::before,
#userMenu > div > div:nth-child(11) > div:nth-child(10) > div:nth-child(4) > span:first-child::before {
	content: "國籍";
}

#userMenu > div > div:nth-child(9) > div:nth-child(10) > div > span:first-child::before,
#userMenu > div > div:nth-child(11) > div:nth-child(10) > div > span:first-child::before {

	color: #4C4C4C;
}

.xflagsSmall {
	vertical-align: sub;
}

.currencyFlag {
	border: none!important;
}

.sidebar-labeled-information a:link,
.sidebar-labeled-information a:visited {
	color: #fff;
    text-shadow: none!important;
}

#slider {
    border-radius: 0 20px 0 0;
    margin-right: 0!important;
}

#stats3>.progress>.ui-corner-all, #stats.foundation-style>.progress>.ui-corner-all {
    width: 150px;
	border-radius: 0!important;
}

#stats .ui-progressbar {
    height: 16px!important;
    padding: 0!important;
    border: none!important;
	box-shadow: none!important;
    background: rgba(0, 0, 0, .1) !important;
}

#stats.foundation-style>.progress>#xpProgress>.ui-progressbar-value {
	background: rgba(3, 169, 244, .54)!important;
    border: none !important;
    border-radius: 0!important;
}

#stats.foundation-style>.progress>#rankProgress>.ui-progressbar-value {
    background: rgba(244, 67, 54, .8)!important;
    border: none !important;
    border-radius: 0!important;
}

#stats.foundation-style>.progress>#healthProgress>.ui-progressbar-value {
	background: rgba(76, 175, 80, .9) !important;
    border: none !important;
    border-radius: 0!important;
}

.foundation-style > .sidebar-progres-description:not([id]) {
	display: none;
}
.foundation-style > .sidebar-progres-description {
	position: relative;
    top: -21px;
    left: 10px;
    font-weight: 800;
}

#stats > .progress:nth-child(1)::before {
	content: "經驗 ";
    float: left;
}

#stats > .progress:nth-child(4)::before {
	content: "軍銜 ";
    float: left;
}

#stats > .progress:nth-child(7)::before {
	content: "體力 ";
    float: left;
}


/*  Hiding restore time  */
#stats + .foundation-style {
	display: none;
}

/*  Comsume Item  */

div.switch.foundation-style > span {
    border: none !important;
	background: rgba(0, 0, 0, .25);
}

div.switch.foundation-style:hover>span, div.switch.foundation-style:focus>span {
	background: rgba(0, 0, 0, .35);
}

div.switch.foundation-style {
    border: none !important;
	box-shadow: none !important;
    background: rgba(0, 0, 0, .05);
}

#consumable {
	background: rgba(0, 0, 0, .03);
}

#selectable, #selectable2, #selectable4, #selectable5, #selectable6 {
	border: none !important;
	box-shadow: none !important;
    background: rgba(0, 0, 0, .05);
}

#selectable li, #selectable2 li, #selectable4 li, #selectable5 li, #selectable6 li {
	color: #999;
	border: none !important;
	box-shadow: none !important;
    text-shadow: none !important;
}

#selectable li i, #selectable2 li i, #selectable4 li i, #selectable5 li i, #selectable6 li i,
#selectable li.ui-selected, #selectable2 li.ui-selected, #selectable4 li.ui-selected, #selectable5 li.ui-selected, #selectable6 li.ui-selected {
	text-shadow: none !important;
}
}

@-moz-document regexp("^https?://secura.e-sim.org/+(index.html.*)*([\\?#]+.*)*$"), regexp("^https?://secura.e-sim.org/+(shouts.html.*)*([\\?#]+.*)*$") {
#command {
	color: #666!important;
	border: 1px solid #999!important;
	box-shadow: none !important;
	text-shadow: none !important;
}

#command label {
	color: #666!important;
}

.shoutEditButtons > b {
	display: none;
}

a.voteLink {
	width: 25px;
    white-space: nowrap;
    display: inline-block;
}

a.voteLink, a.showShoutDetails {
	line-height: 25px;
    vertical-align: bottom;
    color: transparent!important;
}

.shoutModerator a.voteLink, .shoutModerator a.showShoutDetails {
	color: rgba(255, 0, 0, 0.175)!important;
}

a.voteLink:hover, a.showShoutDetails:hover {
    background: transparent!important;
}

a.showShoutDetails img {
	display: none;
}

a.voteLink::before {
	content: "\e8dc";
}

a.showShoutDetails::before {
	content: "\e0b9";
}

a.showShoutDetails font {
	color: #777;
}

a.voteLink::before,
a.showShoutDetails::before,
a.showShoutDetails font::before {
	display: inline-block;
    font-family: 'Material Icons';
    font-size: 14px;
    width: 30px;
    vertical-align: middle;
    text-align: center;
    color: rgba(0,0,0,.55);
}

a.voteLink::before,
a.showShoutDetails::before {
    width: 25px;
    height: 25px;
    border-radius: 50%;
	background-color: rgba(0, 0, 0, .1);
    vertical-align: middle;
}

a.showShoutDetails:hover::before {
	background-color: rgba(0, 0, 0, .15);
}

a.showShoutDetails font:nth-child(1)::before {
	content: "\e0ca";
}

a.showShoutDetails font:nth-child(3)::before {
    content: "\e8dc";
     font-size: 16px;
}
}

