.foundation-base-font {
	color: #666!important;
}

#newFightView .fightContainer .fightFlag,
#newFightView .fightContainer .topAttackers .hit img.smallAvatar,
#newFightView .fightContainer .topDefenders .hit img.smallAvatar {
	border: none !important;
	box-shadow: none !important;
}

#newFightView .fightContainer .topAttackers .hit div.xflagsSmall,
#newFightView .fightContainer .topDefenders .hit div.xflagsSmall{
	border: none !important;
	border-radius: 0!important;
	box-shadow: none !important;
}

.testDivblue {
	border: 1px solid rgba(0,0,0,.04)!important;
	box-shadow: none !important;
	background: #f2f2f2 !important;
}

#ticketQuality {
	border: none!important;
	box-shadow: none !important;
	background: rgba(0, 0, 0, .15)!important;
}

#battleHeaderImage {
	background: transparent!important;
}

#newFightView .fightContainer {
	background: rgba(0,0,0,.04) !important;
	border: none !important;
	box-shadow: none !important;

}

#newFightView .fightContainer .topPlayer .hitInfl,
#newFightView .fightContainer #roundCountdown,
#newFightView .fightContainer .fightRounds {
	border: none!important;
	box-shadow: none !important;
	background-image: none !important;
	background: rgba(0, 0, 0, .1)!important;
	border-bottom: 4px solid rgba(0, 0, 0, .06) !important;
}

#newFightView .fightContainer:nth-child(1),
#newFightView .fightContainer:nth-child(9),
#newFightView .fightContainer:nth-child(12) {
	border-radius: 15px 15px 0 0!important;
}

#newFightView .fightContainer:nth-child(2),
#newFightView .fightContainer:nth-child(11),
#newFightView .fightContainer:nth-child(14) {
	border-radius: 0 0 15px 15px!important;
