#newFightView .fightContainer:nth-child(9) {
	margin-top: 7px!important;
}

#newFightView .fightContainer:nth-child(11) {
	margin-bottom: 7px!important;
}

#newFightView .fightContainer:nth-child(13) {
	border-radius: 0!important;
}



#newFightView .fightContainer .fightButton {
	background-image: none !important;
}

.wd-35:nth-child(5) b {
	color: #4caf50!important;
}

.wd-35:nth-child(7) b {
	color: #f44336!important;
}

#attackerFirepower,
#newFightView .fightContainer #attackerScore {
	color: #f44336!important;
	text-shadow: none !important;
}

#defenderFirepower,
#newFightView .fightContainer #defenderScore,
#newFightView .fightContainer #defenderScore {
	color: #3f51b5!important;
	text-shadow: none !important;
}

#newFightView .fightContainer #roundCountdown {
	color: #666!important;
	text-shadow: none!important;
}

#newFightView .fightContainer .topAttackers .hit div.hitInfl,
#newFightView .fightContainer .topDefenders.topDefenders .hit>.hitInfl {
	border: none !important;
	background-image: none !important;
}

#newFightView .fightContainer .topAttackers .hit div.hitInfl {
	background: #e91e63!important;
}

#newFightView .fightContainer .topDefenders.topDefenders .hit>.hitInfl {
	background: #2196f3!important;
}

#battlesTable1,
.foundation-style .dataTable>tbody>tr:first-child>td {
	color: #777!important;
	font-weight: 800!important;
	background: transparent!important;
}

.foundation-style #pagination-digg li.active {
	color: #777!important;
	border: none !important;
	background: rgba(0, 0, 0, .08)!important;
	background-image: none !important;
	border-bottom: 4px solid rgba(0, 0, 0, .06) !important;
}

.foundation-style #pagination-digg li.active:hover {
	border-bottom: 4px solid rgba(0, 0, 0, .17) !important;
	transition-property: border!important;
    transition-duration: .3s!important;
    transition-timing-function: ease!important;
}

.foundation-style #pagination-digg li.active a {
	color: #777!important;
}
}

@-moz-document regexp("^https?://secura.e-sim.org/+(article.html.*)*([\\?#]+.*)*$") {
.testDivblue {
	border: 1px solid rgba(0,0,0,.04)!important;
	box-shadow: none !important;
	background: #f2f2f2!important;
	background-image: none!important;
}

.foundation-style .bigArticleTab, #voteButton, #subButton, #unsubButton {
	color: #999;
	background: transparent!important;
	border: none !important;
	text-shadow: none !important;
}

.foundation-style .bigArticleTab {
	background: rgba(0, 0, 0, .07)!important;
	border-bottom: 4px solid rgba(0, 0, 0, .04) !important;
}

.foundation-style .bigArticleTab:hover {
	border-bottom: 4px solid rgba(0, 0, 0, .17) !important;
}

.bbcodebuttons a {
	color: #777!important;
    background: rgba(0, 0, 0, .06)!important;
    border-radius: 6px;
    margin: 2px!important;
    border: none !important;
    border-bottom: 4px solid rgba(0, 0, 0, .045) !important;
    transition-property: border;
    transition-duration: .3s;
    transition-timing-function: ease;
    text-shadow: none!important;
}
}

@-moz-document regexp("^https?://secura.e-sim.org/+(train.html.*)*([\\?#]+.*)*$") {
#trainButton,#trainPushes {
