css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("spreadsheets.google.com"),
domain("docs.google.com") ,
url-prefix("http://www.google.com/calendar/"),
domain("mail.google.com"),
url-prefix("http://www.google.com/reader/"), 
url-prefix("https://www.google.com/reader") 
{

.modal-dialog .close_box{ visibility:hidden !important;}
.modal-dialog-title-close{ background:url(data:image/png;base64,) 0 2px no-repeat !important;
}

.modal-dialog .example{color:#888!important;}
.modal-dialog .goog-tree-children {color:#222 !important}


.modal-dialog-bg{background-color:#888888 !important}
.modal-dialog,.tr_raw-html-dialog{background-color:#4E575C!important;border:0px solid #4E575C!important;}
.modal-dialog{background-color:#000!important;color:#f2f2f2 !important;}


.modal-dialog fieldset{border:0 !important;}

.modal-dialog-content{background:#E4E5E7 !important;}

.tr_raw-html-dialog-body{background-color:#f0fdff !important}

.tr_button-region-rhs{background-color:#f0fdff !important;}


.modal-dialog-title,.tr_dialog-title{color:#fff!important;background-color:#2C3138 !important;}
.modal-dialog-buttons {background:#2C3138 !important;}

.modal-dialog-buttons button{color:#f2f2f2 !important;background-image: url(data:image/png;base64,) !important; padding:3px 6px !important;border:0px !important;-moz-appearance:none !important;font-size:12px !important;margin-right:10px;}

/* dark blue links */
.modal-dialog-content a,
.manage-sharing-link, .change-owner-link {color:#1A3764!important;}
/* global text */
.change-owner-contents div{color:#333!important;}


/* table */
.tabDlg {background-color:#ccc !important;margin:0 !important;padding:0 !important;}
.tabDlg a{color: #40474D !important;}


}
