css:
@-moz-document domain(forum.vio-v.com) {

#tplBoardList {
    background-image: url('https://forum.vio-v.com/wcf/images/nexusRed/pageTopBG.png'),url('https://forum.vio-v.com/wcf/images/nexusRed/pageBottomBG.png'),url('https://i.imgur.com/EkXptmT.png');
    background-position: center top,center bottom,center top;
    background-repeat: repeat-x,repeat-x,repeat-x;
    background-size: auto,auto,cover;
    background-attachment: scroll,scroll,fixed;
}

.popover > .popoverContent {
    background-color: #0d0d0d !important;
    color: white !important;
}

.messageFooterNote {
    color: white !important;
}

.dialogContent {
    background-color: #0d0d0d !important;
    color: white !important;
}

.content,
.wbbBoardList .wbbBoard {
    background-color: #0d0d0d !important;
    color: white !important;
}
.container {
    border: 0px !important;
}
.boxHeadline,
.boxHeadline > h1,
.boxSubHeadline > h2,
.boxHeadline > p,
.statsDataList,
dl.statsDataList > dt,
dl.statsDataList > dd,
.boxHeadline > h1 a,
.boxSubHeadline > h2 a,
.boxHeadline > .dataList,
.boxHeadline > .inlineDataList,
dl.inlineDataList > dt,
.faqContainer .faqFooterNote {
    color: white !important;
}

.icon, .fa {
   color: white !important;
}

#shoutbox,
.entryContent:hover,
.shoutboxEntry:hover,
.fadeOut {
    background-color: #1F1F1F !important;
    border: 0px !important;
    box-shadow: none !important;
}

.comment,
.tabMenu > li,
.ui-state-default,
.ui-tabs-nav {
    background-color: #1F1F1F !important;
}

.faqContainer:hover,
.faqQuestion {
    background-color: #1F1F1F !important;
}

.commentResponseList > li,
.recentActivityList > li,
.commentResponseList > li:hover {
    background-color: #1F1F1F !important;
}
.commentResponse {
    border-top: 0px !important;
}

.container {
    background-color: #1F1F1F !important;
}

.breadcrumbs {
    display: none !important;
}

.messageContent > div,
.message.messageSidebarOrientationLeft .messageContent {
    border: 0px !important;
    box-shadow: none !important;
    border-radius: 0px !important;
}
.message .messageContent .messageHeader {
    border: 0px !important;
    box-shadow: none !important;
    border-radius: 0px !important;
}
.messageBody {
    color: white !important;
    background-color: #0d0d0d !important;
    border-radius: 0px !important;
}
.quoteBox,
.quoteBox > span {
    color: white !important;
    background-color: #1F1F1F !important;
}

.wbbBoardList .wbbLastPost > div {
    background-image: none !important;
    background-color: #1F1F1F !important;
    border-radius: 0px !important;
    box-shadow: none !important;
    border: none !important;
}
.message {
    background-color: #1F1F1F !important;
    box-shadow: none !important;
    border: 1px solid #0d0d0d !important;
}
.containerList,
.containerList > li:hover,
.userList,
.userList > li:hover {
    background-color: #1F1F1F !important;
    color: white !important;
}
.containerList > li:nth-child(2n) {
    background-color: #1F1F1F !important;
}
.messageCollapsed {
    color: white !important;
}
.messageHeader {
    background-image: none !important;
    background-color: #1F1F1F !important;
}
.messageHeader h1,
.datetime {
    color: white !important;
}
.usersOnlineInfoBox,
.statsInfoBox {
    background-color: #1F1F1F !important;
    border: none !important;
}
#main > div > div > .sidebar {
    background-color: #1F1F1F !important;
}
#topMenu {
    background-image: none !important;
    background-color: #1F1F1F !important;
}
.tabularBox {
    border: none !important;
}
.message.messageReduced .messageContent,
.container.containerPadding.marginTop,
.dialogContent .wbbBoardListReduced > li > div,
.wbbBoardContainer.wbbDepth2 > div,
.wbbBoardList:not(.wbbBoardListReduced) > li.wbbDepth1 > div,
.wbbLastBoxElement > div,
ul.containerList > li,
li.wbbCategory ul:not(.wbbSubBoards) > li > div,
.wbbBoardContainer.wbbDepth2 > div:not(:last-child),
.table > tbody > tr > td,
.containerList > li {
    box-shadow: none !important;
}
.containerList > li:not(:last-child),
li.wbbCategory ul:not(.wbbSubBoards) > li:not(:last-child) {
    border-bottom: none !important;
}
.sidebar .sidebarBoxList > li:not(:last-child),
.sidebar fieldset > legend {
    border-bottom: none !important;
}
    
}
