css:
@-moz-document domain(www.myfitnesspal.com) {
    body {
        font-family: "Roboto", "Helvetica", "Arial", sans-serif !important;
    }
    .btn.btn-primary {
        color: #fff !important;
        background-color: #2196f3 !important;
        margin: 8px !important;
        padding: 8px 16px !important;
        font-size: 0.875rem !important;
        min-width: 88px !important;
        font-weight: 500 !important;
        border-radius: 2px !important;
        text-decoration: none !important;
        box-shadow: 0px 1px 5px 0px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 3px 1px -2px rgba(0, 0, 0, 0.12) !important;
        background-image: none !important;
        font-family: "Roboto", "Helvetica", "Arial", sans-serif !important;
        text-transform: uppercase !important;
        transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms, box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms;
        transition-property: background-color, box-shadow;
        transition-duration: 250ms, 250ms;
        transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1), cubic-bezier(0.4, 0, 0.2, 1);
        transition-delay: 0ms, 0ms;
    }
    .btn.btn-primary:hover {
        background-color: #1976d2!important;
    }
    .btn.btn-primary:active {
        box-shadow: 0px 5px 5px -3px rgba(0, 0, 0, 0.2), 0px 8px 10px 1px rgba(0, 0, 0, 0.14), 0px 3px 14px 2px rgba(0, 0, 0, 0.12)!important;
    }
    .button {
        margin: 8px !important;
        color: #fff !important;
        background-color: #d5d5d5 !important;
        padding: 8px 16px !important;
        font-size: 0.875rem !important;
        min-width: 88px !important;
        font-weight: 500 !important;
        border-radius: 2px !important;
        transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms, box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1) 0ms;
        transition-property: background-color, box-shadow;
        transition-duration: 250ms, 250ms;
        transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1), cubic-bezier(0.4, 0, 0.2, 1);
        transition-delay: 0ms, 0ms;
    }
    .button.gray {
        background-color: #d5d5d5 !important;
        text-decoration: none !important;
        color: rgba(0, 0, 0, 0.87) !important;
        box-shadow: 0px 1px 5px 0px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 3px 1px -2px rgba(0, 0, 0, 0.12) !important;
        font-weight: 600 !important;
        border-radius: 2px !important;
        background-image: none !important;
        font-family: "Roboto", "Helvetica", "Arial", sans-serif !important;
        text-transform: uppercase !important;
    }
    .button.gray:hover {
        background-color: #d5d5d5 !important;
    }
    .button.gray:active {
        box-shadow: 0px 5px 5px -3px rgba(0, 0, 0, 0.2), 0px 8px 10px 1px rgba(0, 0, 0, 0.14), 0px 3px 14px 2px rgba(0, 0, 0, 0.12) !important;
    }
    /* avatar */
    
    .user-info img {
        border-radius: 50%;
        display: inline-flex;
        overflow: hidden;
    }
    .user-info .ember-view {
        background: none !important;
    }
    .body {
        background-color: #eeeeee !important;
    }
    /* layout */
    
    .column-table-cell.column-right {
        display: none;
    }
    .__1860a {
        max-width: 100% !important;
    }
    .ember-view.home-screen-macros {
        display: grid!important;
        grid-template-columns: 20% 80%!important;
    }
    .col.user-info {
        margin-right: 0!important;
    }
    .col.summary-info {
        width: 100%!important;
    }
    .energy-calculation {
        width: 94%!important;
        margin-top: 20px!important;
        padding-top: 20px!important;
        border-top: 1px solid rgba(0, 0, 0, 0.12)!important;
    }
    .user-profile-photo {
        display: inline-block!important;
    }
    .user-profile-photo a {
        display: inline-block!important;
    }
    .user-profile-photo img {
        border-radius: 50%!important;
    }
    .post-status-update {
        text-align: center;
        width: 100%!important;
    }
    .post-status-update + button {
        float: none!important;
    }
    /* links */
    
    a[href="*/exercise/diary"].button:before {
        content: '?️';
        margin-right: 10px;
        font-size: 20px;
        position: relative;
        top: 3px;
    }
    a[href="*/food/diary"].button:before {
        content: '?';
        margin-right: 10px;
        font-size: 20px;
        position: relative;
        top: 3px;
    }
}
