css:
@-moz-document domain("forum.fanres.com") {
@keyframes logolight {
    47.5%, 50%, 52%, 54%, 100% {
        background-position: left 4px;
    }
    0%, 47%, 47.7%, 50.3%, 53.6% {
        background-position: right 4px;
    }
}
#logo {
    padding:0
}
#logo .wrapper {
    position:relative
}
#logo img {
    display:none
}
#logo .wrapper>a {
/*[[blinking-static]]*/
    height:56px;
    width:246px;
    display:inline-block;
}
#logo .wrapper ul {
    display:inline-block;
    position:absolute;
    bottom:0;
    right:0;
}
}

