css:
@-moz-document url-prefix("https://codecanyon.net/author_dashboard") {
.-theme-elite .page-section__background {
    display: none;
}
.t-heading.-color-dark,
.t-heading {
    color: #f9f9f9;
}
.t-body.-color-dark,
.t-body,
.page-tabs a {
    color: #b1b1b1;
}
section.page-section.h-pt3.h-mb3.h-pb0.-border-bottom.-theme-elite {
    margin-bottom: 0 !important;
    border-bottom: none;
}
section.page-section.h-pt3.h-mb3.h-pb0.-border-bottom.-theme-elite {
    background-image: url(https://public-assets.envato-static.com/assets/common/bg-page-section-4b4d6232f7d65b1eda49f92911855825b81774b95534ab3b8eb62e1decb37d71.png);
    background-size: 400px;
}
.content-main--basic {
    background: #262626;
}
.grid-container.h-mb3 {
    margin-top: 24px;
}
.header-categories {
    background-color: #363636;
    border-bottom: 1px solid #292929;
}
.header-categories__main-link,
.header-categories__main-link--empty {
    color: #b1b1b1;
}
.header-site-floating-logo__codecanyon {
    background-color: #1a1a1a;
}
.header-categories__search--experiment {
    border-left: 1px solid #666666;
}
input#term,
.-border-light button.search-field__button {
    background: #484848;
    border-color: #484848;
    color: #c7c7c7;
}
.-border-light button.search-field__button {
    font-size: 15px;
}
.e-box {
    background-color: #323233;
    border: 1px solid #363636;
}
.e-box.-stacked {
    background: #454545;
}
.box--hard-top {
    background-color: #323233;
    color: #bababa;
    border: none;
}
.t-icon-list a,
a,
.t-icon-list a:hover,
.user-html a,
.user-html a:hover {
    color: #00ffff;
}
.comment__initial {
    background-color: #454545;
    color: #e8e8e8;
}
.comment-reply {
    background-color: #323233;
    color: #bababa;
}
textarea.js-comment-new-reply-field.flexible-textarea {
    color: #e8e8e8;
    background: #454545;
    border: none;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.2);
}
.author-comments-search-controls input[type='search'] {
    border-radius: 5px;
    padding: 4px 8px 4px;
    vertical-align: 1.5px;
    color: #c7c7c7 !important;
    border: none;
    font-size: 15px;
    width: 405px;
}
.page-tabs .selected a {
    color: #ffffff;
    background: #666666;
    border: none
}
.page-tabs a:hover,
.page-tabs a:focus {
    border: none;
}
select.slt {
    background: #484848;
    color: white;
    border: none;
    vertical-align: 2px;
    font-size: 15px;
}
.upload-category-selector__select,
.upload-category-selector__btn {
    vertical-align: bottom;
}
.upload-category-selector__select {
    margin-right: 5px;
}
.e-box.-stacked.-radius-top.h-p2,
.e-box.-radius-bottom.h-mb2.h-p2.h-pb3,
.box--topbar:nth-of-type(3),
.box--hard-top.new-typography:nth-of-type(4) {
    display: none;
}
hr {
    background-color: #454545;
}
.avatar img[src="https://public-assets.envato-static.com/assets/common/icons-buttons/default-user-b8d0c2225ee9d9e68b5d80cd0ed6b7800c0135da303b735b777ac5a39f778a69.jpg"],
.comment-reply__avatar img[src="https://public-assets.envato-static.com/assets/common/icons-buttons/default-user-b8d0c2225ee9d9e68b5d80cd0ed6b7800c0135da303b735b777ac5a39f778a69.jpg"] {
    filter: invert(100%) brightness(1.2);
}
.cross-sell {
    margin-top: 0;
}
.comments-search-controls__results-count {
    color: #ababab;
}
button.e-btn.-color-muted {
    background: #484848;
}
button.e-btn.-color-muted:hover {
    background: #0084B4;
}
}
