css:
@-moz-document url-prefix("https://www.amazon.de/") {
#navSwmHoliday {
    background-repeat: no-repeat;
    border: none;
    text-align: right;
    background-size: 0;
}

#navbar #nav-search {

    margin-top: 0;
    padding-top: 25px;
    padding-right: 0px;
    padding-left: 210px;

}



.nav-locale-de #nav-logo .nav-logo-locale {
    display: none;
    width: 18px;
    height: 14px;
}

#navbar.nav-bluebeacon .nav-logo-tagline {
    color: #48a3c6;
    display: none;
}


#nav-search {
    position: relative;
    display: block;
    white-space: nowrap;
    overflow: hidden;
    width: auto;
    margin: 10px 0 0;
        margin-top: 10px;
}
#navbar.nav-bluebeacon {
    background-color: #fff;
}

.nav-packard-glow #nav-global-location-slot {
    float: left;
    padding: 6px 0 0 10px;
    text-decoration: none;
    margin-top: 2px;
    width: 192px;
    overflow: hidden;
    display: none;
}
#navbar.nav-bluebeacon #nav-belt {

    background-color: transparent;

}
#navbar.nav-bluebeacon #nav-main {

    background-image: none;
    background-color: transparent;

}
#nav-search .nav-search-field .nav-input {

    position: absolute;
    top: 0;
    left: 0;
    display: block;
    width: 100%;
    line-height: 15px;
    padding: 6px 10px 6px 0;
    margin: 0;
    height: 33px;
    font-family: arial,sans-serif;
    font-size: 15px;
    background: 0 0;
    color: #ff1212;
    outline: 0;
    -webkit-appearance: none;
    text-indent: 8px;
    border-width: 1px;
    border-color: black;

}

.nav-packard-glow #nav-xshop-container {

    position: static;
    float: none;
    padding: 2px 0 0 0;
    display: none;

}

.nav-packard-glow #nav-shop {

    padding: 6px 15px 0 0;
    width: auto;
    display: none;

}

#nav-tools {

    float: left;
    padding: 6px 7px 0 32px;
    text-decoration: none;
    margin-top: 2px;
    display: none;

}

ftGr-desktop-hero .image-map .cropped-image-map-size img {

    height: 300px !important;
    width: 1500px !important;
    border-radius: 10px;

}

.fresh-shoveler .feed-carousel .feed-carousel-card .product-image {

    width: auto;
    height: auto;
    max-width: 270px;
    max-height: 200px;
    vertical-align: middle;
    border-radius: 10px;

}

#btfContent, .btfContent {

    padding: 5px 20px 0 20px;
    background-color: #000;

}

body {

    font-size: 13px;
    line-height: 19px;
    color: #111;
    font-family: Arial,sans-serif;
    background-color: #000;

}

#nav-main .nav-fill, #nav-main .nav-left, #nav-main .nav-right, #navbar .nav-main .nav-fill, #navbar .nav-main .nav-left, #navbar .nav-main .nav-right {

    height: 55px;
    background-color: #000;

}
#navbar.nav-bluebeacon #nav-belt {

    background-color: #000;

}

#navbar.nav-bluebeacon #nav-main {

    background-image: none;
    background-color: #000;

}

.gw-ftGr-desktop-hero .gw-hero-image .cropped-image-map-size img, .gw-ftGr-desktop-hero .image-map .cropped-image-map-size img {

    height: 300px !important;
    width: 1500px !important;
    border-radius: 10px;

}

#navFooter.navLeftFooter {

    margin-bottom: 0;
    padding-bottom: 0;
    background-color: #232f3e;
    min-width: 1000px;
    position: relative;
    display: none;

}

#pageContent .sidebar, #sidebar {

    position: relative;
    padding: 20px 23px 10px 23px;
    background: #000;

}

.fresh-shoveler .as-title-block .a-color-base {

    color: #cbcbcb !important;

}

a, a:active, a:link, a:visited {

    text-decoration: none;
    color: #a1a1a1;

}

img {

    vertical-align: top;
    border-radius: 10px;

}

#rhf .rhf-divider {

    border-top: 1px solid #DDD;
    height: 23px;
    margin-bottom: -6px;
    background: -moz-linear-gradient(top,#ddd,#f7f7f7 3px,#fff);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#ddd),color-stop(3px,#f7f7f7),color-stop(100%,#fff));
    background: -webkit-linear-gradient(top,#ddd,#f7f7f7 3px,#fff);
    background: -o-linear-gradient(top,#ddd,#f7f7f7 3px,#fff);
    background: -ms-linear-gradient(top,#ddd,#f7f7f7 3px,#fff);
    background: linear-gradient(top,#ddd,#f7f7f7 3px,#fff);
    filter: none;
    z-index: 0;
    zoom: 1;
    display: none;

}

#rhf .rhf-border {

    border: px solid #DDD;
    border-radius: 5px;
    padding: 0 20px;
        padding-top: 0px;
        padding-right: 20px;
        padding-bottom: 0px;
        padding-left: 20px;

}

#rhf .rhf-header {

    font-size: 20px;
    text-align: left;
    display: inline-block;
    color: #cbcbcb;

}

.a-button:hover .a-button-inner {

    background: #000;

}

.a-button-image .a-button-inner {

    padding: 6px;
    line-height: 0;
    height: auto;
    background: #000;

}

#rhf .rvi-container {

    height: 75px;
    line-height: 75px;
    overflow: hidden;
    padding: 0 10px;
    position: relative;
    display: none;

}

.nav-subnav #nav-subnav.spacious {
    display: -webkit-box;
    display: -moz-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -webkit-justify-content: space-between;
    -moz-justify-content: space-between;
    justify-content: space-between;
    height: 50px;
    background: #fff;
    box-shadow: 0 0 1px #ddd;
    display: none;
}

body {
    font-size: 13px;
    line-height: 19px;
    color: #fff;
    font-family: Arial,sans-serif;
}

.a-size-small {
    font-size: 12px !important;
    line-height: 1.5 !important;
    color: #fff;
}

hr.bucketDivider {
    border: none;
    height: 44px !important;
    margin-bottom: -18px;
    background: -moz-linear-gradient(top,#ddd,#f7f7f7 3px,#fff);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#ddd),color-stop(3px,#f7f7f7),color-stop(100%,#fff));
    background: -webkit-linear-gradient(top,#ddd,#f7f7f7 3px,#fff);
    background: -o-linear-gradient(top,#ddd,#f7f7f7 3px,#fff);
    background: -ms-linear-gradient(top,#ddd,#f7f7f7 3px,#fff);
    background: linear-gradient(top,#ddd,#f7f7f7 3px,#fff);
    filter: none;
    z-index: 0;
    zoom: 1;
    display: none;
}

#quickPromoBucketContent ul li {
    color: #fff;
}

.a-input-text, input[type="text"], input[type="search"], input[type="number"], input[type="tel"], input[type="password"], select.a-select-multiple, textarea {
    border: 0px solid #a6a6a6;
        border-top-color: rgb(166, 166, 166);
    border-top-color: #949494;
    border-radius: 9px;
    box-shadow: 0 1px 0 rgba(255,255,255,.5),0 1px 0 rgba(0,0,0,.07) inset;
    outline: 0;
}

.a-color-secondary {
    color: #fff !important;
}

.a-box .a-divider.a-divider-section .a-divider-inner, .a-color-base-background .a-divider.a-divider-section .a-divider-inner, .a-divider.a-divider-section .a-divider-inner {

    height: 44px;
    margin-bottom: -18px;
    background: -webkit-linear-gradient(to bottom,rgba(0,0,0,.14),rgba(0,0,0,.03) 3px,transparent);
    background: linear-gradient(to bottom,rgba(0,0,0,.14),rgba(0,0,0,.03) 3px,transparent);
    z-index: 0;
    zoom: 1;
    display: none;

}

#productDescription_feature_div > h2.softlines {

    color: #fff !important;
    font-size: 21px !important;
    line-height: 1.3;
    padding-bottom: 4px;
    font-weight: bold;
    margin: 0px;

}

#productDescription {

    color: #fff;
    word-wrap: break-word;
    font-size: small;
    line-height: initial;

}

#detail-bullets ul, #detail_bullets_id ul, #feature-bullets-btf ul, #technical-data ul {

    color: #fff;
    font-size: 13px;
    font-family: verdana,arial,helvetica,sans-serif;

}

.askInlineWidget .vote input[type="submit"] {

    -webkit-background-size: 100%;
    -moz-background-size: 100%;
    -o-background-size: 100%;
    background-size: 100%;
    background-repeat: no-repeat;
    text-indent: -9999em;
    font-size: 1px;
    border: none;
    width: 20px;
    height: 16px;
    background-color: #000;

}

.a-button a, .a-button:hover a {

    color: #fff;
    text-decoration: none !important;

}

.a-color-base {

    color: #ddd;

}

#rhf .rhf-border {

    border: px solid transparent;
    border-radius: 5px;
    padding: 0 20px;

}

.a-ordered-list .a-list-item, .a-unordered-list .a-list-item, ol .a-list-item, ul .a-list-item {

    color: #fff;

}

select {

    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    padding: 3px;
    border: 1px transparent;

}

.a-inline-block, .aok-inline-block {

    display: inline-block;
    border-radius: 9px;

}

.a-color-base {

    color: #fff !important;

}

table.a-bordered td, table.a-bordered th {
    border-bottom: 1px solid #000;
    background-color: #000;
}

.comparison_table tr:nth-child(2n) td.comparison_baseitem_column {
    background-color: #000;
    padding-left: 10px;
}

#prodDetails .col1 td, #prodDetails .col2 td {
    padding: 3px 1px 3px 10px;
    border-top: 0px dotted #ccc;
    line-height: 18px;
}

#prodDetails .content td {
    font-size: 12px;
    color: #fff;
}

#prodDetails table td.label {
    background-color: #000;
    width: 35%;
    color: #b6b6b6;
    vertical-align: top;
}

.aplus-v2 * {
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    border-radius: 9px;
}

.aplus-v2 th.apm-tablemodule-keyhead {
    border-top: 1px solid #000;
    padding: 8px 14px 6px !important;
    background-color: #000;
    font-weight: normal;
    color: #e1e1e1;
    float: none !important;
    margin-right: 0;
    text-align: center;
    width: 106px;
}

.aplus-v2 tr.apm-tablemodule-keyvalue td {
    padding-top: 8px !important;
    padding-left: 14px;
    padding-bottom: 8px;
    border-top: 1px solid #000;
    border-bottom: 1px solid #000;
    text-align: center;
    width: inherit !important;
}

tr:first-child td, tr:first-child th {
    padding-top: 0;
    display: none;
}
}
