css:
@-moz-document domain("mewe.com") {
.header_inner {max-width: none !important; margin: 0px 20px !important}
.header_nav_list {width:40vw}
.header_logo_wrapper {margin: 0px 30px !important}
.header_notification-btn {margin: 0px 60px !important}
.content-all-wrapper {max-width: none; width: 98vw}
.c-mw-post {max-width: none}
.c-mw-postbox-placeholder {max-width: none !important}
.postbox_header_textarea {max-width: none !important}
.c-feed-simple {max-width: none !important}

/* Media viewer */
.media-dialog_post--comment-fixed {width:40vw !important}
.media-dialog_inner {max-width: none}
.media-dialog.content-high.c-mw-comment-your {width:40vw !important}

/* Emoji picker */
.c-mw-emoji-picker {width:670px !important; margin-left: -300px !important}
.like-category li {width:45px !important}
.category li {width:45px !important}
.search.active {padding-top:6px !important; padding-bottom:3px !important}

/* Groups */
#content-wrapper {max-width: none !important}
.group-feed-routable_feed {flex-basis: 100% !important}
.c-mw-group-whats-new.ember-view {max-width: 15vw !important}
.sidebar-right-group {width: auto !important}

/* Fullscreen chat */
.chat-message_content {max-width: 100% !important}

/* Search results */
.dialog_inner {max-width: 80% !important; padding-right: 45px !important}
.smart-search_results {max-width: none !important; width: 70vw !important}
.smart-search_menu--visible {margin-left: -5vw !important}

/* Userpages */
.profile-posts-routable_feed {flex-basis: 100% !important}
}
