css:
@-moz-document domain("justdancenow.com"), domain("justdancenow.ca")
{
/*------------JDNMenu--------------*/

  #just-dance-now
  {
    background: url(https://i.imgur.com/eycqMiS.png) center/cover no-repeat;
    background-size: 100% 100%!important;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
  }

  #song-selection
  {
    background-image: url(https://i.imgur.com/30gKdYE.png);
    background-size: cover;
  }

  #afterdance
  {
    background-image: url(https://i.imgur.com/mkShSB1.png)!important;
    background-size: cover;
  }
/*----------------Players----------------*/

  #players
  {
    position: absolute;
    top: 0px;
    left: 17.5%;
    z-index: 990;
    width: 67%;
    height: 20%;
  }

  .state-dance #players
  {
    left: 17.5%;
  }

  .state-dance #players .player-stars
  {
    bottom: 29%;
  }

  .dancercard.dancercard .player-name
  {
    text-shadow: 1px 1px 1px rgba(150, 150, 150, 1) !important;
    bottom: 15%;
    font-size: 80%;
    -webkit-mask-image: -webkit-gradient(linear, left bottom, right bottom, color-stop(0.00, rgba(0, 0, 0, 0)), color-stop(0.35, rgba(0, 0, 0, 0.95)), color-stop(0.50, rgb(255, 255, 255)), color-stop(0.65, rgba(0, 0, 0, 0.9)), color-stop(1.00, rgba(0, 0, 0, 0)));
  }

  .dancercard .player-name em
  {
    text-shadow: 1px 0px 0px rgb(47, 47, 47);
    overflow: visible;
  }

  .dancercard .player-color,
.player--new .player-color
  {
    transform: scale(1.5, 0.2);
    top: 32%;
  }

  .state-dance #players .crown
  {
    position: absolute;
    top: 30.5%;
    left: -10.0%;
    transform: rotate(-15deg);
  }

  html:not(.ipad) .hud.beat>#players.animate .player-color
  {
    animation: none;
  }

  .player-color::after
  {
    transform: rotate(-2deg);
    width: 97%;
  }

  .img-crown
  {
    display: none !important;
  }

  .dancercard .avatar-wrapper
  {
    top: -125%;
    left: 18%;
    width: 60%;
    transform: scale(0.7);
  }

  .dancercard .player-flag
  {
    width: 0%;
    left: -100%;
  }

  .dancercard .player-exp
  {
    right: -100%;
    color: #ffffff;
    width: 0%;
  }

  .dancercard .player-logo--vip
  {
    width: 0%;
    height: 100%;
    opacity: 1;
  }

  info::after
  {
    height: 0rem;
  }

  .preview--bg::before
  {
    content: "";
    display: block;
    position: absolute;
    top: -0.41rem;
    right: 8.33rem;
    width: 0rem;
    height: 4.3rem;
    background-color: #ffffff;
    transform: rotate(-12deg);
    transform-origin: top right;
    z-index: -1;
  }

  .dancercard.dancercard .player-name
  {
    bottom: 1%;
    font-size: 55%;
    -webkit-mask-image: -webkit-gradient(linear, left bottom, right bottom, color-stop(0.00, rgb(0, 0, 0)), color-stop(0.35, rgba(0, 0, 0, 0.95)), color-stop(0.50, rgb(255, 255, 255)), color-stop(0.65, rgba(0, 0, 0, 0.9)), color-stop(1.00, rgb(0, 0, 0)));
  }

  .state-dance #players .dancercard .avatar-wrapper
  {
    opacity: 0;
  }

  .state-dance #players .dancercard.dancercard .player-name
  {
    bottom: 15%;
    font-size: 80%;
  }

  .coach-selection__list
  {
    right: 30%;
    width: 115%;
    height: 100%;
  }

  .coach__img
  {
    height: 150%;
    bottom: auto;
  }
/*----------------SongCover----------------*/

  .song--focused img.song__cover
  {
    animation: focusZoom 3s infinite;
  }

  .item-container.item-container--y.favorites .item-container.item-container--x .song--focused::before
  {
    top: -88% !important;
    left: -15% !important;
    transform: rotate(-16deg) scale(1) !important;
  }

  .item-container.item-container--y.favorites .item-container.item-container--x .song::before
  {
    content: url(https://i.imgur.com/scH6UMi.png);
    position: absolute;
    z-index: 9999999;
    top: -40%;
    left: -15%;
    transform: rotate(-16deg) scale(1);
  }

  .song__cover
  {
    width: 115%;
    -webkit-mask-image: url(https://i.imgur.com/1LJPEOX.png);
    -webkit-mask-size: 100%;
    -webkit-mask-position: center;
    -webkit-mask-repeat: no-repeat;
    top: -4%;
    left: -6.5%;
  }

  .song--focused .song__decoration::after
  {
    background-image: url(https://i.imgur.com/pZjyA3V.png);
    z-index: 9999999;
    width: 95.5%!important;
    height: 95.5%!important;
    left: 3%;
    top: 5%;
  }
/*----------------DanceRoom----------------*/

  #room-info::before
  {
    left: -14%;
    opacity: 0.3;
  }

  .dialog #room-info::before
  {
    left: -14%;
    opacity: 0.5;
  }

  .dialog #room-info
  {
    left: -1%;
    opacity: 1;
  }

  #room-info
  {
    left: -1%;
    opacity: 0;
  }

  .state-songselection .danceroom__number
  {
    text-shadow: none;
    color: #ffffff;
  }

  .dialog #room-info .danceroom__qr-code-wrapper
  {
    display: block;
    color: #ffffff;
  }

  .state-dance #room-info,
.state-coachselection #room-info,
.state-afterdance #room-info
  {
    display: none;
  }

  #just-dance-now .danceroom__label
  {
    color: #ffffff;
    font-size: 20%;
    margin-top: 2%;
  }

  #room-info::before
  {
    content: '';
    display: block;
    height: 155%;
    width: 0%;
    background-color: #ffffff;
    position: absolute;
    bottom: -1%;
    right: 0;
    -webkit-transform: translateX(-10%) rotate(-12deg);
    -ms-transform: translateX(-10%) rotate(-12deg);
    transform: translateX(-10%) rotate(-12deg);
    -webkit-transform-origin: bottom right;
    -ms-transform-origin: bottom right;
    transform-origin: bottom right;
    transition: all 0.2s;
    z-index: -1;
  }

  .danceroom__icon
  {
    background-color: #ffffff;
    width: 0%;
    height: 0;
  }

  .blue .account__name::after
  {
    background-color: #ffffff;
  }

  .account::before
  {
    content: '';
    display: block;
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    width: 0.5rem;
    height: 0.4rem;
    position: absolute;
    top: -0.09rem;
    left: -0.07rem;
  }

  .dialog #room-info .danceroom__qr-code-wrapper
  {
    opacity: 0;
  }

  .dialog #room-info
  {
    left: -10%;
  }
/*----------------Display----------------*/

  .score__display
  {
    position: relative;
    height: 1000%;
    width: 100%;
    padding-bottom: 10%;
  }

  .score__display::after
  {
    content: "";
    display: block;
    height: 10%;
    position: absolute;
    top: -5%;
    left: 285px;
    transform: rotate(-12deg) scale(1, 1);
    transform-origin: left top 0px;
    border-left: 2px solid #ffffff;
    transition: all 300ms ease-out;
  }

  .score__number::before
  {
    position: absolute;
    content: "";
    display: none !important;
    -webkit-transform-origin: bottom right;
    -ms-transform-origin: bottom right;
    transform-origin: bottom right;
    bottom: 0;
    right: 0;
    height: 150%;
    width: 105%;
    -webkit-transform: rotate(-12deg);
    -ms-transform: rotate(-12deg);
    transform: rotate(-12deg);
    z-index: -1;
  }

  .score__owner .owner-name
  {
    opacity: 1;
    color: #ffffff;
    left: 250px;
  }

  .score__number
  {
    opacity: 1;
    top: -5%;
    left: 290px;
    color: #ffffff;
  }

  .score__stars
  {
    opacity: 1;
    top: -5%;
    left: 298px;
  }

  .video-preview--visible
  {
    display: none !important;
  }

  #racetrack .platform
  {
    opacity: 0;
  }

  .sprite--stars .sprite-description,
.sprite-description
  {
    top: 10000%;
  }

  .sprite__svg-container svg
  {
    top: 10000%;
  }

  .song-preview
  {
    position: absolute;
    bottom: 0;
    right: -140%;
    transition: transform 0.3s;
    height: 2.22rem;
    width: 2.22rem;
  }

  .song-action__button
  {
    width: 100%;
    height: 100%;
    top: 0;
    right: -275%;
  }

  .song-action__price-tag
  {
    right: -200%;
  }

  .song-details--empty
  {
    top: -40%;
    animation: none !important;
    left: -45%;
  }

  .song-detail__artist
  {
    text-align: right;
  }

  .video-preview
  {
    display: none !important;
  }

  .score__owner span
  {
    left: 85%;
  }

  .state-coachselection #preview .score__owner .owner-name
  {
    opacity: 0;
  }

  .state-coachselection #preview .score__number
  {
    opacity: 0;
  }

  .state-coachselection #preview .score__stars
  {
    opacity: 0;
  }

  .state-coachselection #preview .score__display::after
  {
    opacity: 0;
  }
/*----------------Lyric----------------*/

  #lyrics
  {
    position: absolute;
    top: 85.8%;
    left: 8.5%;
    width: 50%;
    font-size: .20rem;
    height: 100%;
  }

  #lyrics::after
  {
    content: '';
    position: absolute;
    height: 20%;
    width: 80.3%;
    left: -16%;
    opacity: 0.6;
    bottom: 86%;
  }

  #lyrics::before
  {
    content: '';
    position: absolute;
    opacity: 1;
    height: 100%;
    width: 0.3%;
    left: 8%;
    bottom: -1%;
    background-color: white;
    transform: skew(11deg);
  }

  .state-interstitial #lyrics::before,
.state-loadingdance #lyrics::before,
.state-tutorial #lyrics::before
  {
    display: none;
  }

  #lyrics .line.current,
#lyrics .line.previous
  {
    left: 0% !important;
  }

  #lyrics .line
  {
    left: 1%;
  }

  .state-coachselection .song-cover--hi-res::before
  {
    display: none;
  }

  .state-coachselection #preview .song-details h2 span,
.state-coachselection #preview .song-details h3 span
  {
    opacity: 0;
  }

  .state-coachselection #preview .song-detail::before
  {
    opacity: 0;
  }

  .state-coachselection #preview .song-action--start .song-action__label
  {
    opacity: 0;
  }

  .state-coachselection #preview .song-action--start
  {
    opacity: 0;
  }

  .state-coachselection .song-cover--hi-res
  {
    opacity: 0;
  }

  .state-coachselection #preview .video-preview
  {
    opacity: 0;
  }

  .state-coachselection #preview .reward__img
  {
    opacity: 0;
  }

  .reward__img
  {
    opacity: 1;
  }

  .coach::before
  {
    opacity: 0;
  }

  .coach-selection__title
  {
    opacity: 0;
  }

  .coach__histogram::before
  {
    opacity: 1;
  }

  .coach__histogram
  {
    text-align: center;
  }
/*----------------FEEDBACK----------------*/

  .state-dance #players
  {
    left: 17%;
  }

  .stars.player-stars
  {
    top: 55%;
  }

  .hud.beat #players .feedback-label
  {
    background-repeat: no-repeat;
    background-image: url(https://i.imgur.com/hbcFwiq.png);
  }

  #players .feedback
  {
    background-repeat: no-repeat;
    width: 100%;
    height: 100%;
    background-image: url(https://i.imgur.com/uf3LARU.png);
  }

  #players .feedlabel-good,
#players .feedlabel-perfect,
#players .feedlabel-yeah
  {
    top: 20%;
    left: -10%;
    width: 120%;
    height: 120%;
  }

  #players .feedlabel-ok
  {
    left: 4%;
    top: 50%;
  }

  #players .feedback-good
  {
    width: 140%;
    height: 140%;
    left: -20%;
    top: 0%;
  }

  #players .feedback-ok
  {
    top: -15%;
    left: 1%;
  }

  #players .feedback-perfect
  {
    width: 140%;
    height: 140%;
    left: -20%;
  }

  #players .feedback-yeah
  {
    width: 100%;
    height: 100%;
  }

  #players .crown::before
  {
    -webkit-animation: Corona 1s infinite alternate;
    animation: Corona 1s infinite alternate;
  }

  .particle-system
  {
    opacity: 0;
  }

  #beat
  {
    width: 42.2%;
    height: 120%;
    background-image: url(https://i.imgur.com/p23L6y6.png);
    opacity: 1;
    background-repeat: no-repeat;
    background-size: 100% 100%;
    background-position: bottom;
  }
}
