css:
@-moz-document domain("userstyles.org") {
	/*! Name    : Userstyles Inspired Dark */
	/*! Author  : Style-it Themes by: the-j0k3r */
	/*! Version : 1.2.7 beta */
	/*! Repo    : https://github.com/style-it/userstyles-inspired-dark */
	/*! License : CC-BY-SA-4.0 https://github.com/style-it/userstyles-inspired-dark/LICENSE */
	/*! Type    : Forum */
	body, #nav-view-responsive {
		color: #aaa !important;
		background: #222 !important;
	}

	.header.button.no-select {
		color: #ccc !important;
	}

	a {
		color: #5c81ba !important;
	}

	a:hover, a.TextColor:hover, a:hover .TextColor {
		color: #6b9ae2 !important;
		cursor: pointer !important;
	}

	.text_style_page > a:visited {
		color: #a6b !important;
	}

	.text_style_page > a:visited:hover {
		color: #ba70cd !important;
	}

	.header_button.no-select a:hover {
		color: #ddd !important;
	}
	/*
	.ConnectLabel.TextColor and a .TextColor
	are same selector but without I keep seeing a red color.
	*/
	.ConnectLabel.TextColor, a.TextColor, a .TextColor {
		color: #888 !important;
	}

	h1, h2, h3, h4, h5, h6, p, .P {
		color: #aaa !important;
	}

	input.DateBox, input.InputBox, input.SmallInput, textarea {
		color: #eee !important;
		background: #000 !important;
		border: 1px solid #444 !important;
		padding: 3px !important;
	}

	.ui-datepicker {
		background: #222 !important;
		border: 1px solid #999 !important;
	}

	#new-setting {
		border: none !important;
		box-shadow: none !important;
	}

	#user_name, #user_email, #user_homepage, #user_paypal_email,
	td:first-child > input:first-child, #style_pledgie_id, #openid,
	#style_short_description, #example-url, #listing-url,
	#widget-title, #user_login, #password, #password_confirmation,
	#login-existing, #password-existing, #obsoleting-style-id,
	#edit-style-settings input:not([type]),
	#edit-style-settings input[type="text"], #login-new, #email-new,
	#password-new, #password-new-confirm, #user_password,
	#user_password_confirmation, .edit-option-value-url {
		background: #000 !important;
		border: 1px solid #555 !important;
		color: #ccc !important;
	}

	#user_name:focus, #user_email:focus, #user_homepage:focus,
	#user_paypal_email:focus, #issue_full_name:focus, #issue_email:focus,
	#issue_user_name:focus, #openid:focus, #style_short_description:focus,
	#style_pledgie_id:focus, #example-url:focus, input.DateBox:focus,
	input.InputBox:focus, input.SmallInput:focus, #style_short_description:focus,
	input.SmallInput:focus, input.InputBox:focus, textarea:focus,
	td:first-child > input:first-child:focus, #listing-url:focus,
	#widget-title:focus, #user_login:focus, #password:focus,
	#password_confirmation:focus, #login-existing:focus,
	#password-existing:focus, #obsoleting-style-id:focus,
	#edit-style-settings input:not([type]):focus,
	#edit-style-settings input[type="text"]:focus, #login-new:focus,
	#email-new:focus, #password-new:focus, #password-new-confirm:focus,
	#user_password:focus, #user_password_confirmation:focus,
	.edit-option-value-url:focus {
		background: #191919 !important;
	}

	.Button {
		background: #333 !important;
		border: 1px solid #ccc !important;
		box-shadow:none !important;
		-moz-box-shadow: none !important;
		-webkit-box-shadow: none !important;
		border-color: #666 !important;
		color: #ccc !important;
		text-shadow: none !important;
	}

	.Button:hover {
		background: #444 !important;
		color: #eee !important;
		border-color: #777 !important;
	}

	.Button.Active, .ButtonGroup.Open .Button.Handle,
	.Button:focus, .Button:active {
		border-color: #aaa !important;
		background: #555 !important;
		box-shadow: none !important;
		-moz-box-shadow: none !important;
		-webkit-box-shadow: none !important;
	}
	/*
	Fix widget listing.
	*/
	.form-controls td, .form-controls th {
		border: none !important;
	}

	#Form_PostDiscussion, #Form_SaveDraft, #Form_Preview,
	#Form_PostComment, #Form_StartConversation {
		height: 25px !important;
		padding: 5px 6px 7px 6px !important;
	}

	#Form_Add {
		height: 25px !important;
		padding-top: 6px !important;
		margin-right: -1px !important;
	}

	#Form_Flagthis, #Form_SaveComment, #Form_SendMessage {
		height: 27px !important;
		padding-top: 5px !important;
		padding-bottom: 5px !important;
		margin-right: -1px !important;
	}

	#Form_Save {
		height: 25px !important;
		padding-top: 5px !important;
		margin-left: -1px !important;
	}

	#Form_SavePreferences, #Form_Upload {
		height: 25px !important;
		padding-top: 3px !important;
	}

	.Button.Cancel {
		height: auto !important;
		padding: 5px 6px 5px 6px !important;
	}

	.Button.Primary.Okay, #password-login > input:nth-child(7) {
		height: 25px !important;
		padding: 0 6px 0 6px !important;
	}

	#login td {
		border: 0 !important;
	}

	#Form_Search1, #Form_Go {
		margin: 0 !important;
		height: 16px !important;
		width: 16px !important;
	}

	.remove-option, .edit-setting-buttons > input:nth-child(3) {
		width: 28px !important;
		height: 28px !important;
		margin-right: 0 !important;
		margin-bottom: 0 !important;
	}

	.move-setting-down, .move-setting-up,
	.move-option-up, .move-option-down {
		width: 27px !important;
		height: 27px !important;
	}

	.move-setting-down, .move-option-down {
		font-weight: 900 !important;
		font-stretch: condensed !important;
	}

	.move-setting-up, .move-option-up {
		font-weight: 900 !important;
	}

	input[type="reset"], input[type="button"], input[type="submit"] {
		color: #ccc !important;
		padding: 1px 6px 6px 6px !important;
		background: #333 !important;
		border: 1px solid #555 !important;
		border-radius: 3px !important;
		height: 30px !important;
		margin: -2px 5px 8px 2px !important;
		font-family: sans-serif !important;
	}

	input[type="reset"]:hover, input[type="button"]:hover,
	input[type="submit"]:hover {
		color: #eee !important;
		background: #444 !important;
	}

	input[type="reset"]:focus, input[type="button"]:focus,
	input[type="submit"]:focus {
		color: #eee !important;
		background: #444 !important;
		border: 1px solid #555 !important;
	}

	.edit-option-value-url, #edit-style-settings textarea {
		margin-bottom: 6px !important;
	}

	.form-controls > tbody > tr > th, table.form-controls,
	.form-controls > tbody > tr > td, textarea {
		border-color: #444 !important;
	}

	.NavButton {
		color: #eee !important;
		text-shadow: 0 0 1px #000 !important;
		background: #444 !important;
		border: 1px solid #555 !important;
		box-shadow: 0 1px 1px #222 !important;
	}

	.ButtonGroup.Open .NavButton.Handle {
		background: #eee !important;
	}

	.NavButton.Handle .Sprite, .Button.Handle .Sprite {
		border-color: #fff transparent transparent transparent !important;
	}

	.ActivateSlider {
		display: inline-block;
		background: #bbb;
		/*background: rgba(0, 0, 0, .2);*/
		box-shadow: 0 10px 30px rgba(0, 0, 0, .5) inset;
		border: solid 1px #ccc;
	}

	.Deleted {
		background: #333 !important;
		border: 1px solid #555 !important;
	}

	/*
	Note: Warning, Alert & Info are simple boxes that can be used to wrap message
	strings & imply importance.
	*/
	span.Warning, div.Warning {
		background: #300 !important;
		border: 1px solid #b00 !important;
		color: #f44 !important;
	}

	.Count {
		background: #444 !important;
		color: #eee !important;
	}

	.Alert {
		background: #300 !important;
		border: 1px solid #b00 !important;
		color: #ddd !important;
	}

	.Alert a {
		color: #f44 !important;
	}

	.Alert a:hover {
		text-decoration: underline !important;
	}

	.Info {
		background: #292929 !important;
		border: 1px solid #555 !important;
		color: #aaa !important;
	}

	.Pager span {
		color: #777 !important;
	}

	.Pager .Highlight {
		color: #eee !important;
	}

	/* Small UserPhoto() images */
	a.Small:hover {
		text-decoration: none !important;
	}

	a.Small img {
		background: #333 !important;
	}

	/* # Messages

	Note: The MessageModule (in /applications/dashboard/modules) wraps all messages
	that it renders in a div with this DismissMessage class.
	*/
	.DismissMessage a.Dismiss {
		color: #aaa !important;
	}

	.DismissMessage a.Dismiss:hover {
		background: #333 !important;
		color: #eee !important;
	}

	.DismissMessage {
		color: #fff !important;
	}

	.DismissMessage.Info {
		background: #444 !important;
		border: 1px solid #aaa !important;
	}

	.DismissMessage.Warning {
		background: #300 !important;
		border: 1px solid #b00 !important;
	}

	.DismissMessage.Box {
		background: #fff8ce !important;
		border: 1px solid #c5bea4 !important;
	}

	.CasualMessage {
		background: #182030 !important;
		border: 1px solid #6b9ae2 !important;
		color: #ddd !important;
	}

	.CasualMessage a {
		color: #5c81ba !important;
	}

	.CasualMessage a:hover {
		color: #6b9ae2 !important;
	}

	.InfoMessage {
		background: #444 !important;
		border: 1px solid #555 !important;
	}

	.AlertMessage {
		background-color: #261d07 !important;
		border-color: #542 !important;
	}

	.WarningMessage {
		background: #300 !important;
		border: 1px solid #b00 !important;
		color: #ddd !important;
	}

	/* Header */
	#Head {
		background: #4f638b !important;
		box-shadow: 0 7px 12px 0 rgba(22, 22, 22, .75), 1px 0 0 0 #222 !important;
	}

	#Head a {
		color: #ccc !important;
	}

	.SiteSearch .Button {
		background: url('https://forum.userstyles.org/applications/dashboard/design/images/sprites.png') 0 -196px no-repeat transparent !important;
		top: 7px !important;
		filter: invert(100%) !important;
		border: none !important;
	}

	.SiteSearch .Button:hover {
		background: url('https://forum.userstyles.org/applications/dashboard/design/images/sprites.png') 0 -196px no-repeat transparent !important;
		filter: invert(100%) brightness(150%) !important;
	}

	/*
	Option (dropdown) Menus, Bookmark stars, admin checkboxes.
	*/
	.CategoryLink {
		background: #333 !important;
	}

	.category_title {
		font-size: 14px !important;
		font-weight: 600 !important;
		color: #ccc !important;
		margin-top: 20px !important;
	}

	div.Discussion .OptionsTitle, .Item .OptionsTitle {
		filter: invert(50%) !important;
	}

	div.Discussion:hover .OptionsTitle:hover, .Item:hover .OptionsTitle:hover {
		filter: invert(70%) !important;
	}

	.Open > .OptionsTitle {
		filter: invert(50%) !important;
	}

	.Flyout:before, .Flyout:after {
		border-bottom: 7px solid #777 !important;
		border-left: 7px solid transparent !important;
		border-right: 7px solid transparent !important;
	}

	.Flyout:after {
		border-bottom: 7px solid #222 !important;
	}

	.MenuItems {
		border: 1px solid #777 !important;
		background: #222 !important;
	}

	.MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active {
		color: #ccc !Important;
	}

	.MenuItems a:hover {
		color: #eee !Important;
		background-color: #4f638b !Important;
	}

	.MenuItems a:hover .Gloss {
		color: #ddd !important;
	}

	.MenuItems hr, hr {
		border: 1px solid #444 !important;
	}

	.MenuItems.Up:before, .MenuItems.Up:after {
		border-top: 7px solid #777 !important;
		border-left: 7px solid transparent !important;
		border-right: 7px solid transparent !important;
	}

	.MenuItems.Up:after {
		border-top: 7px solid #999 !important;
		bottom: -6px !important;
	}

	.FlyoutMenu {
		width: 300px !important;
		background: #222 !important;
		color: #ccc !important;
		border: solid 1px #666 !important;
	}

	.FlyoutMenu a {
		color: #5c81ba !important;
	}

	.FlyoutMenu a:hover {
		color: #6b9ae2 !important;
	}


	.FlyoutMenu li:hover {
		background: #292929 !important;
	}

	.DropHandle {
		border-width: 4px !important;
		border-color: #000 transparent transparent transparent !important;
	}

	.Box {
		margin: 10px 0 !important;
		white-space: nowrap !important;
	}

	/*
	The white-space: nowrap is a fix for the Notification Preferences
	when option is selected to prevent it from breaking the words, however
	this broke the private message wrapping of the Add People to this Conversation,
	so adding a hack to wrap that part
	*/
	.Box.GuestBox p, .ConnectLabel.TextColor {
		white-space: pre-wrap !important;
	}

	.ConnectLabel.TextColor {
		padding-bottom: 15px !important;
	}

	.Box.GuestBox {
		margin-top: -5px !important;
	}

	.Box p {
		margin-bottom: -10px !important;
	}

	.Box h4 {
		white-space: pre-wrap !important;
	}

	.Box dl dt {
		font-size: 12px !important;
		float: left !important;
		width: 80px !important;
		color: #555 !important;
		overflow: hidden !important;
		white-space: nowrap !important;
	}

	/* Hiding until we figure out what to do with GuestWelcomeModule */
	.MeButton {
		border: solid 1px transparent !important;
	}

	.MeButton:hover {
		background: #444 !important;
	}
	/*
	Targeting sprite instead of MeButton fixes:
	accidental inverted notification counter overlay
	*/
	.Sprite {
		filter: invert(100%) !important;
	}

	.Sprite:hover {
		filter: invert(100%) brightness(110%) !important;
	}

	.PanelActivity {
		border-top: 1px solid #555 !important;
	}

	.PanelActivity li {
		background: #333 !important;
		border-bottom: 1px solid #555 !important;
		color: #aaa !important;
	}

	.PanelActivity li a {
		font-size: 13px !important;
	}

	.PanelActivity li em {
		padding-left: 5px !important;
		color: #777 !important;
		font-size: 80% !important;
	}

	/*
	Compatibility for old custom themes that overrode this value.
	*/
	#Panel .FilterMenu li, .PanelInfo li {
		border-bottom: 1px solid #333 !important;
	}

	.BoxCategories .PanelInfo li:first-child, .BoxCategories .PanelInfo li:nth-child(5) {
		border-bottom: 2px solid #454545 !important;
	}

	#Panel .FilterMenu li:first-child, .PanelInfo li:first-child {
		border-top: 1px solid #333 !important;
	}

	/*
	Make standard items a bit more condensed when in the panel.
	*/
	.PanelInfo .Meta strong {
		display: inline !important;
		border-radius: 2px !important;
		-moz-border-radius: 2px !important;
		-webkit-border-radius: 2px !important;
		background: #ffc400 !important;
		color: #111 !important;
		font-size: 9px !important;
		font-weight: bold !important;
		padding: 3px !important;
		line-height: 1 !important;
	}

	.PanelInfo .Parent {
		text-align: left !important;
		font-weight: bold !important;
		background: none !important;
		color: #333 !important;
		padding: 2px 0 !important;
	}

	#Panel .FilterMenu .Active, .PanelInfo .Active {
		background: rgba(51, 51, 51, .69) !important;
		font-weight: bold !important;
	}

	#Content .FilterMenu li.Active a {
		border-bottom: 2px solid #555 !important;
	}

	#Content .BoxFilter .Active a {
		color: #5c81ba !important;
		border-bottom: 2px solid #555 !important;
	}

	#Content .FilterMenu li.Active a {
		border-bottom: 2px solid #555 !important;
	}

	.Icons img {
		background: #c4cde0 !important;
		color: #c4cde0 !important;
		text-indent: 50px !important;
	}

	/* # DataList

	Note: DataList is used in search results, vanilla discussions & drafts.
	*/
	.SearchForm .InputBox {
		border: solid 1px #666 !important;
	}

	.DataList .Item, .NarrowList .Item {
		border-bottom: 1px solid #444 !important;
	}

	.ItemDiscussion .Meta,
	.DataList .Meta {
		color: #888 !important;
	}

	.HasNew {
		background: #ffc800 !important;
		color: #000 !important;
		/* top | right | bottom | left */
		padding: 0px 4px 0px 3px !important;
		border-radius: 2px !important;
	}

	.Item.Read {
		background: #292929 !important;
	}

	.Item.Read:hover {
		filter: brightness(120%) !important;
	}

	.Item.Checked {
		background: #444 !important;
	}

	.DataList a.Delete {
		color: #c01f1f !important;
	}

	.DataList a.Delete:hover {
		background: #444 !important;
		color: #eb0909 !important;
	}

	/*
	Condensed datalists make the main link & excerpt inline.
	*/
	.DataList .Unfollow {
		background: #444 !important;
	}

	.DataList .Unfollow a {
		color: #888 !important;
	}

	.Item .Inset {
		border-top: 1px solid #bec8cc !important;
		padding: 5px 0 !important;
	}

	.PopList .Item {
		border-bottom: 1px solid #444 !important;
	}
	/*
	Fix both border and hover background its empty, decorate nothing.
	*/
	.PopList .Empty, .PopList .Empty:hover {
		background: transparent !important;
		border: none !important;
	}

	/* /me actions */
	.Hero {
		background: #333 !important;
	}

	.Activities li.Condensed .Excerpt {
		color: #eee !important;
	}

	.Activities .DataList li {
		background: #333 !important;
		margin-bottom: 2px !important;
		padding: 6px !important;
	}

	.Activities a.CommentLink, .Activities a.CommentLink:hover {
		background: #222 !important;
		color: #bbb !important;
		border: 1px solid #aaa !important;
	}

	.Comment {
		background: #222 !important;
	}

	body.Post .Popup a.Close {
		color: #aaa !important;
	}

	.emoji {
		margin-top: -2px;
		margin-bottom: -3px;
		/* account for image padding */
	}
	/* Fix the .EmotifyDropdown visibility/style/size IDK if its UHD resolution or what.*/
	.EmotifyDropdown {
		filter: invert(100%) brightness(250%) !important;
		width: 24px !important;
		height: 16px !important;
		margin-right: 4px !important;
	}
	/*
	Fix prevent the .EmotifyWrapper from showing inside the text area,
	If you type until the textarea end a portion of your text ends up behind
	the .EmotifyDropdown and you cant read it very well.
	*/
	.EmotifyWrapper {
		top: -27px !important;
	}

	.EmotifyDropdown {
		transform: scale(1.1) !important;
	}

	.EmoticonContainer, .EmoticonBox {
		background-color: #222 !important;
		border: 1px solid #555 !important;
		box-shadow: none !important;
		-moz-box-shadow: none !important;
		-webkit-box-shadow: none !important;
		padding: 3px !important
	}

	.Emoticon:hover {
		transform: scale(1.2) !important;
		image-rendering: crisp-edges !important;
	}

	blockquote.Quote, blockQuote.UserQuote {
		background: #313131 !important;
		border-left: 4px solid #666 !important;
	}

	.Message small {
		color: #aaa !important;
	}

	code, pre {
		background: #523b17 !important;
		color: #ddd !important;
	}

	pre code {
		background: transparent !important;
		white-space: pre-wrap !important;
	}
	/*
	The code blocks wrap when theres a code block just before
	The end of a sentence and its ugly and hard to read.
	*/
	code {
		border: none !important;
		padding: 2px 6px 2px 6px
	}

	pre {
		border: 1px dashed #a98956 !important;
		overflow: auto !important;
	}

	mark {
		background: #cf9900 !important;
		color: #000 !important;
	}

	/* Categories */
	.ChildCategories {
		border-top: 1px dotted #666 !important;
		display: block !important;
	}

	.RssButton {
		color: transparent !important;
	}

	/* Profile Page */
	.PhotoWrap:hover a.ChangePicture {
		display: block !important;
	}

	a.ChangePicture {
		background: #333 !important;
		color: #ddd !important;
	}
	/*
	Fix typo in selector from the default css
	a.ChangePicuture:hover -> a.ChangePicture:hover
	*/
	a.ChangePicture:hover {
		color: #eee !important;
		text-decoration: underline !important;
	}

	/* BGColor on profile forms */
	body.Profile.EditMode #Content form {
		background: #202020 !important;
	}

	.PreferenceGroup > thead > tr > td {
		background-color: #222 !important;
		color: #ccc !important;
	}

	/* Include invitations Form and fix .PreferenceGroup hover */
	.PreferenceGroup > tbody > tr:hover > td,
	#Form_Invitation table th:hover {
		background-color: #333 !important;
		color: #eee !important;
	}

	table.PreferenceGroup th, table.PreferenceGroup td,
	#Form_Invitation th, #Form_Invitation td {
		background-color: #202020 !important;
		border-bottom: 1px solid #333 !important;
		border-top: none !important;
		border-right: none !important;
		border-left: none !important;
	}

	.CurrentPicture table td, form.Thumbnail table td {
		border: none !important;
	}

	/* About in content area */
	.ContentColumn .About h2 {
		display: none !important;
	}
	/*
	Fix for being too far up this normally is 0px no breathing room.
	Also the breadcrumbwrapper was removed by default, this causes mis-alingments.
	*/
	.ContentColumn {
		margin-top: 18px !important;
	}
	.UserBox .WhoIs {
		margin-top: -10px !important;
	}

	dl.About dt {
		color: #666 !important;
		background: url('https://forum.userstyles.org/applications/dashboard/design/images/profile-sprites.png') 2px -139px no-repeat transparent !important;
	}

	/* Thumbnail Cropper */
	.jcropper-holder {
		border: 1px #fff solid !important;
	}

