css:
:root {
 	--bg: #222222;
    --border: #3d3d3d;
    --font: #a1a1a1;
    --pageBg: #1d1d1d;
}
*,
.btn-outline-secondary:not(:disabled):not(.disabled), .btn-secondary:not(:disabled):not(.disabled){
    color: var(--font) !important;
}

content,
footer,
header,
.navbar-default {
 	background-color: var(--pageBg);
    box-shadow: none !important;
}

.panel,
.panel-default>.panel-heading,
.dropdown-menu,
.modal-content,
.alert-warning {
 	background-color: var(--bg) !important;
    border-color: var(--border);
    color: var(--font);
}

.modal-header,
.modal-footer {
    border-color: var(--border);
}

.page-bar,
.datepicker.dropdown-menu {
 	background-color: var(--bg);
    border: 1px solid var(--border);
    color: var(--font);
}

.report-filter, .report-filter-btn {
 	border-color: var(--border);   
}

.panel-body .well,
.card,
.card-header,
.alert-secondary {
    background-color: var(--border);
    border-color: var(--bg);
    border-radius: 0;
}

.timeline-bar.break {
 	fill: var(--bg);   
}

.timeline-tick-text, .timeline-tooltip-text {
 	fill: var(--font);   
}

.timeline-warning>label {
 	opacity: 0.5;   
}

.timer-table .timer-timeentry-editor {
    background-color: var(--border) !important;
}

.form-control,
.select2-container--bootstrap .select2-selection--single,
.select2-container--bootstrap .select2-selection--multiple {
    border-color: var(--border);
}

.table-hover>tbody>tr:hover, .table>tbody>tr.active>td, .table>tbody>tr.active>th, .table>tbody>tr>td.active, .table>tbody>tr>th.active, .table>tfoot>tr.active>td, .table>tfoot>tr.active>th, .table>tfoot>tr>td.active, .table>tfoot>tr>th.active, .table>thead>tr.active>td, .table>thead>tr.active>th, .table>thead>tr>td.active, .table>thead>tr>th.active,
.datepicker .datepicker-switch:hover, .datepicker .next:hover, .datepicker .prev:hover, .datepicker tfoot tr th:hover,
.datepicker table tr td span:hover,
.datepicker table tr td.day.focused, .datepicker table tr td.day:hover,
.row-group-edit {
  	background-color: var(--border);  
}

footer .pull-left .margin-left-small:after {
    content: 'Theme by Neokil';
    margin-left: 10px;
}

.form-control,
.btn-outline-secondary,
.select2-container--bootstrap .select2-selection{
    background-color: var(--border) !important;
    border-color: var(--bg) !important;
    outline: none !important;
    box-shadow: none !important;
}

.btn-secondary,
.custom-checkbox .custom-control-label::after,
.select2-container--bootstrap .select2-selection--multiple .select2-selection__choice,
.badge-secondary{
    background-color: var(--bg) !important;
    border-color: var(--bg) !important;
}

.table td, .table th {
    border-color: var(--bg);
}
