css:
@-moz-document domain("webmail.strato.de") {
:root {
 	--bg: #222222;
    --border: #3d3d3d;
    --font: #a1a1a1;
    --pageBg: #1d1d1d;
}

/* i really want this to be global */
*:not(button) {
	color: var(--font) !important;   
}

.window-sidepanel .generic-toolbar.bottom, .window-sidepanel, .window-search, .rightside.mail-detail-pane,
.folder-tree,
.list-view-control,
.window-sidepanel .resizebar,
.classic-toolbar, .inline-toolbar, .mobile-navbar,
.window-sidepanel .generic-toolbar.bottom, .window-sidepanel, .window-search, .rightside.mail-detail-pane,
.generic-toolbar,
.thread-view-list,
.thread-view-list>.thread-view-header,
.thread-view.list-view .list-item,
.mail-detail.expanded .detail-view-header,
.mail-detail section.body,
.mail-item .attachments,
.dropdown-menu,
.modal-body .list-view-control a.select-all[aria-checked="true"] .fa-check-square-o, .window-body .list-view-control a.select-all[aria-checked="true"] .fa-check-square-o,
.tt-dropdown-menu,
#io-ox-topbar,
.window-content,
.rightside,
.settings-list-view>li,
.leftside,
.vgrid-cell,
.contact-grid-container .vgrid-cell.vgrid-label,
.vgrid .vgrid-toolbar,
.month-view .scrollpane .week .day-filler,
.month-view .scrollpane .week .day,
.month-view .scrollpane .week .day .number,
.io-ox-sidepopup,
.io-ox-dialog-popup,
.modal-footer,
.window-container.header-bottom .window-footer,
.file-list-view.column-layout .list-item:not(.selected):nth-child(odd), .myshares-list.column-layout .list-item:not(.selected):nth-child(odd), .myfavorites-list.column-layout .list-item:not(.selected):nth-child(odd),
.io-ox-files-main .viewer-sidebar,
#io-ox-core.show-banner #io-ox-banner,
.modal-content,
.modal-header,
.tokenfield .token{
    background-color: var(--bg);
    border-color: var(--border);
}

.folder-tree .tree-container .selectable.selected>.folder-node,
.folder-tree .tree-container .selectable>.folder-node:focus, .folder-tree .tree-container .selectable>.folder-node:hover, .folder-tree .tree-container .selectable>.folder-node:active,
.folder-tree .tree-container .selectable>.folder-node:focus *, .folder-tree .tree-container .selectable>.folder-node:hover *, .folder-tree .tree-container .selectable>.folder-node:active *,
.folder-tree .tree-container .selectable.selected>.folder-node:focus, .folder-tree .tree-container .selectable.selected>.folder-node:hover,
.folder-tree .tree-container .selectable.selected>.folder-node:focus *:not(.folder-options), .folder-tree .tree-container .selectable.selected>.folder-node:hover *:not(.folder-options),
.folder-tree .tree-container .selectable.selected>.folder-node:focus .folder-options i, .folder-tree .tree-container .selectable.selected>.folder-node:hover .folder-options i,
.folder-tree .tree-container .selectable.selected>.folder-node .folder-options,
.mail-attachment-list ul.inline-items>li.item,
.modal-body .list-view.visible-selection .list-item:hover, .window-body .list-view.visible-selection .list-item:hover, .modal-body .list-view.visible-selection .list-item.selected, .window-body .list-view.visible-selection .list-item.selected, .modal-body .list-view.visible-selection .list-item.selected.deleted, .window-body .list-view.visible-selection .list-item.selected.deleted,
.dropdown-menu .divider,
.list-view-control .toolbar a:not(.breadcrumb-link):focus, .list-view-control .toolbar a:not(.breadcrumb-link):focus i,
.form-control,
#io-ox-topbar .launcher:not(.dropdown).active-app,
.io-ox-portal .widget .decoration,
.modal-body .vgrid .vgrid-scrollpane .vgrid-scrollpane-container .vgrid-cell:focus, .window-body .vgrid .vgrid-scrollpane .vgrid-scrollpane-container .vgrid-cell:focus, .modal-body .vgrid .vgrid-scrollpane .vgrid-scrollpane-container .vgrid-cell:hover, .window-body .vgrid .vgrid-scrollpane .vgrid-scrollpane-container .vgrid-cell:hover, .modal-body .vgrid .vgrid-scrollpane .vgrid-scrollpane-container .vgrid-cell.selected, .window-body .vgrid .vgrid-scrollpane .vgrid-scrollpane-container .vgrid-cell.selected, .modal-body .vgrid .vgrid-scrollpane .vgrid-scrollpane-container .vgrid-cell.selected:focus, .window-body .vgrid .vgrid-scrollpane .vgrid-scrollpane-container .vgrid-cell.selected:focus,
#io-ox-notifications,
#io-ox-notifications #io-ox-notifications-display .item:hover,
#io-ox-notifications #io-ox-notifications-display .notifications,
.io-ox-mail-compose .editable-toolbar .mce-panel.mce-container:not(.mce-floatpanel):not(.mce-edit-area),
.io-ox-mail-compose textarea.editable, .io-ox-mail-compose .mce-content-body.editable,
.io-ox-mail-compose .editable-toolbar .mce-panel.mce-edit-area,
.itip-item  {
    background-color: var(--border);
    border-color: var(--border);
}

.border-right,
.month-view .scrollpane .week .day.bordertop,
.month-view .scrollpane .week .day.borderleft,
.month-view .scrollpane .week .day-filler.borderright,
.month-view .scrollpane .week .day.borderbottom,
.month-view .scrollpane .week .day-filler.bordertop {
 	border-color: var(--border);   
}

.io-ox-quota-view .progress {
	background-color: var(--font);
}

.io-ox-sidepopup-arrow.right .triangle {
 	border-right-color: var(--bg);   
}

.io-ox-sidepopup-arrow.right .border {
 	border-right-color: var(--border);   
}

::-webkit-scrollbar {
  width: 9px;
  height: 6px;
}
::-webkit-scrollbar-button {
  width: 0px;
  height: 0px;
}
::-webkit-scrollbar-thumb {
  background: var(--font);
  border: 0px none var(--border);
}
::-webkit-scrollbar-thumb:hover {
  background: var(--font);
}
::-webkit-scrollbar-thumb:active {
  background: var(--font);
}
::-webkit-scrollbar-track {
  background: #666666;
  border: 0px none var(--border);
}
::-webkit-scrollbar-corner {
  background: #666666;
}

.banner-title:after {
 	content: 'Theme by Neokil';
    position: absolute;
    font-size: 10px;
    vertical-align: text-bottom;
    padding-top: 10px;
}

.tokenfield .token .contact-image {
	filter: invert(1);
}
}
