additional_info:
Add this for a better look:

@import 'https://cdnjs.cloudflare.com/ajax/libs/meyer-reset/2.0/reset.min.css';
@import 'https://cdnjs.cloudflare.com/ajax/libs/ionicons/2.0.1/css/ionicons.min.css';
@font-face {
  font-family: 'Ionicons';
  src: url('https://cdnjs.cloudflare.com/ajax/libs/ionicons/2.0.1/fonts/ionicons.ttf');
}

* {
  box-sizing: border-box;
}

css:
body {
  overflow-x: hidden;
  font-family: IBM Plex Sans;
  line-height: 1.4;
  -moz-osx-font-smoothing: grayscale;
  -webkit-font-smoothing: antialiased;
}

input[type='checkbox']+ label {
  vertical-align: middle;
}

.button-extra-small.button {
  padding: 1px 10px;
}

#loginForm .neuesWebtrading,
#loginForm .linkToLogin,
.button {
  padding: 10px 20px;
  text-align: center;
}

.BLOG .SPALTE_2> div .BOX_HEADLINE {
  padding: 0 0 5px;
}

#ONVISTA {
  width: 100%;
  margin-top: 0;
}

header,
footer,
.INHALT {
  max-width: 1064px;
  margin-right: auto;
  margin-left: auto;
  padding-right: 20px;
  padding-left: 20px;
}

header .HAUPTNAVI> ul> li> ul> li> ul:before,
header .HAUPTNAVI> ul> li> ul:before,
header .HAUPTNAVI> ul:before,
header .TOP_WERTE:before,
header::before {
  left: -50%!important;
  width: calc(100vw + 50%)!important;
}

header {
  position: relative;
  margin-bottom: 20px;
}

header:before {
  height: 100%;
}

header .TOP_WERTE {
  position: relative;
  width: 100%;
  padding: 10px 0;
  font-size: 12px;
}

header .TOP_WERTE:before {
  left: 0;
  width: 100%;
}

header .LOGO {
  width: 280px;
  height: 40px;
  margin: 40px 0 35px;
}

header .META_NAVI {
  right: 100px;
}

header .rd_loginLayer,
header .rd_loggedInLayer {
  right: 20px;
}

header #siteSearch {
  top: 120px;
  right: 20px;
}

input[type='text'],
input[type='password'],
input[type='search'],
textarea,
select,
header #siteSearch .SEARCH {
  width: 300px;
  padding: 10px 15px;
  border: 1px solid #F3F2F2;
  font-family: inherit;
  font-size: 12px;
}

header #siteSearch:before {
  top: 10px;
  right: 15px;
}

header .HAUPTNAVI.NAVI_1_EBENE {
  height: 40px;
  margin-top: -20px;
}

header .HAUPTNAVI.NAVI_2_EBENEN {
  height: 80px;
  margin-top: -20px;
}

header .HAUPTNAVI.NAVI_3_EBENEN {
  height: 115px;
  margin-top: -20px;
}

header .HAUPTNAVI li {
  display: inline-block;
}

header .HAUPTNAVI> ul> li> ul> li> ul:before,
header .HAUPTNAVI> ul> li> ul:before,
header .HAUPTNAVI> ul:before {
  top: auto;
  bottom: 0;
  left: 0;
  width: 100%;
  padding: 0;
  background: #E4E4E4;
}

header .HAUPTNAVI> ul {
  padding-left: 0;
}

header .HAUPTNAVI> ul> li> ul> li> ul> li> a,
header .HAUPTNAVI> ul> li> ul> li> a,
header .HAUPTNAVI> ul> li> a {
  display: block;
  margin-right: 25px;
  padding: 12px 0;
}

header .HAUPTNAVI> ul> li> ul> li> ul> li> a {
  padding: 8px 0;
}

header .HAUPTNAVI> ul> li> a.AKTIV:after,
header .HAUPTNAVI> ul> li> ul> li> a.AKTIV:after,
header .HAUPTNAVI> ul> li> ul> li> ul> li> a.AKTIV:After {
  bottom: 0;
}

header .HAUPTNAVI> ul> li> ul> li:first-child a {
  padding-left: inherit;
}

header .HAUPTNAVI> ul> li> ul> li> ul,
header .HAUPTNAVI> ul> li> ul {
  top: 40px;
  width: 100%;
  padding-left: 0;
}

header .HAUPTNAVI>ul>li>ul>li>ul {
  display: flex;
  overflow-x: scroll;
  flex-wrap: nowrap;
  white-space: nowrap;
}

header .HAUPTNAVI .NAVI_ONVISTA_BANK {
  margin-top: 0;
}

header .HAUPTNAVI .NAVI_ONVISTA_BANK> a {
  margin-top: 0;
  margin-left: 0;
}

#ONVISTA .CONTAINER1 {
  margin-bottom: 0;
  border-bottom: 1px solid #F3F2F2;
}

.CONTAINER1,
.CONTAINER2 {
  position: relative;
  padding: 0;
  border: 0;
  border-right: 1px solid #F3F2F2;
  border-left: 1px solid #F3F2F2;
  border-radius: 0;
  background: #FFF;
  box-shadow: none;
}

.INHALT {
  position: relative;
  width: 100%;
  margin-bottom: 20px;
  line-height: inherit;
}

.BREADCRUMB {
  width: 100%;
  height: auto;
  margin-bottom: 20px;
  padding: 0;
  position: relative !important;
}

.BREADCRUMB h1 {
  color: #464646;
}

.BREADCRUMB ul li,
.BREADCRUMB ul li a {
  color: #A9A9A9;
}

.WEBSEITE> div> footer {
  padding: 20px;
  font-size: 12px;
}

.WEBSEITE> div> footer .TOP_LINKS {
  padding-top: 0;
}

#loginForm {
  width: 550px;
  transform: translate(-100%, 30px);
}

#loggedInForm {
  top: 120px;
  right: 20px;
  width: 550px;
}

#loggedInForm .LAYER,
#loginForm .LAYER {
  padding: 0;
  overflow: hidden;
  border: 1px solid #A41A8F;
  border-radius: 2px;
  background: #FFF;
  box-shadow: 0 10px 60px -5px rgba(0, 0, 0, .3);
}

#loggedInForm .MYONVISTA .MYONVISTA_LINKS {
  padding: 0;
  font-size: 16px;
}

#loggedInForm .MYONVISTA .MYONVISTA_LINKS a {
  padding: 15px 20px 12px;
  border-top: 1px solid #D9D9D9;
}

#loggedInForm .MYONVISTA .MYONVISTA_LINKS [class*='icon'] {
  vertical-align: unset;
}

#loginForm .neuesWebtrading {
  left: 0;
  width: 100%;
  margin: 20px 0 40px;
  transform: none;
}

#loginForm .WEITERFUEHRENDER_LINK.DEPOT_EROEFFNEN {
  margin-bottom: 10px;
  padding-bottom: 14px;
}

#loginForm .WEITERFUEHRENDER_LINK.DEPOT_EROEFFNEN,
#loginForm form {
  border-bottom: 1px solid #F3F2F2;
}

#loginForm input[type='submit'] {
  margin-top: -20px;
}

.PUSH_SCHALTER.IM_HEADER {
  top: 46px;
  right: 20px;
}

.ARTIKEL_ARCHIV .INHALTS_AUSWAHL li {
  height: auto;
  padding: 5px 0 0;
}

.ARTIKEL_ARCHIV div {
  padding-bottom: 0;
}

header .rd_loggedInLayer .rd_button {
  width: 60px;
}

.BOX_HEADLINE,
.MEISTEMITTIERTE_BASISWERTE .BOX_HEADLINE,
.MEISTGEHANDELTE_WERTE .BOX_HEADLINE {
  margin-bottom: 20px;
  color: #A41A8F!important;
}

.LANDINGPAGE.UEBERBLICK .EINGELOGGT p {
  margin: 0 0 20px;
}

.LANDINGPAGE.UEBERBLICK .EINGELOGGT section.TOOLS {
  padding: 0;
  border-color: #E4E4E4;
}

.LANDINGPAGE.UEBERBLICK .EINGELOGGT section.TOOLS ul:first-of-type {
  margin-right: 40px;
}

.LANDINGPAGE.UEBERBLICK .EINGELOGGT h2 {
  margin-bottom: 20px;
  color: #868686;
  font-size: 16px;
  font-weight: 600;
  text-transform: uppercase;
}

.LANDINGPAGE.UEBERBLICK .EINGELOGGT li a {
  transition: filter 200ms linear;
  filter: grayscale(100%);
}

.LANDINGPAGE.UEBERBLICK .EINGELOGGT li a:hover {
  filter: grayscale(0%);
}

.INHALT .INFO_LAYER_CONTAINER em {
  margin-left: 5px;
  opacity: .4;
  filter: grayscale(100%);
}

.DEPOT .SEITEN_FUNKTIONEN {
  margin-top: 15px;
  float: right;
}

.DEPOT .HIGHLIGHT_BOX {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  margin-bottom: 20px;
  padding: 10px 0 0;
}

.DEPOT .HIGHLIGHT_BOX h2 {
  margin: 0;
  padding-left: 15px;
}

.DROPDOWN> span.AUSWAHL {
  padding: 4px 35px 3px 10px;
}

.DROPDOWN> span.AUSWAHL .SELEKTOR {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 25px;
  height: 100%;
  border-bottom: 0;
  background: none;
  text-indent: 0;
}

.DROPDOWN> span.AUSWAHL .SELEKTOR::before {
  content: '\f123';
  display: inline-block;
  font-family: Ionicons;
  font-size: 10px;
}

.DEPOT .HIGHLIGHT_BOX .DEPOT_DATEN {
  flex: 1 0 100%;
  padding: 10px 15px;
  background: #E8E8E8;
}

.DEPOT .HIGHLIGHT_BOX .LINK_LISTE {
  display: flex;
  align-items: center;
  margin-top: 1px;
}

#myo .DEPOT .HIGHLIGHT_BOX .LINK_LISTE a {
  display: block;
  background: -40px 4px;
}

.INHALT thead th {
  background: #E8E8E8;
}

.DEPOT .HIGHLIGHT_BOX tbody th,
.DEPOT .HIGHLIGHT_BOX tbody td {
  padding: 3px 0;
}

.INHALT tbody tr:first-child th {
  padding-right: 15px;
}

.DEPOT .HIGHLIGHT_BOX cite {
  top: 30px;
  right: 5px;
}

.DEPOT .HIGHLIGHT_BOX form {
  padding: 5px 15px;
}

.DEPOT .HIGHLIGHT_BOX input.INAKTIV {
  margin-left: 5px;
  padding: 5px 15px 6px;
  color: #CECECE;
}

.DEPOT .HIGHLIGHT_BOX .LINK_LISTE li .EIN_AUS_ZAHLUNG {
  position: absolute;
  right: 15px;
  bottom: 11px;
}

.DEPOT .INHALT .CONTENT_NAVI_INHALT {
  position: relative;
}

.CONTENT_NAVI li.AKTIV,
.DEPOT .INHALT .CONTENT_NAVI_INHALT,
.DEPOT .HIGHLIGHT_BOX {
  border: 1px solid #E8E8E8;
}

.table_tab_box .CONTENT_NAVI li.AKTIV {
  border-right: 0;
}

.CONTENT_NAVI li a {
  display: block;
  padding: 7px 10px;
}

.CONTENT_NAVI_INHALT> div {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  padding: 0;
}

.DEPOT .INHALT .POSITIONEN h3,
.DEPOT .INHALT .TRANSAKTIONEN h3,
.DEPOT .INHALT .SIGNALE h3 {
  flex: 1 0 auto;
  margin: 0;
  padding: 8px 0 8px 10px;
}

.DEPOT .INHALT .POSITIONEN .ANSICHTEN_AUSWAHL {
  display: flex;
  align-items: center;
  padding: 0;
}

.DEPOT .INHALT .POSITIONEN .ANSICHTEN_AUSWAHL form {
  display: inline-block;
  position: relative;
  right: 0;
  margin: 0 10px;
}

.DEPOT .INHALT .POSITIONEN th.EIN_AUS_BLENDEN {
  padding: 5px 7px;
}

.DEPOT .INHALT .POSITIONEN td,
.DEPOT .INHALT .SIGNALE td,
.DEPOT .INHALT .POSITIONEN th {
  padding: 5px 10px;
}

.PADDING_KLEIN,
.BOX {
  padding: 10px;
}

.FOOTER_BANNER,
.PROMOLINK,
.SKYSCRAPER,
.DEPOT ul.FEEDBACK_FAQ,
.TEASERBOX,
.INHALT> #SMART_BILLBOARD,
header .TOP_AKTIEN,
#WALLPAPER,
.MEGASIZE_BANNER,
header .HAUPTNAVI.NAVI_1_EBENE:after,
#sas_12922,
header .HAUPTNAVI>ul>li>ul>li>ul:before {
  display: none;
}

div,
article,
section> article,
nav,
.INHALT form> fieldset> fieldset {
  margin-bottom: 0;
}

#loggedInForm .BANK,
#loginForm .BANK,
#loginForm .MYONVISTA,
#myo .LANDINGPAGE.UEBERBLICK .INHALT> div {
  padding: 20px;
}

#loggedInForm .MYONVISTA,
a.WEITERFUEHRENDER_LINK,
.BLOG .COMMENTS ul,
.CONTENT_NAVI li {
  padding: 0;
}

.EINZELANSICHT.WERTPAPIERUEBERSICHT .WERTPAPIER {
  padding: 20px 0 10px;
}

.SNAPSHOT_FUNKTIONEN {
  margin-top: -10px;
}

