css:
@-moz-document domain("majornelson.com") {
body {color: #fff; background-color: #000}
.media-card-content {color: #fff}
.speedbump {display: none}
.entry-title a {color: #fff}
.entry-title a:hover {color: #202020}
.hentry {background: #000000; color: #fff}
.hentry:hover {background: #000; color: #202020}
.content-wrap:hover, .content-wrap.expanded {color: #202020; background: #000000}
.content-wrap, .content-wrap.expanded {color: #202020; background: #000000}
.loop-hentry {padding: 0px 2%}
.icymi-container {display: none}
.pointerevents .section-header:before {background: url(http://www.solidbackgrounds.com/images/1920x1080/1920x1080-black-solid-color-background.jpg) repeat-x top left}
.section-header .max-width:after {background: url(http://www.solidbackgrounds.com/images/1920x1080/1920x1080-black-solid-color-background.jpg) no-repeat}
header[role=banner] {background: #000000}
.mobile-menu {background: #000000}
header .navbar-nav>li a:hover, header .navbar-nav>li a:focus {background: #ffffff; color: #000000}
#menu-main-nav li {color: #fff; background: #000}
.section-header {background: #000000; color: #fff}
#menu-main-nav li a {background: #000; color: #fff}
#menu-main-nav li a:hover {background: #fff; color: #000}
#menu-main-nav li:hover {background: #000000}
.content-wrap {height: 400px}
.content-wrap:after {opacity: 0}
.entry-content {color: #ffffff}
.content-block {background: #000}
a {color: #ffffff}
a:hover {color: #202020}
#msProductBadge.mspb-large .mspb-price {color: #202020}
#msProductBadge.mspb-large h1 {color: #202020}
#msProductBadge.mspb-large p {color: #202020}
#msProductBadge .mspb-details .mspb-rating .mspb-full-stars {color: #202020}
#msProductBadge .mspb-details .mspb-rating .mspb-total-ratings {color: #202020}
#msProductBadge .mspb-details .mspb-buttons a {background-color: #000; border: 0px solid transparent; color: #fff}
#msProductBadge .mspb-details .mspb-buttons a:hover {background-color: #fff; border: 0px solid transparent; color: #000}
.countdown-message p {background: #000; color: #fff}
.content-block .mn-icon {color: #ffffff}
.podcast-tab:before {border-color: black #000 transparent transparent}
.pointerevents header.page-header:before {background: #000}
header.page-header {background: #000}
.single-content tr:hover td {background: #101010}
.single-content thead {background-color: #00000000; background-image: -webkit-linear-gradient(left,#00000000 0%,#00000000 100%); background-image: -o-linear-gradient(left,#17a817 0%,#107c10 100%); background-image: linear-gradient(to right,#00000000 0%,#07ff31 100%)}
h6 {background: #000; color: #fff; border-bottom: 0px solid #000}
.btn-block {background: #000; color: #fff; border-bottom: 0px solid #000}
.btn-block:hover {background: #fff; color: #000; border-bottom: 0px solid #000}
.btn-comments {background: #000; color: #fff; border-bottom: 0px solid #000}
.btn-comments:hover {background: #fff; color: #000}
#msProductBadge.mspb-large .mspb-secondary-details .mspb-right-container {color: #202020}
.more-loop {display: none}
.section-header h1 {display: none}
.post-content .post-message p:last-child {color: #fff}
.col-md-offset-2 {margin-left: 0%}
.col-md-8 {width: 100%}
.col-md-offset-1 {margin-left: 0%}
.entry-title {color: #fff}
*::-webkit-scrollbar {width: 10px ! important; height: 10px ! important; background: #000 ! important; border: 0px solid #252525 ! important}
*::-webkit-scrollbar-button {display: none ! important}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {border: 0 ! important; box-shadow: none ! important;}
*::-webkit-scrollbar-thumb {min-height: 28px ! important; background: #202020 ! important}
*::-webkit-scrollbar-thumb:hover {background: #505050!important}
*::-webkit-scrollbar-thumb:active {background: #505050!important}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {background: #000 ! important}
}
