     .identity .controls_section.user_list li .follow_list_item_blog:before {
         background-image: none;
         border-right: none;
         position: absolute;
         display: block;
         width: 20px;
         right: 8px;
         bottom: 0;
         top: 0;
         content: " ";
    }
    /* Page header/search */
     .l-header-container--refresh {
