    /* Link highlights */
     .post .post_body a:hover, .post .post_body a, .reblog-content a, .reblog-content a:hover {
         background-image: linear-gradient(180deg,rgba(222, 222, 222, 0) 50%,rgba(255, 255, 255, 0.25) 0);
    }
    /* Post background */
     .post.post_full {
         width: 540px;
         font-size: 14px;
         background: #303030;
         padding: 15px 0 0;
         word-wrap: break-word;
         border-radius: 3px;
    }
     .reblog_follow_button .follow-text, .worded-follow-button .follow-text {
