css:
@-moz-document domain("www.panda.tv") {
body {
    background-color: #10111D !important;
}
.room-chat-box,
.ps-scrollbar-y-rail,
.room-head-info,
.room-foot-box,
.room-quiz-container,
.room-detail-box,
.room-detail-novideo,
.room-head-tool-follow,
.room-quiz-tips-pannel,
.room-chat-inputfield,
.room-detail-intro-text > p,
.room-detail-intro-text > p > span,
.room-chat-box > div {
    background: #191b2e !important;
}
.room-head-tool-follow-container,
.room-head-tool-follow-count,
.room-head-tool-follow-btn,
.room-chat-texta,
.room-chat-texta-placeholder,
.room-quiz-tips-pannel {
    border-color: #444 !important;
}
.room-chat-box,
.room-head-info,
.room-notice,
.room-head-info-cover,
.room-foot-box,
.room-quiz-container,
.room-detail-box,
.room-detail-novideo,
.quiz-status,
.room-quiz-head,
.room-quiz-detail-container,
.room-quiz-detail-container {
    border-color: #191b2e !important;
}
.room-head-tool-follow-container,
.room-head-tool-follow-count,
.room-head-tool-follow-btn,
.quiz-status,
.room-chat-texta,
.room-chat-texta-placeholder,
.input-chosen {
    background-color: #444 !important;
}
.room-head-info-title {
    color: #CCC;
}
.room-head-info-detail,
.room-foot-box,
.room-quiz-container,
.room-detail-box,
.room-chat-texta,
.room-chat-texta-placeholder,
.input-chosen,
.room-detail-novideo,
.quiz-title-name,
.quiz-detail-name,
.quiz-detail-desc,
.room-quiz-tips-pannel,
.room-chat-content,
.room-detail-intro-text > p,
.room-detail-intro-text > p > span {
    color: #BBB !important;
}
.pd-plouto-container,
.room-gift-container,
.room-firstpay-container,
.room-bamboo-container,
.room-wegame-container,
.new-fish-task-container,
.time-limit-task-container,
.time-limit-status-icon,
.room-rank-container,
.room-rank-container > div,
.room-rank-container > div > div,
.room-quiz-rank-container,
.new-fish-status-small {
    display: none;
}
.room-quiz-title,
.quiz-detail-option-container,
.quiz-detail-option-progress,
.room-chat-send,
h3 {
    border-color: #2b7e5b !important;
}
.quiz-detail-option-progress,
.room-chat-send {
    background-color: #2b7e5b !important;
}
.room-quiz-detail-container {
    border-color: #191b2e;
}
.ps-scrollbar-y {
    background-color: #2B2E4F;
}
.room-chat-user-name {
    color: #2b7e5b;
}
.room-chat-expand-btn {
    background: #191b2e !important;
}
.room-chat-container {
    position: relative;
    top: 0;
    bottom: 0;
}
.room-task,
.room-bag-container,
.quiz-winner,
.quiz-winner-show,
.room-quiz-tips > i,
.icon-panda-emoji,
.room-chat-tag-plat,
.icon-level,
.room-chat-tag-ispay,
.room-chat-tag-user-identity,
.room-head-info-cover {
    filter: brightness(60%);
}
.userbag-open-bag-modal,
.open-bag,
.userbag-open-bag-inner {
    z-index: 9999 !important;
}
.room-task-modal,
.room-task-modal::after {
    background: #343860 !important;
    border-color: #343860 !important;
}
}

@-moz-document domain("www.panda.tv") {
.video-list-item,
.video-list-item-wrap,
.video-label {
    background: #10111D !important;
}
.video-info,
.video-title,
.list-title,
.cate-title {
    color: #BBB !important;
}
.icon-host-level,
.video-label-item,
.list-header-hots,
.video-list-item {
    filter: brightness(60%);
}
.header-line {
    background: #2b7e5b !important;
}
.main-header > h3 {
    color: #CCC !important;
}
}

@-moz-document domain("www.panda.tv") {
.index-lift,
.bw,
.index-shuffer,
.index-footer-container,
.index-channel-list,
.index-pandastars,
.pd-plouto-container {
    display: none !important;
}
.pheader,
#panda_header,
body,
#panda_footer {
    background: #10111D !important;
}
.ph-nav_item > a {
    color: #CCC !important;
}
.sidebar-logo,
.sidebar-userinfo,
#message-container,
#panda_header {
    filter: brightness(60%);
}
html,
body {
    height: 100%;
}
.ph-nav > ul > li:nth-child(4),
.ph-nav > ul > li:nth-child(5),
.ph-nav > ul > li:nth-child(6) {
    display: none !important;
}
}
