@-moz-document domain("ithome.com") {
/*页尾*/
  [id^="ft"] {
    display: none !important;
  }

  /*最新最热*/
  .rt .nlst .ct  {
    width: auto;
	 height: 50px;					
    margin: -16px;
					
  }

.rt .nlst {
    padding-top: 15px;
}

.rt .nlst .ct ul li a {
    display: block;
    overflow: hidden;
    padding: 0 50px;
    height: 34px;
    color: #d22222;
    line-height: 34px;
}

.rt .nlst .new-list ul li {
    float: left;
    margin-bottom: 2px;
    padding: 3px 0 3px 9pt;
    min-width: 329px;
    border: 1px solid #fff;
    background: url(https://img.ithome.com/images/v2.1/new_list_icon.png) left 2px no-repeat;
    background-size: 1pc 34px;
}

.rt .nlst .hot-list .bx {
    float: left;
    margin: 0 20px 20px 0;
    width: 380px;
    height: 400px;
    border: 1px solid #e8e8e8;
    border-top: 0;
}


.current_nav,
.related_post,
.newsgrade,
.dajia,
.sc,
.tb-item:nth-of-type(1) > .item-link,
.item-link-2,
.nav-item-7.nav-item
{
    display: none !important;
  }

[id^="rm_app"] {
    display: none !important;
  }

.rt .nlst .ct {
    background:#fff;
}

.content .post_title {
    padding: 0px 0 0;
}

html {
    background:none;
    color: #000;
}

