css:
@-moz-document domain("youtube.com") {
.html5-play-progress,
.ytp-play-progress {
    background: #FFC0CB repeat-x !important;
}
.html5-load-progress,
.ytp-load-progress {
    background: #9999FF !important;
}
.html5-scrubber-button,
.ytp-scrubber-button {
    background: url("data:image/png;base64,") !important;
    width: 45px !important;
    height: 44px !important;
    border: none !important;
    margin-left: -15px !important;
    margin-top: -15px !important;
    transform: scale(0.8);
    -webkit-transform: scale(0.8);
    -moz-transform: scale(0.8);
    -ms-transform: scale(0.8);
}
.html5-progress-bar-container,
.ytp-progress-bar-container {
    height: 12px !important;
}
.html5-progress-bar,
.ytp-progress-bar {
    margin-top: 12px !important;
}
.html5-progress-list,
.ytp-progress-list,
.video-ads .html5-progress-list.html5-ad-progress-list,
.video-ads .ytp-progress-list.ytp-ad-progress-list {
    height: 12px !important;
}
}
