css:
@-moz-document domain("closed.pizza") {
#global-menu-logo {
    background: url('data:image/png;base64,') center no-repeat;
}

#global-menu-logo img {
    visibility: hidden;
}

.login-page > form > img {
    content: url('data:image/png;base64,')
}

#global-menu-login input {
    visibility: hidden;
}

#global-menu-login {
    background: url('data:image/png;base64,') center no-repeat;
}

.open-spin {
    content: url('data:image/png;base64,')
}

h2.label {
    border-bottom: 3px solid #FF0000;
    color: #FF0000;
}

a {
    color: #FF0000;
}

#global-menu li.selected a {
    color: #FF0000 !important;
}

#global-menu li.selected a {
    color: #ff0000;
}

.post-meta .empathy-added + .empathy, .reply-meta .empathy-added + .empathy {
    color: #FF0000;
}

.post-meta .empathy-added + .empathy::before, .reply-meta .empathy-added + .empathy::before {
    color: #ff0000;
}

.feeling-selector .feeling-button.checked {
    color: #ff0000;
}

.textarea-with-menu.active-text .textarea-menu-text, .textarea-with-menu.active-memo .textarea-menu-memo, .textarea-with-menu.active-poll .textarea-menu-poll {
    background: -webkit-gradient(linear,left top,left bottom,from(#FF0000),to(#FF0000)) 0 0;
}

.dialog .window-title {
    border-top: 1px solid #ff0000;
    border-bottom: 1px solid #ff0000;
    background: #ff0000;
}

.tab2 a.selected, .tab3 a.selected {
    background: -webkit-gradient(linear,left top,left bottom,from(#FF0000),to(#FF0000));
}

.news-list a.link {
    color: #ff0000;
}

.user-organization {
    color: #FF0000;
}

#global-menu li.selected a::before {
    color: #FF0000;
}

h2.reply-label {
    background: #ff0000;
    border-top: 1px solid #ff0000;
}

.spoiler-button.checked {
    color: #FF0000;
}

.spoiler-button.checked:before {
	color:#FF0000
}

#global-menu #global-my-menu .symbol::before {
    color: #ff0000;
}

.index-memo h2:not(.label), #help .help-content h2 {
    background: #FF0000;
}

.community-switcher .community-switcher-tab.selected {
    background: -webkit-gradient(linear,left top,left bottom,from(#ff0000),to(#ff0000));
}

.news-community-badge {
    background: #ff0000;
}

.user-data h4 span {
    background-color: #ff0000;
}

.sidebar-setting .sidebar-menu-post::before {
    color: #ff0000;
}

.sidebar-setting .sidebar-menu-replies::before {
    color: #ff0000;
}

.sidebar-setting .sidebar-menu-empathies::before {
    color: #ff0000;
}

.user-sidebar .follow-button::before {
    color: #ff0000;
}

.user-sidebar .friend-button::before {
    color: #ff0000;
}

#memo-drawboard-page button.selected {
	background:#FF0000
}

#memo-drawboard-page .memo-canvas #artwork-canvas {
	border:1px solid #ff0000
}

#global-menu li a:hover,#global-menu li button:hover {
	box-shadow:inset 0 -4px 0 -1px #ff0000
}

.report-violation-button, .report-button:before {
color: #FF0000;
}

.post-meta .link-confirm::before {
    color: #FF0000;
}

@media screen and (max-width:980px) {
    #global-menu li.selected a
    {
         border-bottom: 2px solid #FF0000;
   }
   .guest:not(.user) #global-menu-logo
    {
    width: 165px;
    }
}
}
