css:
@-moz-document domain("webtoons.com")
{
  #content
  {
    font: 14px/100% century gothic;
    background-color:black;
  }

  .cont_box
  {
    text-align: center;
    padding-top: 50px;
    padding-bottom: 50px;
  }

  .viewer_lst
  {
    overflow: hidden;
    position: relative;
    padding-top: 50px;
    text-align: center;
    width: 815px;
    left: 15%;
  }

  .viewer_lst
  {
    background: rgba(32, 32, 32, 0.2);
    border: none;
    border-radius: 5px;
    text-align: center;
  }

  .episode_area
  {
    height: 148px;
    margin: 0 auto;
    padding: 0px 0 !important;
    color: #ffffff;
    background: rgba(32, 32, 32, 0.3);
    text-align: center;
  }

  .subj
  {
    color: #ffffff !important;
  }

  #_bottomDisplay, #footer, #_bottomEncourage, #_bottomLikeSubscribe
  {
    visibility: hidden;
    display: none;
  }

  .challenge_spot, .day_info, ._readComplete, .spi_area
  {
    visibility: hidden;
    display: none;
  }

  .viewer_img
  {
    margin-bottom: 10px !important;
  }
}
