css:
@-moz-document domain("developer.chrome.com") {
body {
    background: #393e50;
}
#logo a img {
    opacity: 0;
}
#logo a {
    display: flex;
    height: 40px;
    width: 40px;
    background: url(http://localhost:54321/Google-logo.svg) no-repeat;
    background-size: 100% 100%;
    filter: drop-shadow(0px 0px 7px #4F4F4F);
    transition: all .3s;
}
#logo a:hover {
    filter: drop-shadow(0px 0px 3px #F2F2F2);
    transform: scale(1.1) rotate(-15deg);
}
#logo:after {
    content: "Chrome";
    margin-left: 12px;
    color: #828282;
    font-size: 2em;
}
h1, h2, h3, h4, h5, h6 {
    color: #c7c7c7;
}
#fatnav .pillar.highlight .toplevel {
    color: #7ea1e4;
}
.inline-site-toc li li a:focus, .inline-site-toc li li a:hover, .inline-site-toc li li a:link:focus, .inline-site-toc li li a:link:hover, .inline-site-toc li li a:visited:focus, .inline-site-toc li li a:visited:hover {
    color: #7ea1e4;
}
.inline-site-toc li.toplevel>ol>li.show {
    background-color: #464c62;
}
.inline-toc #toc .toplevel>a {
    color: #828282;
}
body, html {
    color: #BDBDBD;
}
.inline-site-toc li li a, .inline-site-toc li li a:link, .inline-site-toc li li a:visited {
    color: #828282;
}
.inline-site-toc li li.selected>a {
    color: #BDBDBD;
}
.code, code, pre {
    color: #89B789;
    background-color: #464C62;
    border-bottom: 1px solid #56CCF2;
}
mark, pre b {
    background: #505871;
}
.pun, .opn, .clo{
    color: #b9b90a;
}
.str {
    color: #89B789;
}
.kwd {
    color: #56CCF2;
}
.pln {
    color: #8e9cce;
}
.com {
    color: #828282;
}
aside.caution, aside.note, aside.warning, div.caution, div.note, div.warning, p.caution, p.note, p.warning {
    background-color: #464C62;
}
aside.warning, div.warning, p.warning {
    border-color: #EB5757;
}
.article-content {
    border: none;
}
img {
    filter: brightness(0.7);
}
pre[data-filename]::after {
    background-color: #505871;
    box-shadow: 1px 0px 4px #393E50;
    color: #BDBDBD;
}
.api {
    color: #BDBDBD;
}
figure img, td, th {
    border-color: #464C62;
}
th {
    background: #464C62;
}
.api .api-reference h2 {
    background-color: #464C62;
}
.api .api-reference th {
    background-color: #464C62;
}
.api .api-reference td, .api .api-reference th {
    border: 1px solid #464C62;
}
.api .availability {
    color: #EB5757;
}
.api .api-reference table .innerTable td, .api .api-reference table .innerTable th {
    border: 1px solid #464C62;
}
#fatnav .pillar.active {
    background-color: #464C62;
}
#fatnav .expandee {
    background-color: #464C62;
}
#fatnav .expandee li.submenu {
    color: #2D9CDB;
}
#fatnav .pillar.active .expandee::after {
    background: none;
}
#fatnav .pillar .expandee .submenu {
    border-right: 1px solid #505871;
}
#fatnav .expandee li.submenu .category {
    border-bottom: 1px solid #505871;
}
#fatnav .pillar {
    border-right: 0px solid #505871;
}
#fatnav .pillar.active {
    transition: all .15s;
    border-right-width: 5px;
    background-image: none;
    box-sizing: border-box;
}
#search.active {
    background: #464C62;
}
#search .expandee input[type=search] {
    background-color: #505871;
    border-color: #2D9CDB;
}
#search.active .expandee {
    box-shadow: 1px 0px 3px #393E50;
}
#fatnav .expandee a, #fatnav .expandee li.submenu {
    transition: all .15s;
}
#fatnav .expandee a.highlight, #fatnav .expandee a:hover, #fatnav .expandee li.submenu.active {
    background: #505871;
    padding-left: 15px;
}
#fatnav .expandee a.highlight {
    color: #7ea1e4;
}
#gc-footer {
    background: #464C62;
}
#site-sections {
    background-color: #464C62;
}
.button {
    transition: all .1s;
    background: #505871;
}
.button:hover {
    background: #56CCF2;
    transform: scale(1.06);
}
.tag {
    color: #73ABA9;
}
.dec, .var {
    color: #C526C5;
}
.lit {
    color: #3bd0d0;
}
.atn {
    color: #c19bf3;
}

}

@media only screen and (max-width: 580px) {
    #logo>a {
        flex: 0;
        min-width: 40px;
    }
    #logo>span {
        order: 1;
    }
    #logo:after {
        order: 0;
        flex: 1;
    }
    #fatnav {
        background: #505871;
        flex: 1;
        box-shadow: 0px 1px 5px #4F4F4F;
        z-index: 1;
    }
    #fatnav.active {
        border-bottom: 1px solid #F2F2F2;
    }
    #fatnav .pillar {
        border-bottom: 1px solid #464C62;
    }
    .inline-site-toc .toc {
        margin-top: 10px;
    }
}
