css:
@-moz-document domain("republic.ru") {
    body, .post-navbar, .header--normal, .comments,
    .social-likes--circle-gray .social-likes__widget,
    .share-informer, .read-also, .footer {
        background: #272822 !important;
        color: #F8F8F2 !important;
    }

    .hc__reply__box__textarea, .hc__reply__box textarea {
        background-color: #272822 !important;
        color: #75715E !important;
    }

    .social-likes--circle-gray .social-likes__widget,
    .share-informer {
        border: 1px solid #F8F8F2 !important;
    }

    .post-navbar .post-navbar__post-head-title,
    .hc__message__txt, .hc__message__txt b, .hc__message__txt u, .hc__message__txt i, .hc__message__txt s, .hc__message__txt q, .hc__message__txt code {
        color: #F8F8F2 !important;
    }

    .comments-section {
        background: #272822 !important;
    }
    a {
        color: #FFE792 !important;
    }
}
